css:
@-moz-document domain("medium.com") {
	html,body,div,p,section,article,strong,.metabar,.u-backgroundGrayLightest {
		background-color: #111 !important;
		color: #777 !important;
	}

	header div,footer div,.image div {
		background-color: transparent !important;
	}

	h1,h2,h3,h4,h5,h6 {
		color: #fff !important;
	}

	.notes-list-fade,.notes-list-fade-bottom {
		background: inherit !important;
	}

	pre,code {
		background-color: #222 !important;
	}

	a {
		color: #777 !important;
	}
	
	span {
		color: #eee !important;
	}
	
	.button {
		border-color: #999;
		color: #999;
	}
	
	.button:hover {
		border-color: #fff;
		color: #fff
	}
	
	.button:focus {
		border-color: #fff;
		color: #fff
	}
	
	.svgIcon {
		fill: #999;
		color: #999;
	}
	
	.svgIcon:hover, .svgIcon:active, .svgIcon:focus {
		fill: #fff;
		color: #fff;
	}
	
	

	.svgIcon--use {
		fill: #999;
		color: #999;
	}
	
	.svgIcon--use:hover, .svgIcon--use:active, .svgIcon--use:focus {
		fill: #fff;
		color: #fff;
	}
	
	
	.button--chromeless {
		fill: #999;
		color: #999;
	}
	
	.button--chromeless:hover, .button--chromeless:active, .button--chromeless:focus {
		fill: #fff;
		color: #fff;
	}
	
	
	.button--withSvgIcon {
		fill: #999;
		color: #999;
	}
	
	.button--withSvgIcon:hover, .button--withSvgIcon:active, .button--withSvgIcon:focus {
		fill: #fff;
		color: #fff;
	}
	
	.button--chromeless .button--chromeless:focus .button--chromeless:active .buttonSet--profile {
		color: rgba(230,230,230,.6) !important;
	}
	
	.buttonSet--profile .button--chromeless {
		color: rgba(150,150,150,.6) !important;
	}
	
	.buttonSet--profile .button--chromeless:active, .buttonSet--profile .button--chromeless:focus, .buttonSet--profile .button--chromeless:hover {
		color: rgba(230,230,230,.6) !important;
	}
	
	.button--chromeless:active .svgIcon, .button--chromeless:focus .svgIcon, .button--link:active .svgIcon, .button--link:focus .svgIcon {
		fill: rgba(230,230,230,.6) !important;
	}
	
	.button--chromeless .svgIcon {
		fill: rgba(150,150,150,.6) !important;
	}
	
	.button.is-inSiteNavBar .svgIcon, .button.is-inSiteNavBar:focus .svgIcon, .button.is-inSiteNavBar:hover .svgIcon {
		fill: rgba(230,230,230,.6) !important;
	}
	
	.label--withIcon.label--lighter .svgIcon {
		fill: rgba(150,150,150,.6) !important;
	}
	
	.block-postMeta .postMetaInline, .block-postMeta .postMetaInline .button {
		color: rgba(150,150,150,.6) !important;
	}
	
	.u-backgroundWhite {
		background: #111 !important;
	}
	
	.sortableTable .is-active td, .sortableTable .is-active:hover td, .sortableTable tr:hover td {
		background: rgba(40,40,40,.65);
	}
	
	.sortableTable-header.is-reverseSorted button, .sortableTable-header.is-reverseSorted button:focus, .sortableTable-header.is-sorted button, .sortableTable-header.is-sorted button:focus {
		color: rgba(230,230,230,.6) !important;
	}
	
	.button--chromeless:active, .button--chromeless:focus, .button--chromeless:hover, .button--link:active, .button--link:focus, .button--link:hover, .sortableTable-wrapper button {
		color: rgba(230,230,230,.6) !important;
	}
	
	.sortableTable-header.is-reverseSorted .svgIcon.svgIcon--sortDescending, .sortableTable-header.is-sorted .svgIcon.svgIcon--sortAscending {
		fill: rgba(230,230,230,.8);
	}
	
	.sortableTable-wrapper th .svgIcon {
		fill: rgba(230,230,230,.8);
	}
	
	

	.metabar--affixed {
		background: rgba(30, 30, 30,0.9) !important;
	}
	input {
		background-color: #222 !important;
		color: #ddd !important;
	}
}
