css:
@-moz-document domain("play.google.com") {
::-webkit-scrollbar {
    height: 12px;
    width: 12px;
    background: #000;
}

::-webkit-scrollbar-thumb {
    background: #2e4047;
    -webkit-border-radius: 1ex;
    -webkit-box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.75);
}

::-webkit-scrollbar-corner {
    background: #000;
}

body,
.material .cluster-text-protection,
.material .cluster-text-protection:before {
    background-color: #222222;
}

body.material,
.cluster .header .cluster-title,
.material-card .title,
.material-detail-view .artist-details .bio-wrapper .bio,
.column .material-card[data-size="small"][data-type="imfl"] .title,
.material-card[data-is-listen-now="true"] .reason .reason-text,
#player .now-playing-actions paper-icon-button,
.nav-item-container.selected:not([style-scope]):not(.style-scope),
.nav-item-container {
    color: #eeeeee;
}

.material-card,
.material-detail-view .artist-details,
.material-detail-view .material-container-details .read-more-button:hover {
    background-color: #2d2d2d;
}

.fade-out:after,
.nav-item-container:hover .fade-out::after,
.fade-out.gray:after {
    background: transparent;
}

.paper-toolbar-0,
.paper-progress-0 #primaryProgress.paper-progress,
.paper-slider-0 #sliderKnobInner.paper-slider,
.paper-fab-0,
.paper-button-1 {
    background-color: #3d666e;
}

#player {
    background-color: #000000;
}


#player paper-icon-button[data-id="play-pause"]:not([style-scope]):not(.style-scope):not([disabled]),
.nav-item-container.selected iron-icon:not([style-scope]):not(.style-scope),
.primary:not([style-scope]):not(.style-scope),
iron-icon.primary:not([style-scope]):not(.style-scope) {
    color: #3d666e;
}

.paper-drawer-panel-0 iron-selector.paper-drawer-panel > #drawer.paper-drawer-panel,
#nav-container .nav-toolbar,
.nav-item-container.selected {
    background-color: #191919;
}

.nav-item-container:hover {
    background-color: #333333;
}

.nav-section-divider {
    border-color: #222222;
}
}
