css:
@-moz-document domain("www.lostfilm.tv") {
body {background: #f6f5f3;padding-top: 1em !important;}
a[href^="/btrdr.php"]{display:none;}
a[href^="/shmrowse.php?"],a[href^="http://adverti.me"],#myA{
    display:none;
}

#Onwrapper a[href^="/browse.php?"] div{
    display:none;
}
.left .shadow:nth-child(4) .block .block_link {
    display: none;
}
.bogi {
    display: none;
}
.main_logo_link {
    background: url(http://i.imgur.com/NYyx1vV.png) no-repeat !important;
}
.up {
    background: none;
    color: #777;
}
.logo{
color:#5D5D5D;
}
.d_pages_link {
    color: #7B7B7B !important;
}
.up:hover {
    background: none;
    color: #F44336;
}

.a_download {
    background: #F44336;
    color: #fff;
    border-radius: 2px;
    margin-right: 3px;
    padding: 0 5px;
    line-height: 1.7;
    text-decoration: none;
    font-size: 18px;
    width: auto;
}
.a_download::after {
    content: '';
    background: url(http://i.imgur.com/y3fO4sr.png);
    height: 24px;
    width: 24px;
    display: block;
    background-position-y: 2px;
}
.a_download:hover,.a_full_news:hover {
    background: #d43d31;
}

.torrent_title {
    font-size: 13px;
    color: #666666;
}

a {
    color: #666666;
}
.user_menu_link {
    color: #F44336;
    text-transform: uppercase;
    text-decoration: none;
    font-size: 11px;
}
.wh {
    color: #424242;
}
.prof {
    color: #8A8A8A;
    font-size: 13px;
    vertical-align: top;
    background: #fff;
    padding: 10px;
    height: auto !important;
    box-shadow: 0 2px 2px 0 rgba(0,0,0,.14),0 3px 1px -2px rgba(0,0,0,.2),0 1px 5px 0 rgba(0,0,0,.12);
    margin-bottom: 1em;
    border-top: 3px solid #F44336;
}
.shadow {
    box-shadow: 0 2px 2px 0 rgba(0,0,0,.14),0 3px 1px -2px rgba(0,0,0,.2),0 1px 5px 0 rgba(0,0,0,.12);
    border-radius: 0;
}
#new_sd_list > div{ display: block !important; }


.block {
    background-image: none;
    background-color: #FFF;
}
.block_link a {
    font-size: 14.9px;
}
.block_link {
    background: #F44336;
    cursor: pointer;
    line-height: 1.5;
}
.block_link:hover {
    background: #E53935;
}
.block_bottoml {
    background-image: none;
    background-color: #fff;
}
.block_bottomr {
    background: none;
}
.block_bottomr img, .block_bottoml img, .block_bottomc img{
    display:none;
}
.a_discuss,.a_details,.a_thanks {
    background: #616161;
    border-radius: 2px;
    color: #fff;
    text-align: center;
    text-decoration: none;
    padding: 2px 5px;
    font-size: 14px;
    height: auto;
    width: auto;
}
.a_discuss:hover, .a_details:hover, .a_thanks:hover {
    background: #3e3e3e;
}

.a_thanks::after{
    content: '';
    background-image: url(data:image/svg+xml;utf8;base64,);
    background-size: 1.2em;
    background-position: 4px;
    height: 24px;
    width: 24px;
    display: block;
}

.a_discuss::after {
    content: '';
    background-image: url(data:image/svg+xml;utf8;base64,);
    background-size: 1.3em;
    background-position: 2px;
    height: 24px;
    width: 24px;
    display: block;
}
.a_details::after {
     content: '';
    background-image: url(data:image/svg+xml;utf8;base64,);
    background-size: 1.2em;
    background-position: 4px;
    height: 24px;
    width: 24px;
    display: block;
}
.a_discuss::after, .a_details::after, .a_thanks::after {
    background-repeat: no-repeat;
}

.m {
    background-image: none;
    background-color: #F44336;
}

.top_menu_lc {
    display: none;
}

.top_menu_rc {
    display: none;
}

.menu a:hover {
    background: none;
    box-shadow: inset 0 -3px;
}

.menu a {
    font-size: 12px;
    text-transform: uppercase;
    font-weight: 600;
    line-height: 2;
    padding: 1em;
    height: 24px;
}
.right {
    margin-top: 0;
    padding-top: 0;
}
.content_body a {
    color: #fff;
    text-decoration: none;
    font-size: 14px;
}
.news-list .item.active {
    background: #F44336 !important;
}

#news-pane {
    background: #393939 !important;
}
.a_full_news {
    background: #F44336;
    border-radius: 2px;
    height: auto;
    width: auto;
}

.a_full_news::after {content: 'Подробнее';font-size: 14px;padding: 0 10px;line-height: 2;}

a.d_pages_link {
    font-size: 13px;
    margin: 2px 5px;
    display: inline-block;
}
span.d_pages_link_selected {
    padding: 3px;
    font-size: 12px;
    background: #F44336;
    text-align: center;
    display: inline-block;
}

.form_b {
    border-radius: 2px;
    border: none;
    color: #fff;
    background-color: #F44336;
    outline: none;
    float: right;
    margin: 4px;
    padding: 2px;
    cursor: pointer;
}
.form_b:hover {
    background-color: #E53935;
}
.form_i {
    border-color: #C1C1C1;
    padding: 3px;
    margin: 5px 4px;
    outline:none;
}
.form_i:focus {
    border-color: #F44336;
}

.bb_a {
    background-image: url(data:image/svg+xml;utf8;base64,);
    padding-left: 30px;
}
.bb_a:hover {
    color: #F44336;
	background-image: url(data:image/svg+xml;utf8;base64,);
}
.content_head {
    border-color: #E0E0E0;
    font-size: 18px;
    color: #4E4E4E;
}
.micro, .micro a {
    font-size: 9px !important;
    color: #C8C8C8;
}
a[href^="//vk.com"] span img{
    display: none;
}
a[onclick*="facebook"] img{
    display: none;
}
a[href^="//twitter.com"] img{
    display: none;
}
a[onclick*="mail.ru"] img{
    display:none;
}

a[href^="//vk.com"]::after{
    content: '';
    display:inline-block;
    width: 24px;
    height: 24px;
   background-image: url(data:image/svg+xml;utf8;base64,)
}
a[onclick*="facebook"]::after{
    content: '';
    display:inline-block;
    width: 24px;
    height: 24px;
    background-image: url(data:image/svg+xml;utf8;base64,)
}
a[href^="//twitter.com"]::after{
    content: '';
    display:inline-block;
    width: 24px;
    height: 24px;
    background-image: url(data:image/svg+xml;utf8;base64,)
}
a[onclick*="mail.ru"]::after{
    content: '';
    display:inline-block;
    width: 24px;
    height: 24px;
    background-image: url(data:image/svg+xml;utf8;base64,);
}

.spacer{
	visibility:hidden;
}

.a_submit {
    background: #F44336;
    border-radius: 2px;
    height: auto;
    width: auto;
    text-decoration: none;
}

.a_submit::after {
    content: 'Написать';
    font-size: 14px;
    padding: 0 10px;
    line-height: 2;
    color: #fff;
}

.a_submit:hover {
    background: #d43d31;
}

.bb_quote_author {
    border: 0;
    color: #afafaf;
}
.bb_quote {
    padding: 5px;
    margin: 0 0 5px;
    background: #f5f5f5;
    border: 1px solid #d4d4d4;
    box-shadow: 0 8px 13px -7px rgba(0,0,0,.2);
}

div[id^="msg_num"] tr td:first-child img {
    border-radius: 100%;
    width: 80px;
    height: 80px;
    border: 2px solid #f44336;
    box-shadow: 0 10px 20px -5px rgba(0,0,0,.4);
}
}

@-moz-document url-prefix("http://www.lostfilm.tv/news.php") {
.mid .content .content_head {
    padding-bottom: 6px;
	    position: relative;
}
.mid .content {
    margin: 0;
    padding: 10px;
}
.content_body a {
    color: #4e4e4e;
}
}

@-moz-document url-prefix("http://www.lostfilm.tv/browse.php?cat") {
.mid img {
    width: 497px;
    margin: -10px;
    margin-top: -26px;
    margin-bottom: 20px;
}
.mid {
    width: 477px;
        font-size: 14px;
}

.mid div h1 {
    position: absolute;
    color: #fff;
    max-width: 400px;
    font-weight: 300;
    background: rgba(0, 0, 0, 0.6);
    margin-left: -10px;
    padding: 10px;
}
.t_row {
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.25);
    margin: .5em 0;
    padding: 10px 7px;
}

.t_row.odd {
    background: none;
    box-shadow: inset 2px 0 #F44336, 0 1px 2px rgba(0, 0, 0, 0.25);
}

.content {
    margin-top: 2em;
    margin-bottom: 2em;
}

.content h2 {border-bottom: 1px solid transparent;border-top: 1px solid transparent;color: #616161 !important;padding-bottom: 10px;padding-left: 22px;position: relative;text-transform: uppercase;font-weight: 400 !important;will-change: transform,-webkit-transform;}

.content_head {
    border: 0;
}

.t_row.even {
    box-shadow: inset 2px 0 #8BC34A, 0 1px 2px rgba(0, 0, 0, 0.25);
}

.t_row:hover {
    background: #eee;
}
.content_body h1 {
    background: none !important;
    padding: 0 !important;
    margin-left: 0 !important;
    position: static !important;
    color: #000 !important;
}

.content_body img {
    width: auto !important;
    margin: 0 5px 0 0 !important;
}

.content_body p {
    margin-top: -15px;
}
}

@-moz-document url("http://www.lostfilm.tv/my.php") {
textarea#my_info, input#my_avatar {
    width: 365px !important;
}
td.odd {
    padding: 0 1em;
    text-align: left;
    vertical-align: middle;
}
.mid td {
    padding-left: 10px;
}
}

@-moz-document url("http://www.lostfilm.tv/phpbb2.php") {
body div:nth-child(3) a[href^="/shmrowse.php"]{
    display: none;
}

.forumline th {
    color: #FFFFFF;
    border: 1px solid transparent;
    border-right: none;
    background: #F44336;
    background-image: none;
}
}

@-moz-document url-prefix("http://www.lostfilm.tv/discuss.php") {
.mid .content .content_head {
    padding-bottom: 6px;
	    position: relative;
}
.mid .content {
    margin: 0;
    padding: 10px;
}
nobr {
    white-space: inherit;
}
}
