css:
@-moz-document domain("new.vk.com") {
.top_home_link .top_home_logo{background-position: 0 0;}

._im_dialogs_settings, .page_block{box-shadow: 0 1px 1px rgba(0,0,0,.2);}

.page_block {
    position: relative;
    background: #fff;
    border-radius: 1px;
    margin: 6px 0 0;
}

#page_header_wrap{ box-shadow: 0px 0px 3px rgba(0,0,0,.2);width: 100% !important;}
#page_header{width: 100% !important;}

.narrow_column_wrap {   width: 238px; }

span.left_label,.left_settings { display: none; }

#side_bar li span{position:relative}
.side_bar_inner { width: 30px;}
.left_count_wrap,.left_count_wrap:hover {
    font-size: 7px !important;
    top: 0px !important;
    left: 16px !important;
    background: #F44336 !important;
    z-index: 2 !important;
    color: white !important;
    border-radius: 12px !important;
    width: 12px !important;
    height: 12px !important;
    padding: 0px !important;
    text-align: center !important;
    position: absolute !important;
}

.leave_redesign{display:none}

html, body{height: 100%;overflow-x: hidden;}

#page_body{width: calc(100% - 120px) !important;left: -86px;position:relative;}

body #page_layout{
    width: calc(100% ) !important;
    left: 20px;
    padding:0px;
}

div#stl_left {
    width: 55px;
    overflow: hidden;
}

#stl_text{display:none}

body #im--page{padding:0px}

.im-page--dialogs {
    width: 235px !important;
    overflow:hidden !important;
}

.im-page--history {
    margin-left: 235px !important;
}

.emoji_tt_wrap {
    width: 727px;
    opacity: 0;
    filter: alpha(opacity=0);
    z-index: 119;
    left: -484px;
    bottom: 31px;
    border-radius: 0px;
    box-shadow: 0 -1px 2px rgba(0,0,0,.2);
    background-color: #fafbfc;
    border: 0px;
    position: relative;
    position: absolute;
}

.emoji_tabs {
    background: rgba(255,255,255,1);
}
.emoji_list{
	width:100%;
}

.emoji_tabs_wrap {
    position: absolute;
    width: calc(100% - 90px);
}

.emoji_tabs_r_s {
    margin-left: 675px;
}

#emoji_block_0:before,#emoji_block_0:after{display:none}

.emoji_tabs_l_s, .emoji_tabs_r_s {
 background:#fff !important;
}
.emoji_tabs_l_sc, .emoji_tabs_r_sc {
	display:none;
}

#side_bar ol li {
    margin-left: -8px;
    height: 27px;
}
}
