css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('linkedin.com'), url-prefix('https://www.linkedin.com') {

#top-header {
        background-color: #32506d !important;
	background-image:none !important;}

.wrapper {
	width: auto !important;
	padding: 0 1em;}

.global-header .header-section.first-child{
	margin-left: -155px !important;}

.member-ads{display:none}

#text-ad-container #ad-slot-3.ad-hdr{display:none}
.recommended-sections-container .viewmore-container.state-viewmore.view-1{display:none}
.aux-module{display:none}
.wvmp-upsell{display:none}
.top-card{height:342px;}
.section-container{margin-top:-10px; width:974px;}
.ad{display:none}
.ad{display:none}

.responsive-nav {
        background-color: #32506d !important;
        background-image:none !important;}
.premium-nav {display:none}
.feed-update {border-radius:0px;}
.image {border-radius:0px;}
.feed-update>.content>.image {border-radius:50px;}
.image.medium-actor-image img {border-radius:50px;}

 li[class*="linkedin:pulseContentPool"], li[class*="pulse-recommend-article-rollup"], .people-connect-recommend   {
        display: none;}

::-webkit-scrollbar{
	width: 15px;
	height: 0px;
	background-color: #a1a1a1;
	opacity: 0%;}

::-webkit-scrollbar-thumb {
  background-color: #32506d;
  border-top-left-radius: 0px;
  border-top-right-radius: 0px;
  border-bottom-right-radius: 0px;
  border-bottom-left-radius: 0px;}

#props{display:none}

    .bottom-nav {
        background: #171617 !important;
    }
    a.tab-name {
        color:#F3F3F3 !important;
    }
.global-search {
padding: 6px 0 6px;
float: right;
margin-left: 156px;
box-sizing: content-box;
}
.global-search .search-scope .label {
background: #575757;
background-image: -webkit-linear-gradient(top, #888888 0%,#888888 4%,#575757 100%);
background-image: -moz-linear-gradient(top, #888888 0%,#888888 4%,#575757 100%);
background-image: -o-linear-gradient(top, #888888 0%,#888888 4%,#575757 100%);
background-image: linear-gradient(top, #888888 0%,#888888 4%,#575757 100%);
-webkit-border-radius: 0px 0 0 0px;
-moz-border-radius: 0px 0 0 0px;
-ms-border-radius: 0px 0 0 0px;
-o-border-radius: 0px 0 0 0px;
border-radius: 0px 0 0 0px;
height:30px;
display: block;
}
.global-search .search-box-container {
-webkit-border-radius: 0 0px 0px 0;
-moz-border-radius: 0 0px 0px 0;
-ms-border-radius: 0 0px 0px 0;
-o-border-radius: 0 0px 0px 0;
border-radius: 0 0px 0px 0;
border: none;
	height:30px;
width: 345px;
background: #fff;
float: left;
}
.global-search .search-button {
font-weight: bold;
border-width: 0px;
border-style: solid;
cursor: pointer;
margin: 0;
overflow: visible;
text-decoration: none !important;
width: 40px;
text-shadow: 0 0px 0px rgba(0,0,0,0.35);
-webkit-border-radius: 0px;
-moz-border-radius: 0px;
-ms-border-radius: 0px;
-o-border-radius: 0px;
border-radius: 0px;
padding: 0 10px;
height: 30px;
line-height: 30px;
-webkit-box-sizing: border-box;
-moz-box-sizing: border-box;
box-sizing: border-box;
font-size: 13px;
color: #fff;
background-color: #1c1c1c;
	border-color: #1c1c1c;}

    #main-search-box {
        border:none !important;
    }
    .chrome-v4 .global-nav .global-search .search-box-container {
        border:none !important;
        height:26px !important;
    }
    .chrome-v4 .global-nav .global-search .search-scope .label {
        border:none !important;
        height:26px !important;
    }
    .feed-photo.photo {
        margin-left:10px !important;
    }
    #slick-sharing-cont .member-photo {
        margin-left:-5px !important;
        margin-right:20px !important;
    }
    #ul.search-selector {
        margin-left:20px !important;
    }
    .top-nav div.account {
        display:none !important;
    }
    #today-news-wrapper {
        display:none !important;
    }

    #slick-sharing-cont {
        border-bottom:1px solid #DDD !important;
    }
    
    #slick-sharing-cont img {
        margin-top:15px !important;
        padding-left:15px !important;
    }
    .add-connection-cont {
        display:none;
    }
*:focus {outline: none;}
.profile-card .profile-picture img{border-radius:50%}
.recommended-sections-headline {display:none}
#freemium-bg {height:100% !important;}
.freemium-bg-images {height:100% !important; position:absolute !important;}
#aux {opacity:0.6 !important;}
#ad-right-top{display:none !important;}
#content {margin-left:200px}
#pymk {display:none}
#sidebar-footer {display:none}
#identity {display:none}
#in-logo {display:none}
}
