css:
@-moz-document domain("pokeheroes.com") {
/*

This PH Theme is made by Aahan on Pokeheroes.
If you like this skin, then please feel free to use it and suggest it to your friends.
Enjoy!!!!

*/

#header h1 {
    color: #CCC;
    text-shadow: 0px 0px 3px #000000, 0px 0px 3px #000000, 0px 0px 3px #000000, 0px 0px 3px #000000, 0px 0px 3px #000000, 0px 0px 3px #000000, 0px 0px 3px #000000, 0px 0px 3px #000000, 0px 0px 3px #000000, 0px 0px 3px #000000, 0px 0px 3px #000000, 0px 0px 3px #000000, 0px 0px 3px #000000, 0px 0px 3px #000000, 0px 0px 3px #000000, 0px 0px 3px #000000, 0px 0px 3px #000000, 0px 0px 3px #000000;
}
body {
    background-color: #f4d3b2;
    background-image: url(https://lh5.googleusercontent.com/-vIKM69Tpj_Y/T5LZMcbCA0I/AAAAAAAAAOA/0ZQNDjcuWNU/s1226/w12985024942362.jpg) !important;
    background-repeat: repeat;
    margin: 0;
    padding: 0;
    font-family: Verdana, sans-serif;
    
}
input[type="submit"] {
    background-color: #ffad47;
    border-color: #5f4109;
    padding: 0px 16px;
    font-size: 12pt;
    font-weight: bold;
}
button {
    cursor: pointer;
    background: #cc0000;
    padding: 0px 6px 2px 6px;
    border: 2px solid #6D1B08;
    font-size: 11pt;
    font-weight: bold;
    color: #3B0303;
    margin-top: -3px;
}
button:hover {
    background: #bff8fd;
    color: #bff8fd;
}
#header {
    position: relative;
    width: 980px;
    height: 176px;
    margin: auto auto;
    margin-top: 55px;
    border: 1.5px solid #2e393a;
    background: url("http://img00.deviantart.net/5ab5/i/2013/014/2/f/yveltal_desktop_background__1600x900__by_leobreacker-d5riut5.png") -20px -110px !important;
    background-color: #bff8fd;
    
}
#main {
    width: 980px;
    position: relative;
    margin: auto auto;
    margin-top: 4px;
    margin-bottom: 30px;
    border: 1.5px solid black;
    background: rgba(191,248,253,0.6) url(http://pokeheroes.com) repeat-x;
}
a.userbar {
    color: #555555;
}
a.userbar:hover {
    color: #FF5500;
}
#userbar {
    position: fixed;
    z-index: 15;
    top: 0%;
    margin-top: -10px;
    left: 50%;
    margin-left: -490px;
    height: 55px;
    width: 980px;
    background: #aff682;
    border: 2px dashed blue;
    font-weight: bold;
    color: #2F1876;
    font-size: 20px;
    font-family: Harrington;
}
#userbar .smallMarginRight {
    margin-right: 20px;
}
#userbar .smallMarginLeft {
    margin-left: 30px;
}
#footer {
    font-size: 10px;
    clear: both;
}
#sidebar {
    margin-left: 8px;
    margin-right: 8px;
    margin-top: -4px;
    padding: 12px;
    width: 250px;
}
@media (max-width: 800px) {
    #sidebar {
        display: none;
    }
    #header .menuButton {
        display: block;
    }
}
#sidebar {
    font-size: 10pt;
    width: 175px;
    padding: 12px;
    background-color: rgba(191, 248, 253,0);
}
#sidebar .sidebox h2 {
    font-size: 14pt;
    margin: 0;
    background-color: rgba(191,248,253,0.6);
}
#content {
    margin-top: 8px;
    margin-left: 8px;
    margin-right: 8px;
}
#content .contentbox {
    font-size: 10pt;
    margin-bottom: 16px;
    padding: 16px;
    background-color: #bff8fd;
}
#content .contentbox h2 {
    font-size: 14pt;
    margin: 0;
}
div.success {
    margin-top: 8px;
    margin-bottom: 8px;
    text-align: center;
    padding: 12px;
    background: #3AA73A;
    border: 2px solid #032B03;
    color: #032B03;
}
div.sidenav {
    width: 100%;
}
div.sidenav a {
    display: inline-block;
    margin-left: 12px;
    margin-top: 8px;
    width: 100%;
    font-size: 12pt;
    font-weight: bold;
    text-decoration: none;
    color: #3362f9;
}
div#pkmn_profile {
    padding: 6px;
    width: 250px;
    background: #ff9090 !important;
    float: left;
    margin-right: 16px;
}
#profile_bar {
    position: absolute;
    margin-left: 80px;
    padding: 5px 0px;
    padding-left: 25px;
    width: 150px;
    height: 18px;
    border: 1px solid #444;
    background: #ffad47 !important;
    z-index: 1;
    font-weight: bold;
    color: #232323 !important;
    font-size: 10px;
    
}
td.poketable1 {
    font-size: 11px;
    font-weight: bold;
    padding: 4px;
    width: 110px;
    height: 20px;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
    color: #775500 !important;
    background-color: #ffad47 !important;
}
td.poketable2 {
    font-size: 11px;
    font-weight: bold;
    padding: 4px;
    width: 110px;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
    color: #414141 !important;
    background-color: #FF9933 !important;
}
#nav_button {
    width: 97%;
    position: relative;
    left: 1.5%;
    height: 34px;
    cursor: pointer;
    padding-top: 8px;
    background: #ffe34c;
    font-size: 25px;
    color: 	#091805;
    text-align: center;
    vertical-align: middle;
    opacity: 1;
    font-variant: small-caps;
}
#nav_button:hover {
    background: #8c98f7;
    color: #ffe34c;
}
#subnav {
    position: absolute;
    width: 475px;
    padding: 50px;
    padding-top: 0;
    background: #120f20;
    z-index: 10;
    display: none;
    color: #091805;
    font-size: 11pt;
    letter-spacing: 1px;
    font-family: Harrington, Harrington, sans-serif;
}
#party_box {
    background: rgba(171,246,180,0.9) !important;
}
.feed_ava {
    opacity: 1!important;
}
.feed_ava:hover {
    opacity: 1 !important;
}
td.feed_text {
    background: #8df8fb !important;
    
}
td.feed_text:hover {
    background: #b3a0ff !important;
}
div.right_box {
    background: #b3a0ff !important;
}
li.filter {
    background: #f5ff63 !important;
}
#daily_statistics {
    color: #f5ff63 !important;

}
#post_field {
    width: 98.5%;
    position: relative;
    margin-left: 7.5px;
    Margin-top: 0px;
    background: rgba(253, 208, 137, 1) url(http://pokeheroes.com) repeat-x;
    border: none;
}
table.post_table td.post_info {
    width: 150px;
    text-align: center;
    border-right: 1px solid;
    background: #ffad47 url(http://pokeheroes.com) repeat-x;
    padding-bottom: 10px;
    Margin-bottom: -5px;
}
.tabcontent {
    display: none;
    border: none;
    background: rgba(248, 128, 25, 0.6) !important;
    z-index: 2;
    position: relative;
}

#big_button {
    width: 130px;
    height: 130px;
    background: #ff2121 url(http://pokeheroes.com) repeat-x;
    border: 2px dashed #0a0f50;
    padding: 10px;
    text-align: center;
    color: #e2e3f8;
    font-family: Harrington;
    font-size: 18pt;
    font-weight: bold;
    display: table-cell;
    vertical-align: middle;
}
#party_field {
    width: 130px;
    height: 130px;
    background: rgba(255,33,33, 0.47) url(http://pokeheroes.com) repeat-x;
    border: 2px dashed #0a0f50;
    padding: 10px;
    color: #e2e3f8;
    font-weight: bold;
    display: table-cell;
    vertical-align: middle;
}
#pkmn_storage_small {
    background: rgba(255, 173, 71, 0.47) url(http://pokeheroes.com) repeat-x;
    color: black;
}
#box_select {
    background: rgba(255, 173, 71, 0.47) url(http://pokeheroes.com) repeat-x;
    color: black;
}
#pkmn_storage_info {
    background: rgba(255, 173, 71, 0.47) url(http://pokeheroes.com) repeat-x;
    color: black;
}
#friend_bar {
    position: fixed;
    width: 200px;
    height: 100%;
    left: 100%;
    top: 0;
    margin-left: -200px;
    background: grey;
    z-index: 75;
    overflow-x: hidden;
    overflow-y: auto;
    border-left: 1px solid black;
    display: none;
}
#friend_bar::-webkit-scrollbar {
    width: 0px;
}
#friend_bar a {
    display: inline-block;
    width: 100%;
    height: 40px;
    padding: 5px;
    text-decoration: none;
    vertical-align: middle;
    border-bottom: 1px solid black;
    background: lightblue;
    
}
#friend_bar a:hover {
    background: #AAAAAA;
}
#friend_bar a img {
    max-width: 40px;
    max-height: 40px;
    font-size: 8pt;
}
#friend_bar span.img {
    width: 50px;
    float: left;
}
#friend_bar a span.last_msg {
    font-weight: normal;
    color: black;
    font-size: 7pt;
    font-style: italic;
}
#friend_bar input {
    position: fixed;
    top: 100%;
    left: 100%;
    margin-left: -200px;
    height: 24px;
    margin-top: -30px;
    width: 200px;
}
#private_chat {
    position: fixed;
    top: 100%;
    left: 100%;
    width: 300px;
    margin-left: -650px;
    height: 350px;
    margin-top: -350px;
    background: white;
    border: 1px solid black;
    z-index: 70;
    overflow: hidden;
    display: none;
}
#private_chat div.chat_head {
    width: 100%;
    height: 16px;
    background: #313131;
    position: absolute;
    top: 0;
    left: 0;
    font-weight: bold;
    color: white;
    padding: 6px;
    padding-top: 10px;
}
#private_chat div.chat_inner {
    margin-top: 32px;
    height: 274px;
    overflow-y: auto;
    overflow-x: hidden;
    position: relative;
}
#private_chat div.chat_msg {
    border-bottom: 1px solid rgba(0, 0, 0, 0.3);
    padding: 4px;
    font-size: 8pt;
    color: black;
    min-height: 25px;
    position: relative;
    margin-top: 3px;
    line-height: 18px;
}
#private_chat div.user_ava {
    position: absolute;
    left: 0;
    top: 0;
    margin-left: 4px;
    float: left;
    height: auto;
}
#private_chat div.user_ava img {
    max-width: 25px;
    max-height: 25px;
}
#private_chat div.chat_txt {
    position: relative;
    margin-left: 30px;
}
#private_chat textarea {
    margin-top: 4px;
    margin-left: 4px;
    height: 24px;
    width: 85%;
    background: gray;
    color: white;
}
#userp_top {
    position: relative;
    width: 95%;
    left: 2.5%;
    margin-left: -15px;
    padding: 15px;
    border: 1.5px solid #284752;
    background: rgba(255, 92, 0, 0.4) url(http://pokeheroes.com) repeat-x !important;
    
}
#userp_box {
    position: relative;
    width: 88%;
    left: 6%;
    margin-left: -15px;
    margin-top: 15px;
    padding: 15px;
    padding-top: 5px;
    background: rgba(66,134,249, 0.2) url(http://pokeheroes.com) repeat-x !important;
    border: 1.5px solid #608896;
    font-size: 15px;
    box-shadow: 3px 3px 3px #1f5f88;
    
}
#userp_feeds {
    max-height: 260px;
    overflow-y: auto;
    overflow-x: hidden;
    width: 100%;
    position: relative;
    
}
#userp_feed2:hover span.feed_remove {
    display: block;
}
span.feed_remove {
    display: none;
    float: right;
}
.userp_feed {
    margin-top: 5px;
    padding: 5px;
    padding-top: 8px;
    border: 1.5px solid;
    margin-left: 40px;
    width: 80%;
    background: rgba(72,249,66, 0.6) url(http://pokeheroes.com) repeat-x !important;
}
span.userfeed_date {
    font-family: Harrington;
    margin-left: 12px;
}
span.userp_username {
    font-size: 30px;
    font-weight: bold;
    font-family: Harrington;
}
span.userp_top_info {
    font-size: 20px;
    font-family: Harrington;
}
h2.userp_box {
    font-size: 18px;
    border-bottom: 2px solid;
    
}
#userp_box td {
    font-size: 15px;
    border: 1px solid darkblue;
    background: rgba(66,134,249, 0.4) url(http://pokeheroes.com) repeat-x !important;
    padding: 4px;
    text-align: center;
    vertical-align: middle;
}
#userp_box th {
    font-size: 15px;
    border: 1px solid darkblue;
    background: rgba(66,134,249, 0.6) url(http://pokeheroes.com) repeat-x !important;
    padding: 4px;
    font-family: Harrington;
}
img#userp_thumbnail {
    border: 1.5px solid;
    margin-left: 5px;
    box-shadow: 6px 5px 10px #1f5f88;
}
div.poll_selector {
    width: 0;
    padding: 6px;
    background: rgba(255, 92, 0, 0.4) url(http://pokeheroes.com) repeat-x !important;
    border: 1px solid #062538;
    cursor: pointer;
}
td.poll_selector {
    background: none;
    border-width: 0px !important;
}
.online_sign {
    font-weight: bold;
    width: 100px;
    opacity: 0.9;
    text-align: center;
    
}
#dvC {
    background: rgba(255, 92, 0, 0.4)!important;
}
.pkmn_field {
    background: rgba(255, 92, 0, 0.4)!important;
}
}
