@-moz-document domain(endchan.xyz) {
/*Global*/
body {
    background:#D2FFEE url("https://i.imgur.com/Q7VNxIo.png") repeat 0 0!important;
    color: #000;
    font-family: arial,helvetica,sans-serif;
}

a {
    color: #345456;
	text-decoration: none;
}

a:hover {
    color: #D00;
}

#threadList {
    padding:0 5px 0 5px;
}

hr {
    border-color: #B7D9C5;
    border-top: 1px solid #B7C5D9;
    height: 0;
    clear: both;
}

/*nav*/
nav {
    background-color: #d6f0e4;
    border-bottom: 1px solid #A9A9A9;
	box-shadow: 0 1px 2px rgba(0, 0, 0, .15);
	z-index: 100;
}

nav:hover {
    background-color: #d5faff;
}

nav a:hover {
    color:white;
    background-color: #FF7800;
}

/*Index*/
#topBox {
    border: none;
    background: none;
    text-align: center;
    font-size: 16px;
    padding: 10px 0 20px 0;
}

#innerWrapper > div, .infoBox {
    background-color: #d6f0e4;
    border-width: 1px;
    border-style: none solid solid none;
    border-color: #b6dbdc;
}

div#links {
    background: none;
    border: none;
}

.linkPost img{
    padding: 6px 0 6px 0;
}

footer {
    background: none;
    font-size: 10px;
    margin: 20px 0 10px 0;
}

/*boards.js*/
#boardsCellHeader span{
    font-weight: bold;
    text-align: center;
    background-color: #ff7800;
    border: 1px solid #FF7F0D;
    color: #FFF;
    text-shadow: 1px 1px #AA5912, 1px -1px 0 #E06900, 1px 0px #E06900, -1px -1px 0 #E06900, -1px 1px 0 #E06900;
}

.boardsCell:nth-of-type(2n+1) span {
    background-color: #d6f0e4;
    border-width: 1px;
    border-style: none solid solid none;
    border-color: #b6dbdc;
}

/*boardManagement, logs*/
.settings {
    background-color: #d6f0e4;
    border-width: 1px;
    border-style: none solid solid none;
    border-color: #b6dbdc;
}

.logCell {
    font-weight: bold;
    font-size: 15px;
    margin:10px;
    padding:0 5px 0 0;
}

.logCell p span {
    font-weight: normal;
    padding: 10px;
}

/*Board Header*/
#bannerImage {
    border: 1px solid #a9a9a9;
    margin: -5px 0 15px 0;
}

#modeBanner {
	color: rgba(0, 60, 60, 1);
	background-color: rgba(151, 255, 216, 0.34);
}

.boardHeader p{
	font-family: tahoma;
	color: #003333;
}

.boardHeader p#labelName {
	font-weight: bold;
	font-size: 22pt;
	color: #003333;
}

.boardHeader a {
    font-family: arial, sans-serif;
    font-size: 12px;
}

#divMessage {
    text-align: center;
    color: red;
    font-size: 12px;
    font-weight: bold;
}

/*Caixa de postagem*/
.form-post th {
    background-color: #ff7800;
    color: #FFF;
    font-size: 10pt;
    border: 1px solid #FF7F0D;
    text-shadow: 1px 1px #AA5912, 1px -1px 0 #E06900, 1px 0px #E06900, -1px -1px 0 #E06900, -1px 1px 0 #E06900;
}

input, button, textarea {
    border: 1px solid #AAA;
    padding: 3px 4px 3px;
}

input:focus, textarea:focus {
    border: 1px solid #FF7800;
}

/*Caixa Resposta rapida*/
#quick-reply table {
	background-color: #D6F0E4;
}

#post-form-inner th {
	background-color: #FF7800 !important;
    text-shadow: 1px 1px #AA5912, 1px -1px 0 #E06900, 1px 0px #E06900, -1px -1px 0 #E06900, -1px 1px 0 #E06900;
}

#quick-reply th .handle {
	color: white;
	font-size: 13px;
}

/*Postagens*/
.innerPost {
    background-color: #d6f0e4;
	border-width: 1px;
	border-style: none solid solid none;
	border-color: #b6dbdc;
    max-width: 95%;
}

.innerPost .uploadCell img {
    padding: 6px;
    margin-right: 20px;
}

.markedPost {
	background-color: #A9D8FF;
}

.divMessage {
    font-size: 13px;
	margin:12px 19px 12px 28px;
	padding:0 5px 0 0;
}

.quoteLink {
    color: #345456;
}

.noEmailName, .noEmailName:hover {
	font-size: 13px;
    color: #117743;
}

.linkName:link {
    font-size: 13px;
    color: #345456;
}

.linkName:link:hover {
    color: #D00;
}

.panelBacklinks, .uploadCell, .labelLastEdit, .labelOmission {
    font-size: 11px;
}

.labelOmission {
    color: #5D5D5D;
    margin-top: 1em;
}

.contentOmissionIndicator {
    margin: 15px 0 5px 10px;
}

.divBanMessage {
	font-size: 14px;
	color: #ed1c24;
    margin: 0 10px 7px 10px;
}

/*hover reply box*/
.quoteblock {
    background: none !important;;
}

.quoteblock .labelCreated, .quoteblock .linkQuote, .quoteblock .labelRole, .quoteblock .spanId, .quoteblock .linkSelf{
    font-size: 12px;
}

/*Formatacao*/
.greenText{
    color: #648123;    
}

span.spoiler {
    background: #000;
    color: #000;
}

.spoiler:hover {
    color: #FFF;
    background-color: #000;
}

.redText {
    font-size: 14px;
    color: #AF0A0F;
}

code {
    font-family: monospace;
    font-size: 12px;
    padding: 2px;
}

/*Catalogo*/
.catalogCell {
    vertical-align: top;
    text-align: center;
    font-weight: normal;
    display: inline-block;
    font-size: 16px;
    max-height: 238px;
    margin: 0 15px 25px 20px;
    padding: 15px;
}

.catalogCell:hover {
	overflow-y:auto;
}

.linkThumb img {
	border: 2px solid rgba(153,153,153,0);
	box-shadow: 0 0 4px rgba(0,0,0,0.55);
}

/*Lista de paginas/catalogo, fim da pagina*/
.threadPages, .boardPages {
    background: none;
    border: none;
    font-size: 15px;
    margin-top: 25px;
}

#divPages {
	background: none;
}

/*Caixa de report*/
.contentAction {
    background-color: #d6f0e4;
    border-width: 1px;
    border-style: none solid solid none;
    border-color: #b6dbdc;
    float: none;
    text-align: center;
    margin-right: auto;
    margin-left: auto;
    width: 25%;
    min-width:300px;
}

#reportFieldReason {
    margin-left: 2.1em;
}

#deletionFieldPassword {
    margin-left: 1em;
}

img#captchaImageReport {
    margin-left: auto;
    margin-right: auto;
}

/*hidepreview*/
.linkPreview {
	visibility: collapse;
}

.linkPreview + .linkSelf {
	margin-left: -50px; /*Move post number over Preview*/
}

.divMessage {
    min-width: 300px; /*fix post number position*/
}

.quoteblock .linkSelf {
	margin-left: 0; /*Fix hover post box*/
}

.catalogCell .divMessage {
    min-width: 0; /*Fix calalog*/
}

}
