css:
@-moz-document domain("streamup.com") {

body,
.partner p,
.partner .more,
.partner h1,
.partner h2,
.partner h3,
.partner h4
{
    color: #fff !important;
    font-family: anton !important;
}

p.notice {
    background-color: #fff !important;
    font-family: anton !important;
    
}

strong { color: #fff; }

    
.streamupControlsStreamupLogo {
    display: none !Important;
}

.streamupControlsTopBar {
    display: none !important;
}
    
    
    
html,
body,
.header,
.header .search,
.channel-page,
.channel-page .header,
.footer,
.channel-wrap {
    background-color: #503a60;
}
.channel-profile .channel-title {
    color: #503a60;
}
.channel-profile .username a {
    color: #503a60;
}
.about .hero.bottom,
.home-categories,
.header .search .search-input,
.chat .chat-rows-wrap .chat-rows,
.chat-wrap .chat,
.chat-text-bar .new-chat-line,
.viewers {
    background-color: #503a60;
}
.chat-row .chat-row-content,
.viewers-list .chat-row .chat-row-user a,
.chat-suggestions .chat-row .chat-row-user a,
.chat-text-bar .new-chat-line {
    color: #fff;
}

.header .search .search-input {
    background-color: transparent;
}

.about .hero.bottom,
.home-categories,
.header,
.header .search,
.chat-tipping,
.chat-wrap,
.chat-wrap .chat,
.chat .chat-text-bar,
.chat-text-bar .new-chat-line {
    background-color: white;
}
.dropdown-menu {
    background-color: #503a60;
}
.dropdown-menu>li>a {
    color: #503a60;
}
.about .hero.bottom,
.dropdown-menu>li>a.special {
    color: #503a60;
}
.channel-wrap {
    padding: 0;
}
.chat-wrap {
    height: 45%;
    top: 55% !important;
    z-index: 1000;
    opacity: 0;
}
.chat-wrap:hover {
    opacity: 0.85;
}

body.channel-page {
    padding-top: 0;
}

body.channel-page .header {
    opacity: 0;
}

body.channel-page .header:hover {
    opacity: 0.85;
}

.modal-content {
    background-color: #503a60;
}

}
