css:
@-moz-document url-prefix("http://www.hitbox.tv") {
@font-face {
    font-family: "Starwars";
    src: url(http://spaceextanded.github.io/Star.ttf) format("truetype");
}

@font-face {
    font-family: "batman";
    src: url(http://spaceextanded.github.io/batman.ttf) format("truetype");
}



.theme-dark .btn-subscribe, .theme-light .btn-subscribe {
    background: #ff0000;
}
#nav-logo path {
    fill: #ff0000;
}

body.theme-dark a:hover, body .force-dark a:hover {
    color: #ff0000;
}

#detailpage #detail-meta .detail-tabs-wrapper .profile-edit .blue-text {
    color: #ff0000;
}

.follow-pop .follow-pop-header {
    padding: 12px;
    font-size: 14px;
    color: #ff0000;
    font-weight: bold;
}


.theme-dark .btn-green, .theme-light .btn-green, .theme-dark .btn-green-hover:hover, .theme-light .btn-green-hover:hover, .theme-dark input[type=submit].btn-green, .theme-light input[type=submit].btn-green, .theme-dark input[type=submit].btn-green:hover, .theme-light input[type=submit].btn-green:hover {
    border: 0px none;
    background-color: #ff0000;
    color: white;
}

.subscribe.page .sub-header .name {
    font-size: 26px;
    line-height: 26px;
    font-weight: 600;
    font-family:Starwars;
    padding-bottom: 5px;
    display: inline-block;
}

.toggle-switch.switch-on {
    background-color: #ff0000;
    font-family:Starwars;
    border-color: #ff0000;
}

.theme-dark #detailpage #detail-meta .detail-tabs-wrapper .detail-tab.active {
    border-color: #ff0000;
}


#chat-tabs>div.active {
    opacity: 1;
    border-bottom-color: #ff0000;
    font-family:Starwars;
    color: #ff0000;
}
.menu-item:hover {
    color: white !important;
    font-family:Starwars;
    background: #ff0000;
}

.toggle-switch.toggle-switch-settings.switch-on {
    background-color: #ff0000;
    font-family:Starwars;
    border-color: #ff0000;
}


.nav-links>span.highlight, .nav-links>a.highlight {
    color: #ff0000;
    font-family:Starwars;
    opacity: 1;
    font-weight: bold;
}


.subscribe.page .sub-header .plan-price .price {
    color: #ff0000;
    font-family:Starwars;
    font-weight: 600;
}



.settings-nav a.active {
    opacity: 1;
    display: inline-block;
    border-color: #ff0000;
    color: #ff0000;
}



.toggle-switch.switch-on {
    background-color: #ff0000;
    font-family:Starwars;
    border-color: #ff0000;
}

.tse-scrollbar .drag-handle {
    background: white;
    position: absolute;
    z-index: 10;
    right: 2px;
    min-height: 10px;
    width: 8px;
    opacity: 0;
    -webkit-transition: opacity 0.2s linear;
    -moz-transition: opacity 0.2s linear;
    -o-transition: opacity 0.2s linear;
    -ms-transition: opacity 0.2s linear;
    transition: opacity 0.2s linear;
     -background: #ff0000;
    -webkit-background-clip: padding-box;
    -moz-background-clip: padding;
}

.theme-dark .btn-blue:hover, .theme-light .btn-blue:hover {
    background: #ff0000;
    font-family:Starwars;
}

.theme-dark .btn-blue, .theme-light .btn-blue {
    background: #ff0000;
    font-family:Starwars;
    color: white;
    
}

.a:hover {
    color: #ff0000;
    font-family:Starwars;
}


.theme-dark #detail-content .content-box .deckgrid .column a {
    color: #ff0000;
    font-family:Starwars;
}



button {
	background-color: #ff0000!important;
    font-family:Starwars;
}

.theme-dark .btn-follow:hover, .theme-light .btn-follow:hover {
    background: #ff0000;
    font-family:Starwars;
}


.theme-dark .btn-follow:hover, .theme-light .btn-follow:hover {
    background: #ff0000;
    font-family:Starwars;
}
}
