css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("alpha-hordes.forumactif.net") {
/* bluflonalgul a bricolÃ© vite fait un style furtif pour ne pas attirer les zombies de l'outre-monde*/
/* V 1.2 */

img[id="i_logo"]{
  display:none !important;
}

body {
  background-color:white !important;
  background-image:none !important;
}

.bodyline {
background-color:white !important;
}

th {
  background-image:none !important;
  background-color:white !important;
  color: black !important;
}

td.row1, td.row2 {
  background-color:white !important;
}

a:link, a:active, a:visited, a.postlink:link, a.forumlink, a.topictitle:link, a.nav {
  color:black !important;
  text-decoration:Underline !important; /* soulignÃ© comment ? */
}

td.cat, td.catHead, td.catSides, td.catLeft, td.catRight, td.catBottom {
background-color:#EEEEEE !important;
background-image:none !important;
}

td.rowpic {
background-color:#EEEEEE !important;
background-image:none !important;
}

td.row3, td.row3Right, td.spaceRow {
background-color:#EEEEEE !important;
}

a.cattitle {
color:#444444 !important;
}

input.mainoption, input.liteoption  {
background-color:#EEEEEE !important;
}
img {
opacity: 0.2 !important;
}

input, input.post, textarea.post, select {
background-color:#F0F0F0 !important;
}

.maintitle, .maintitle h1 {
color:#999999 !important;
font-size:14px !important;
}

.bodylinewidth {
width:96% !important;
}

span[class="postdetails poster-profile"] img[alt="" ] {
display:none; /* pas d'image avatar */
}

.quote {
background-color:#888888 !important;
border:1px solid #444444 !important;
}

.bodyline {
border:none !important;
}

.forumline {
background-color:#999999 !important;
border:1px solid #AAAAAA !important;
}

embed{ display: none !important }
h1.cattitle { color: #444 !important }
a.nav  { color: #444 !important }

/*FIN*/
}
