@-moz-document domain("forum.ubuntu.ru"), domain("packages.ubuntu.com"), domain("debianforum.ru") {
/* Dark style for forun.ubuntu.ru, debianforum.ru & packages.ubuntu.com 
Author - Haron Prime
License WTFPL http://www.wtfpl.net/
Version 1.5.0 */

/* Base */
*, ::before, ::after {
	color: #aaa !important;
	border-color: #555 !important;
	outline-color: #555 !important;
	text-shadow: none !important;
	box-shadow: none !important;
	background-color: transparent !important;
}
html * {
	color: inherit !important;
}
p::first-letter, h1::first-letter, h2::first-letter, p::first-line {
	color: inherit !important;
	background: none !important;
}
html, body, table, header, article, section, nav, menu, aside, [class*="nav"], [class*="open"], [id*="ropdown"], [class*="ropdown"], div[class*="menu"], [class*="tooltip"], div[class*="popup"], div[id*="popup"], div[class*="note"], span[class*="note"], div[id*="detail"],div[class*="detail"], div[class*="description"], div[class*="content"], div[class*="container"], div[style*="display: block"], div[style*="visibility: visible"] {
	background-color: #252525 !important;
}
/* Color change */
span[style="color: red;"], span[style="color: #FF0000;"] {
    color: #f00 !important;
}
span[style="color: black;"], span[style="color: #000000;"] {
    color: #000 !important;
}
span[style="color: yellow;"], span[style="color: #FFFF00;"] {
    color: #ff0 !important;
}
span[style="color: pink;"], span[style="color: #FFC0CB;"] {
    color: #fcc !important;
}
span[style="color: green;"], span[style="color: #008000;"] {
    color: #080 !important;
}
span[style="color: orange;"], span[style="color: #FFA500;"] {
    color: #fa0 !important;
}
span[style="color: purple;"], span[style="color: #800080;"] {
    color: #808 !important;
}
span[style="color: blue;"], span[style="color: #0000FF;"] {
    color: #33f !important;
}
span[style="color: beige;"], span[style="color: #F5F5DC;"] {
    color: #ffd !important;
}
span[style="color: brown;"], span[style="color: #A52A2A;"] {
    color: #a22 !important;
}
span[style="color: teal;"], span[style="color: #008080;"] {
    color: #088 !important;
}
span[style="color: navy;"], span[style="color: #000080;"] {
    color: #24c !important;
}
span[style="color: maroon;"], span[style="color: #800000;"] {
    color: #800 !important;
}
span[style="color: limegreen;"], span[style="color: #32CD32;"] {
    color: #3c3 !important;
}
span[style="color: white;"], span[style="color: #FFFFFF;"] {
    color: #fff !important;
}

 
/*: No BG :*/
*:not(:empty):not(span):not([class="html5-volume-slider html5-draggable"]):not([class="html5-player-chrome html5-stop-propagation"]), *::before, *::after,
td:empty, p:empty, div:empty:not([role]):not([style*="flashblock"]):not([class^="html5"]):not([class*="noscriptPlaceholder"]) {
	background-image: none !important;
}
 
/*: Filter non-icons :*/
span:not(:empty):not([class*="icon"]):not([id*="icon"]):not([class*="star"]):not([id*="star"]):not([id*="rating"]):not([class*="rating"]):not([class*="prite"]) {
	background-image: none !important;
	text-indent: 0 !important;
}
 
/* Highlight */
::-moz-selection {
	background-color: #626F61 !important;
	color: #F6F7B9 !important;
}
 
/* ::: anchor/links ::: */
a:link         {color: #59b !important; text-decoration: none !important;} /* hyperlink */
a:visited      {color: #59b !important; text-decoration: none !important;}
a:hover        {color: #9df !important; background-color: inherit !important; text-decoration: none !important;}
a:active       {color: #9df !important; text-decoration: none !important;}
 
/* "Top level" div */
body > div {
	background-color: inherit !important;
}
 
/* :::::: Text Presentation :::::: */
summary, details                   {background-color: inherit !important}
kbd, time, label, .date            {color: #B6D2AC !important}
acronym, abbr                      {border-bottom: 1px dotted !important; cursor: help !important;}
mark,code,pre,samp,blockquote      {background-color: #252525 !important}
 
/* :::::: Headings :::::: */
h1, h2, h3, h4, h5, h6  { 
	background-image: none !important;
	border-radius: 5px !important;
	text-indent: 0 !important;
}
h1,h2 {background-color: #252525 !important}
h3,h4 {background-color: inherit !important}
h5,h6 {background-color: inherit !important}
 
/* :::::: Tables, cells :::::: */
table table {background: #222 !important;}
th, caption {background: #252525 !important;}
 
/* ::: Inputs, textareas ::: */
input, textarea, button, select, option, optgroup{
	color: #aaa !important;
	background: #353535 !important;
	-moz-appearance: none !important;
}
input, textarea, button {
	border-color: #555 !important;
	border-width: 1px !important;
}
 
/* :::::: Button styling :::::: */
 
input[type="button"], input[type="submit"], input[type="reset"], button {
	background:  none !important;
}
input[type="button"]:hover, input[type="submit"]:hover, input[type="reset"]:hover, button:hover {
	background: none !important;
}
input[id="search__submit"], search__submit {
	background: url(data:image/png;base64,) center center no-repeat !important;
}
input[id="search__submit"]:hover, search__submit:hover {
	background: url(data:image/png;base64,) center center no-repeat !important;
}

input[type="image"] {opacity: .85 !important}
input[type="image"]:hover {opacity: .95 !important}
 
/* Lightbox fix */
html [id*="lightbox"] * {background-color: transparent !important;}
html [id*="lightbox"] img {opacity: 1 !important;}
 
/* Youtube Annotation */
#movie_player-html5 .annotation {background: #252525 !important}
 
/* Mozilla addons shrink/expand sections */
.expando a {background: none transparent  !important;}
 
/*post images*/
.smf .topic_table td.lastpost {
    background-image: none!important;
}
img[src$="/new.png"], img[src$="/new.gif"] {
    background: url(data:image/png;base64,) left top no-repeat;
    height:18px!important;
    padding-left:18px!important;
    width:0!important;
    margin-bottom: -8px!important;
    margin-left: 4px!important;
}
[class="bbc_standard_quote"], [class="bbc_code"] {
	background-color: #353535 !important;
	border-style: solid !important;
	border-width: 1px !important;
}
.smf .table_list tbody.content td.children, #__header-menu #menu_nav li > div, div[class="poster"], [class="windowbg"], [class="windowbg2"] {
	background-color: #303030 !important;
}
td[class="windowbg2"], div[class="header-submenu"], [class="topic_starter"], [class="title"], [class="membergroup"], [class="postgroup"], [class="stars"], [class="avatar"], [class="postcount"], [class="blurb"], [class="profile"], [class="moderatorbar"], [class="quote_button"], [class="modify_button"], [class="remove_button"], [class="split_button"], [id="menu_nav"], [id="button_help"], [id="button_rules"], [id="button_search"], [id="button_admin"], [id="button_moderate"], [id="button_profile"], [id="button_pm"], [id="button_bookmarks"], [id="button_mlist"], [id="button_logout"] {
	background-color: inherit !important;
}
.smf .table_list tbody.content td.children, [scope="col"], [class="catbg"], [class=" icon1 windowbg"], [class="stickybg icon1 windowbg"], [class="lockedbg icon1 windowbg"], div[id="__header-menu"], .last_th {
	border-style: solid !important;
	border-width: 1px !important;
}
.bar > div {
    background: #6294CE !important;
}
[class="catbg"] {
	border-width: 0px !important;
}
.smf .topic_table td.subject span a {
	font-weight: bold !important;
}
.smf .activity_stats li div.bar div {
    background: #48a !important;
}
}

@-moz-document domain("forum.ubuntu.ru") {
/* Only for forum.ubuntu.ru */

/* Закрепляем шапку, выстраиваем блоки в одну строку */
#__header {
    position: fixed !important;
    z-index: 999 !important;
    top: 0px !important;
    left: 0px !important;
    width: 100% !important;
    height: 40px !important;
    background: none !important;
    background-color: #222 !important;
    box-shadow: 0px 6px 12px rgba(0,0,0,0.5) !important;
}
#__header {
	background-image: url('data:image/png;base64,') !important;
	background-repeat: no-repeat !important;
	background-position: 2em 11px !important;
}
#__header-menu {
    position: fixed !important;
    left: 4em !important;
	height: 40px !important;
	padding: 0px !important;
}
#__header-menu > ul a {
	height: auto !important;
    padding-top: 13px !important;
    font-size: 12px !important;
	color: #aaa !important;
	text-shadow: none !important;
}
#__header-menu > ul a:hover {
	color: #9df !important;
}
#__header-menu > ul a.active {
	color: #9df !important;
	font-weight: bold !important;
}
#__header-menu .header-submenu a:not(.firstlevel) {
	padding-top: 6px !important;
	padding-bottom: 4px !important;
}
#__header-menu .header-submenu {
    position: fixed !important;
    top: 0px !important;
    left: 36.5em !important;
	height: 40px !important;
    z-index: 999 !important;
    font-size: 12px !important;
}
#__search-box {
    position: fixed !important;
    top: 0px !important;
    right: 0px !important;
}
#search__input {
    height: 10px !important;
    margin-top: 3px !important;
}
#__main {
    padding-top: 32px !important;
}

/* Убираем лого */
#__header-logo {
    display: none !important;
}

/* Убираем обводку шапки */
#__header-menu, #__search-box, #__header-menu .header-submenu > div {
    border: none !important;
}

/* Убирем футер */
#__site-footer {
	display: none !important;
}

/* Уменьшаем заголовок */
#__main-title h1 {
	font-size: 24px !important;
	margin: 20px 0px 16px 8px !important;
}
#__main-title #upshrink {
	padding-top: 36px !important;
}

/* Минимизируем шапку при уменьшении отображаемой области менее 960px, удаляем лишние ссылки */
@media (max-width: 960px) {
    #__header-menu .header-submenu {
        left: 12.5em !important;
    }
    #__header-menu .header-submenu a[href="http://forum.ubuntu.ru/index.php?action=help"], 
	#__header-menu .header-submenu a[href="https://forum.ubuntu.ru/index.php?action=help"], 
	#__header-menu .header-submenu a[href="http://forum.ubuntu.ru/index.php?action=bookmarks"], 
	#__header-menu .header-submenu a[href="https://forum.ubuntu.ru/index.php?action=bookmarks"], 
	#__header-menu .header-submenu a.firstlevel.last, 
/* 	#__header-menu > ul a[href="http://help.ubuntu.ru/"],  */
/* 	#__header-menu > ul a[href="https://help.ubuntu.ru/"],  */
	#__header-menu > ul a[href="//help.ubuntu.ru/"],
/* 	#__header-menu > ul a[href="http://team.ubuntu.ru/"],  */
/* 	#__header-menu > ul a[href="https://team.ubuntu.ru/"],  */
	#__header-menu > ul a[href="//team.ubuntu.ru/"],
	#__header-menu > ul a[href="http://ubuntu.ru/get"], 
	#__header-menu > ul a[href="https://ubuntu.ru/get"], 
	#__header-menu > ul a[href="http://forum.ubuntu.ru/index.php?action=search"], 
	#__header-menu > ul a[href="https://forum.ubuntu.ru/index.php?action=search"] {
        display: none !important;
    }
}
/* Минимизируем шапку при уменьшении отображаемой области менее 640px, удаляем лишние ссылки */
@media (max-width: 640px) {
	#__header-menu .header-submenu {
        left: 7.5em !important;
    }
/* 	#__header-menu > ul a[href="http://ubuntu.ru/"],  */
	#__header-menu .header-submenu a[href="http://forum.ubuntu.ru/index.php?action=mlist"], 
	#__header-menu .header-submenu a[href="http://forum.ubuntu.ru/index.php?action=profile"], 
	#__header-menu .header-submenu a[href="http://forum.ubuntu.ru/index.php?action=rules"], 
/* 	#__header-menu > ul a[href="https://ubuntu.ru/"],  */
	#__header-menu > ul a[href="//ubuntu.ru/"],
	#__header-menu .header-submenu a[href="https://forum.ubuntu.ru/index.php?action=mlist"], 
	#__header-menu .header-submenu a[href="https://forum.ubuntu.ru/index.php?action=profile"], 
	#__header-menu .header-submenu a[href="https://forum.ubuntu.ru/index.php?action=rules"] {
		display: none !important;
	}
}
}

@-moz-document domain("debianforum.ru") {
/* Only for debianforum.ru */
span.upperframe span, span.lowerframe span {
	background: none !important;
	background-color: #303030 !important;
}
.roundframe {
	border : 1px solid #555 !important;
}
}
