css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.charazay.com")	{

/*background*/
body{
    background-attachment: fixed !important;
    background-image: url(http://oi67.tinypic.com/16m4l7t.jpg) !important; /*insert link in the brackets here*/
}
/*different background choices*/
/*dirty "old style" one: http://oi68.tinypic.com/4ryq1v.jpg*/
/*hyper lighter hardwood background: http://oi66.tinypic.com/20tgea8.jpg*/
/*basketball background: http://oi66.tinypic.com/33w2p9f.jpg*/
/*dark top lights: http://oi64.tinypic.com/sbj4oh.jpg*/
/*darker barckground: http://oi68.tinypic.com/20jnle.jpg*/
/*yet another one: http://oi66.tinypic.com/2zq4neu.jpg*/
/*slightly darker vertical: http://oi68.tinypic.com/2ptc7rr.jpg*/

/*top bar*/
#primary-nav-wrap ul {
    background-color: #FF6600; /*#F28130*/
    box-shadow: 0 1px 5px rgba(255, 255, 255, 0.6) inset, 0 -5px 25px rgba(0, 0, 0, 0.3) inset, 0px 0px 0px white
}
#primary-nav-wrap ul a {
    color: white;
}
.separatorb {
    border-left: 1px white solid;
}
#primary-nav-wrap ul li:hover {
    background-color: #F28130; /*#FF6600*/
}
#primary-nav-wrap ul ul {
	background-color: #F28130; /*#F28130*/
    box-shadow: 0 0px 0px rgba(255, 255, 255, 0.6) inset, 0 0px 0px rgba(0, 0, 0, 0.3) inset, 0px 0px 0px white
}
#primary-nav-wrap ul ul a {
    color: white;
}
#primary-nav-wrap ul ul a:hover {
    color: white;
}

/*top right buttons*/
ul.topmenu li:hover {
	background: rgba(242, 129, 48, 0.65);
}
ul.topmenu li {
	border: 1px solid #f28130;
}
ul.topmenu li.notification:before {
	background: url(http://oi67.tinypic.com/10cn7mr.jpg) 0px 0px no-repeat transparent;
}
ul.topmenu li.chat:before {
	background: url(http://oi67.tinypic.com/10cn7mr.jpg) -34px 0px no-repeat transparent;
}
ul.topmenu li.messages:before {
	background: url(http://oi67.tinypic.com/10cn7mr.jpg) -34px 0px no-repeat transparent;
}
ul.topmenu li.division:before {
	background: url(http://oi67.tinypic.com/10cn7mr.jpg) -66px 0px no-repeat transparent;
}

/*various buttons*/
a.linkButton {
    color: #fff !important;
    background: #FF6600 none repeat scroll 0 0;
    border: none;
    box-shadow: 0 1px 1px rgba(255, 255, 255, 0.6) inset, 0 -4px 12px rgba(0, 0, 0, 0.3) inset, 0px 0px 1px #FFF;
    text-shadow: none;
}
a.btngreen, button.btngreen, input.btngreen {
    border: #F87213 1px solid;
    background-color: #fF6600;
} 
a.btngreen:hover, button.btngreen:hover, input.btngreen:hover, input.inputSubmit:hover, input.inputButton:hover {
    background-color: #Ff6600 ;
}  
a.textlink {
    font-size: 100%;
    color: white;
}
.button, input.inputSubmit, input.inputButton {
    color: #FFF;
    background: #FF6600;
    text-shadow: none;
    border: none;
}
.button.white {
    border: none;
    text-shadow: none;
    background: #F0F0F0
}
a.btngeneric, button.btngeneric, input.btngeneric, input.inputSubmit, input.inputButton {
    color: #FFF;
    background-color: #FF6600;
    border: none;
	box-shadow: 0 1px 1px rgba(255, 255, 255, 0.6) inset, 0 -4px 12px rgba(0, 0, 0, 0.3) inset, 0px 0px 1px #FFF;
}
a.btngeneric:hover, button.btngeneric:hover, input.btngeneric:hover, input.inputSubmit:hover, input.inputButton:hover {
    background-color: rgba(242, 129, 48, 0.65);
}
.button.red {
    color: white;
    background-color: #FF6600;
    border: none;
    text-shadow: none;
    box-shadow: 0 1px 1px rgba(255, 255, 255, 0.6) inset, 0 -4px 12px rgba(0, 0, 0, 0.3) inset, 0px 0px 1px white
}
.button.small {
    font-weight: bold
}
  
/*minimized toolbox*/
sidebar {
    background-color: #FF6600; /*#F28130*/
    box-shadow: 0 1px 5px rgba(255, 255, 255, 0.6) inset, 0 -5px 25px rgba(0, 0, 0, 0.3) inset, 0px 0px 0px white
}

/*buttons inside toolbox*/
.site-info-t {
    background-color: #f28130;
    color: white;
    text-shadow: 0px 0px 0px;
    text-align: center;
}
.site-info-s {
    background-color: #f0f0f0;
    border: 2px solid #F28130;
}  
element.style {
    border-top: 1px solid #333;
}
#notification_container2 {
    border: 0px;
    background-color: rgba(0, 0, 0, 0.75);
}
#notification_container2::before {
    background: transparent url(http://oi67.tinypic.com/10cn7mr.jpg) no-repeat scroll 0px 0px;
}
#notification_container2:hover {
    background-color: rgba(242, 129, 48, 0.65);
}
.site-info_toolbox .helplinks a {
    background-color: rgba(0, 0, 0, 0.75);
    border: 0px;
}
div .helplinks a.forums:hover, .helplinks a.manual:hover, .helplinks a.helpdesk:hover {
    background-color: rgba(242, 129, 48, 0.65);
}
.site-info_toolbox .helplinks a.forums::before {
    background: url(http://oi67.tinypic.com/10cn7mr.jpg) -199px 5px no-repeat transparent;
}
.site-info_toolbox .helplinks a.manual::before {
    background: url(http://oi67.tinypic.com/10cn7mr.jpg) -165px 5px no-repeat transparent;
}
.site-info_toolbox .helplinks a.helpdesk::before {
    background: url(http://oi67.tinypic.com/10cn7mr.jpg) -131px 6px no-repeat transparent;
}

/*forum*/
div .post_top a {
    color: #f28130;
    text-shadow: 0px 0px 0px;
}
div .post_team { /*doesn't work yet...*/
    vertical-align: middle !important;
}

/*notifications appearing in the box on top of the screen*/
#notification_box, #notification_box2 {
    border: 1px solid #f28130
}    
#notification_box ul li, #notification_box2 ul li {
    border-bottom: 1px dashed #f28130;
}
#notification_box ul li a:hover, #notification_box2 ul li a:hover {
    background-color: #f28130;
}

/*colors of tabs in TM, tactics and matches pages*/
.tabs a {
    background: rgba(242, 129, 48, 0.65);
    color: #fff;
}
.tabs .selected a {
    color: #FFF;
    background-color: #ff6600;
}
.tabs a:hover {
    background-color: #f28130;
    color: #fff;
}
.tabs .selected a:hover {
	background-color: #ff6600;
}
ul.tabs {
    border-bottom: 3px solid #ff6600;
}

/*game page*/
section#matches-arena #live_table h1 {
  font-size: 0px; /*size of 'AT' on scoreboard, 0 means its invisible*/
  border-top: 0px; /*upper bar on 'AT'*/
  border-bottom: 0px; /*lower bar on 'AT'*/
}
table#tableresults td {
    padding: 2px 0px;
}
table#tableresults th {
    padding: 2.2px 0;
    text-align: center;
    width: 0%;
}

/*teampage*/
.TabbedPanelsTab {
    color: white;
    background-color: #FF6600 /*rgba(242, 129, 48, 0.65)*/
}
.TabbedPanelsTab:hover {
    color: white;
	background-color: #F28130
}
.TabbedPanelsTabSelected {
    color: #FF6600;
    background-color: white
}
.TabbedPanelsTabSelected:hover {
    background-color: white;
    color: #FF6600
}
.teamsnames .shirts span {
    font-size: 300%;
    text-transform: none; /*delete this if you want your team name to be capital letters*/
    text-shadow: 0px 0px 10px white;  /*white light, delete it or write 'black' if you prefer the shadow*/ 
}
.logo {
    margin-right: 19px; /*maximux spacing on the right*/
    margin-top: 50px; /*increase it if you want it even lower, decrease it if you want it higher on your screen*/
}

/*trainer search*/
input.inputText, select.inputText {
    width: 255px;
}
}
