css:
@-moz-document url("http://www.reddit.com/r/RWBY"), url("https://www.reddit.com/r/RWBY"), url("http://www.reddit.com/r/rwby"), url("https://www.reddit.com/r/rwby"), regexp("((https)||(http)):/(\\S+?)/r/RWBY(/.*)?$"), regexp("((https)||(http)):/(\\S+?)/r/rwby(/.*)?$") {

  
  /*Grimm Anon Stuff*//*
  .commentarea .author.author:before { content: unset!important;}
  .flair:hover {pointer-events: none!important;font-size:0px;}
  */
  
  
  /*Non Anon Stuff*/
  .body  {background-color:#484547;}
  .content {background:#636363}
  .listing-page .linklisting .thing:hover {border-left:8px #B7171E solid}
  .subscribers:after { content: 'Infected';}
  .users-online:after { content: 'Creating Havoc';}
  .res-nightmode .morelink:hover{background:#8D2A30!important;}
  .res-nightmode .morelink {background:#A83E43!important;}
  .res-nightmode .morelink:hover, .morelink:hover {background:#8D2A30; }
  .res-nightmode .morelink, .morelink {background:#A83E43;}
  a[href="#s"]:after {background: orange; color:orange;}
  
  
  
  
  /*Annoying side buttons*/
  
  
  
  .side a[href*="#fbc"], .side  a[href*="#fbr"]{background:#A4121A;}
  .side a[href*="#fbc"]:hover, .side  a[href*="#fbr"]:hover {background-color:#932027;}
  .side .md a[href*="#disct"] {background:#A4121A;}
  .side .md a[href*="#disct"]:hover {background-color:#932027;}
  .side .md a[href$="#rules"] {background:#A4121A;}
  .side .md a[href$="#rules"]:hover {background-color: #932027;}
  
  
  
  
  /*Image Replacements*/
  
  .flair-mod1-salem1:before {background-position: -300px -150px;}
  .listing-page #header:before, .wiki-page #header:before, .submit-page #header:before{content:'';background:url(http://i.imgur.com/KyoExVc.png);}
  .flair[class*="flair-mod1-"]:before{background-image:url(http://i.imgur.com/XDWMqs5.png);}
  .side a[href="http://roosterteeth.com/archive/?sid=rwby&v=more"] {background:url(http://i.imgur.com/lFqWax2.png);}
  .commentarea > .usertext textarea, .commentarea .child .usertext textarea {background-image: url(http://i.imgur.com/3pn59PI.png);}
  html:lang(aa) .comments-page #header:before, .comments-page #header:before { content:''; background:transparent url(http://i.imgur.com/IohuQXC.png);}
  .comments-page .side #search {top:146px;}
	/*Add the Picture inside the search area*/
	#search input[type="text"]{
		background:#fff url(http://i.imgur.com/d3LcV5W.png) right no-repeat;
	}

  
  #baconBit {     
    position: fixed !important; 
    width: 1058px !important; 
    height: 1058px !important; 
    background-image: url(http://i.imgur.com/H5PFNHt.png) !important;
    -webkit-transition: all 7s linear !important;
    -moz-transition: all 7s linear !important;
    -o-transition: all 7s linear !important;
    -ms-transition: all 7s linear !important;
}
  
.thumbnail.spoiler{
    background: url(http://i.imgur.com/QK5hKKC.png);}
.thumbnail.default{
    background: url(http://i.imgur.com/QK5hKKC.png);}
.thumbnail.self{
    background: url(http://i.imgur.com/QK5hKKC.png);}
.linkflair-rwby-SPOILERS .thumbnail {background: url(http://i.imgur.com/QK5hKKC.png);background-position:0px -140px;height: 70px; }
.linkflair-rwby-SPOILERS-FANART .thumbnail {background: url(http://i.imgur.com/QK5hKKC.png);background-position:0px -140px;height: 70px; }
.linkflair-rwby-SPOILERS-DISCUSSION .thumbnail {background: url(http://i.imgur.com/QK5hKKC.png);background-position:0px -140px;height: 70px; }
.linkflair-rwby-SPOILERS-COSPLAY .thumbnail {background: url(http://i.imgur.com/QK5hKKC.png);background-position:0px -140px;height: 70px; }
.linkflair-rwby-SPOILERS-RWBYSWAG .thumbnail {background: url(http://i.imgur.com/QK5hKKC.png);background-position:0px -140px;height: 70px; }
.linkflair-rwby-SPOILERS-REQUEST .thumbnail {background: url(http://i.imgur.com/QK5hKKC.png);background-position:0px -140px;height: 70px; }
.linkflair-rwby-SPOILERS-FANFIC .thumbnail {background: url(http://i.imgur.com/QK5hKKC.png);background-position:0px -140px;height: 70px; }
.linkflair-rwby-SPOILERS-OFFICIALLINK .thumbnail {background: url(http://i.imgur.com/QK5hKKC.png);background-position:0px -210px;height: 70px; }
.linkflair-rwby-GRIMMECLIPSEDISCUSSION .thumbnail {background: url(http://i.imgur.com/QK5hKKC.png);background-position:0px -280px;height: 70px; }
    
  /*Stuff at the bottom*/
  

.arrow.down:hover:before {
     position: absolute;
     display: block;
     z-index: 1000;
     width: auto;
     padding: 5px;
     border: 0px solid #333;
     background: #1161d0;
     content: " Rawr..";
     text-align: center;
     font-size: 10px;
     color: white;
     margin-left: 25px;
     margin-top: 5px;
     -moz-border-radius: 4px;
     border-radius: 4px;
     -webkit-border-radius: 4px
}

.arrow.up:hover:after {
     position: absolute;
     display: block;
     z-index: 1000;
     width: auto;
     padding: 5px;
     border: 0px solid #333;
     background: #8e001c;
     content: " Rawr!";
     text-align: center;
     font-size: 10px;
     color: white;
     margin-left: 25px;
     margin-top: 5px;
     -moz-border-radius: 4px;
     border-radius: 4px;
     -webkit-border-radius: 4px
}
}
