css:
@-moz-document domain('forums.t4cbrumes.fr') {
#rt-content-top {
    background: url("http://demo.phpbb3styles.net/styles/WoW5thAniversary/theme/images/parchment-top.jpg") top center no-repeat;
    background-size: 100% auto;
}
#rt-main-surround {
    background: url("http://demo.phpbb3styles.net/styles/WoW5thAniversary/theme/images/parchment-bg.jpg") top center repeat-y;
    background-size: 100% auto;
}
#wrap .default li.row.bg2 {
    background: none;
}
#wrap .default li.row {
    border-bottom: none;
    border-top: none;
}
#rt-content-top + div {
    padding-left: 7%;
}
#wrap li.header {
    background: #301605 url("http://demo.phpbb3styles.net/styles/WoW5thAniversary/theme/images/tableheader.jpg") no-repeat;
    max-height: 35px;
    padding-top: 5px;
    padding-bottom: 5px;
    border-radius: 20px;
}
#wrap li.header dl,
#wrap li.header dd {
    padding: 0;
}
#wrap .default .module-title h2 {
    margin-top: 3px;
}
h1.title {
    margin-bottom: 0px;
    margin-top: 0px;
}
ul .linklist {
    margin-top: 0px;
    margin-bottom: 0px;
}
.module-title h2.title {
    display: none;
}
#rt-content-top div.rt-block {
    margin-top: 100px;
}
@media only screen and (max-width: 1250px) {
    .fa-building-o, .fa-file-text-o, .fa-comments-o, .fa-clock-o, .fa-clock-o:before, .fa-building-o:before, .fa-file-text-o:before, .fa-comments-o:before {
        display: none;
    }
    #rt-footer {
        display: none;
    }
    #rt-header-surround li.item1 a {
        display: none;
    }
}
.module-title {
    width: 95%;
}
#wrap a.forumtitle,
#wrap a.topictitle {
    color: #a24c1e;
}
body {
    color: #573325;
}
#rt-main-surround {
    color: #573325;
}
#wrap .postdefault {
    border: 1px solid #a24c1e;
    border-radius: 10px;
}
.post-bg {
    margin-right: 40px;
}
.pagination {
    margin-right: 40px;
}
#jumpbox {
    margin-right: 40px;
}
.navbar {
    margin-right: 40px;
}
.rt-9-cols .rt-grid-3 {
    flex-basis: 100%;
}
.rt-block {
    padding-bottom: 0px;
}
a {
    color: #a24c1e;
}
#wrap .postprofile {
    border-left-color: #CCBBB2;
}
.button,
.readon,
.readon2,
.readon3,
.readon4,
a.readmore,
button.validate,
p.readmore a,
#member-profile a,
#member-registration a,
.formelm-buttons button,
#form-login-submit .btn,
.login .controls .btn,
.logout-button .btn,
.sprocket-readmore,
.roksearch_results a.clr,
#gantry-totop {
    background-color: #a24c1e !important;
    background-image: linear-gradient(#a24c1e, #401B05);
}
.button:hover,
.readon:hover,
.readon2:hover,
.readon3:hover,
.readon4:hover,
a.readmore:hover,
button.validate:hover,
p.readmore a:hover,
#member-profile a:hover,
#member-registration a:hover,
.formelm-buttons button:hover,
#form-login-submit .btn:hover,
.login .controls .btn:hover,
.logout-button .btn:hover,
.sprocket-readmore:hover,
.roksearch_results a.clr:hover,
#gantry-totop:hover {
    background-color: #a24c1e !important;
    background-image: linear-gradient(#a24c1e, #401B05);
}
#rt-footer {
    background: url("http://demo.phpbb3styles.net/styles/WoW5thAniversary/theme/images/page-footer.jpg") top center no-repeat;
    background-size: 100% auto;
    min-height: 250px;
}
#rt-footer div div.rt-grid-3 ~ div.rt-grid-3 {
    position: fixed;
    bottom: 50px;
    left: 0;
    width: 450px;
}
#rt-footer div div.rt-grid-3 ~ div.rt-grid-3 div.rt-block {
    padding-left: 0px;
}
div.fp-sidepanel-03 {
    display: none;
}
div#rt-sidepanel-content div.rt-block {
    padding-top: 0px;
    padding-bottom: 0px;
}
#rt-sidepanel-content {
    background: url("http://t4cbrumes.fr/wp-content/uploads/2016/03/site_logo.png") top left no-repeat;
    background-attachment: fixed;
}
#rt-sidebar-a div.rt-block:nth-child(2) div.module-surround div:nth-child(2) {
    display: none;
}
#rt-sidebar-a div.module-title {
    display: none;
}
#rt-footer div div.rt-grid-3 {
    padding-top: 60px;
}
#rt-sidebar-a {
    padding-top: 140px;
}
#rt-header-surround {
    visibility: hidden;
    height: 0px;
}
#rt-copyright {
    display: none;
}
#rt-header-surround li.item1 a {
    font-size: 250%;
    position: fixed;
    top: 30px;
    left: 50px;
    width: 150px;
    height: 100px;
    visibility: visible;
    line-height: 20px;
    color: transparent;
    text-decoration: none;
}
#rt-header-surround li.item1 a span {
    visibility: hidden;
}
#rt-sidepanel-content{
    border-right: 2px solid #301605;
}

.module-content ul.menu li a span, .module-content ul.menu li .separator, .module-content ul.menu li .item span {
    color: #A06E53
}

#wrap .default .forum-icon, #wrap .default .topic-icon {
    color: #A06E53;
}

blockquote {
    border-left: 5px solid #964515;
}

a.button1,
input.button1,
input.button3,
a.button2,
input.button2,
#wrap .pagination span strong {
    background-color: #a24c1e !important;
    background-image: linear-gradient(#a24c1e, #401B05);

}

#wrap .post:target .content {
    color: #6B2702;
}

}
