/*AGENT_SHEET*/
@-moz-document url(chrome://global/content/customizeToolbar.xul) { 
/*_______________________________________________________________________________________________
 |_|___________________________________________________________________________________________|_|
 | |                                                                                           | |
 | | 'CUSTOMIZE TOOLBAR' WINDOW FOR PALEMOON / PRE-AUSTRALIS BUILDS OF FIREFOX - DARK VERSION  | |
 | |     _________________________________________________________________________________     | |
 | |                                                                                           | | 
 | |      With min-width, 500px will show 4 columns flush with scrollbar, 618px 5 columns      | |
 |_|___________________________________________________________________________________________|_|
 |_|___________________________________________________________________________________________|_|*/

#CustomizeToolbarWindow #main-box {
    margin: -6px !important;
    min-width: 500px ;
    background: url("http://imageham.com/images/2016/03/29/specofdust.png") repeat scroll,-moz-linear-gradient(rgb(125,125,125) 0%,rgb(70,70,70) 5%,rgb(60,60,60) 95%,rgb(20,20,20) 99%) fixed !important 
    }
#CustomizeToolbarWindow #palette-box {
    -moz-appearance: none !important; 
    margin: 5px !important;
    padding: 1px 0 0 1px; 
    border: 1px inset rgba(0,0,0,.5) !important; 
    background: url("http://imageham.com/images/2016/03/29/specofdust.png") repeat scroll rgb(75,75,75) !important;
    box-shadow: 1px 1px 2px rgba(20,20,20,.6) inset,-1px -1px 2px rgba(20,20,20,.6) inset !important 
    }
#modelist,
#donebutton,
#newtoolbar,
#restoreDefault { 
    -moz-appearance: none !important;
    margin-bottom: 5px !important; 
    color: rgb(160,160,160) !important; 
    border-radius: 0px !important;
    border: 1px solid rgb(35,35,35) !important;
    background: -moz-linear-gradient(rgb(125,125,125) 1%, rgb(75,75,75) 12%, rgb(30,30,30) 90%, rgb(10,10,10) 98%) !important; 
    }
#modelist:hover,
#donebutton:hover,
#newtoolbar:hover,
#restoreDefault:hover { 
    color: rgb(210,210,210) !important;
    background: -moz-linear-gradient(rgb(150,150,150) 1%, rgb(90,90,90) 12%, rgb(35,35,35) 90%, rgb(10,10,10) 98%) !important; 
    }
#newtoolbar > hbox > label,
#restoreDefault > hbox > label {
    padding-top: 1px !important; 
    } 
#newtoolbar { 
    margin-left: -6px !important;
    margin-right: 2px !important;
    }
#modelist { 
    padding: 2px 0px 3px 0px !important;
    margin-bottom: 6px !important;
    width: 101px !important 
    }
#modelist > hbox > label { 
    margin-left: 2px !important;
    margin-bottom: -2px !important;
    }
#donebutton { 
    margin-top: -31px !important;
    margin-right: 5px !important;
    padding-top: 1px !important 
    }
#smallicons { 
    margin-bottom: 6px !important;
    margin-left: -3px !important 
    }
#smallicons:not(:hover) .checkbox-check { 
    opacity: .8 
    }
#smallicons > hbox > label {
    display:none !important
    }
#smallicons > hbox:after {
    content: 'Small Icons' ; 
    display: -moz-box;
    color: rgb(160,160,160)  ;
    margin-left: -2px ;
    margin-right: 2px 
    }
#smallicons:hover > hbox:after {
    color: rgb(210,210,210);
    }
#CustomizeToolbarWindow  #main-box .groove,
#main-box > box > label {
    display: none !important 
    }
#CustomizeToolbarWindow #instructions { 
    color: rgb(180,180,180) !important;
    margin-bottom: -2px !important;
    padding-top: 4px !important;
    text-shadow: 0px 1px 0px rgba(0,0,0,.2),1px 2px 1px rgba(0,0,0,.3) !important;
    text-align: center !important;
    font: bold 13px verdana !important;  
    }
#wrapper-spacer > hbox,
#wrapper-spring > hbox { 
     -moz-appearance: none !important;
    border: 1px solid rgba(20,20,20,.7) !important;
    background-color: rgba(200,200,200,.4) !important
    }
#CustomizeToolbarWindow #palette-box toolbarpaletteitem[place="palette"] {
    border: 1px solid !important; 
    border-color: rgba(20,20,20,.5) rgba(255,255,255,.1) rgba(255,255,255,.1) rgba(20,20,20,.5) !important;
    color: rgb(210,210,210) !important;
    margin: 4px 4px 0px 4px !important; 
    box-shadow: 1px 1px 1px rgba(20,20,20,.3) inset,-1px -1px 1px rgba(20,20,20,.25) inset !important;
    }
#CustomizeToolbarWindow #palette-box toolbarpaletteitem[place="palette"]:hover:not(:active) {
    background-color: rgba(250,250,250,.07) !important;
    border: 1px solid !important; 
    border-color: rgba(20,20,20,.6) rgba(255,255,255,.1) rgba(255,255,255,.1) rgba(20,20,20,.6) !important;
    box-shadow: 1px 1px 2px rgba(20,20,20,.5) inset, -1px -1px 2px rgba(20,20,20,.5) inset !important 
    }
#CustomizeToolbarWindow #palette-box toolbarpaletteitem[place="palette"]:hover:active {
    border:none !important;
    box-shadow: none !important;
    } 
.tooltip-label { 
    min-width: 5% !important; 
    max-width: 100% !important;
    text-align: center !important;
    word-wrap: break-word !important; 
    }
tooltip {  
    padding: 2px 8px !important; 
    margin: 12px 0 0 -4px !important;
    -moz-appearance: none !important; 
    color: rgb(230,230,230) !important;
    background: rgba(0,0,0,.4) !important;
    font: bold 13px "trebuchet ms" !important;
    border: 1px solid rgba(0,0,0,.4) !important;
    text-shadow: 1px 1px 2px rgba(0,0,0,.7), 1px 1px 1px rgba(0,0,0,.5), -1px -1px 2px rgba(0,0,0,.7) ;
    box-shadow: 1px 1px 3px rgba(255,255,255,.5)inset, -1px -1px 3px rgba(255,255,255,.3)inset !important  
    }
menupopup {
    box-shadow: none !important;
    -moz-appearance: none !important;
    border-radius: 0px !important;
    color: #a0a0a0 !important;
    border: none !important;
    padding: .4em 0 !important;
    background: -moz-linear-gradient(#7d7d7d 1%,#4b4b4b 3%,#323232 97%,#141414 99.5%) !important;
    text-shadow: 0px 1px 0px rgba(0,0,0,.15), 0px -1px 0px rgba(0,0,0,.3), 2px 2px 1px rgba(200,200,200,.2) !important; 
    }
menupopup .menu-text,
menupopup .menu-iconic-text {
    font: normal 14px candara !important;
    color: #c8c8c8 !important; 
    }
menuitem {
    -moz-appearance: none !important;
    box-shadow: none !important 
    }
menuitem[selected="true"]:not(hover) {
    background: transparent !important; 
    }
menuitem:hover,
menuitem[selected="true"]:hover { 
    background: rgba(0,0,0,.4) !important 
    }
scrollbar { 
    margin-left: -1px !important
    }
scrollbar > slider > thumb,
scrollbarbutton[sbattr="scrollbar-up-top"],
scrollbarbutton[sbattr="scrollbar-down-bottom"] {
    border-radius: 0px !important;
    display: -moz-box !important;
    -moz-appearance: none !important;
    border: 1px outset #000 !important;
    background-color: #464646 !important;
    box-shadow: inset 1px 1px 6px 0px #191919, inset -1px -1px 6px 0px #b2b2b2 !important; 
    }
scrollbar, scrollbar > slider {
    -moz-appearance: none !important;
    background: transparent !important; 
    } 
scrollbar > slider > thumb:-moz-any(:hover, :active),
scrollbarbutton:-moz-any(:hover, :active)[sbattr="scrollbar-up-top"],
scrollbarbutton:-moz-any(:hover, :active)[sbattr="scrollbar-down-bottom"] {
    background-color: #a0a0a0 !important; 
    }
scrollbar:not(:hover) scrollbarbutton { 
    opacity: 0 !important 
    }

}
