css:
@-moz-document domain("adventuregamers.com") {
body,
.bodytext,
p {
	font-family: 'Merriweather', Georgia, 'Times New Roman', Times, serif;
	-moz-osx-font-smoothing: grayscale;
	-webkit-font-smoothing: antialiased !important;
	-moz-font-smoothing: antialiased !important;
	text-rendering: optimizelegibility !important;
	text-align: justify;
	text-justify: inter-word;
}
.bodytext,
.bodytext,
p {
	word-spacing: .2em;
	font-size: 1.2em;
	line-height: 1.2em;
}
.bodytext p {
	margin-bottom: 1.5em;
}
.cinn_top,
.cinn {
	width: 90% !important;
	margin: 0 auto;
}
.cleft {
	width: calc(100% - 330px) !important;
	padding: 0 10px;
}
.cleft,
.cright {
	margin: 5px 0 !important;
}
.cleft_inn {
	width: 95% !important;
	padding: 0;
	margin: 30px;
}
.bodytext .item_header {
	margin-top: 0.5em;
}
.bodytext .item {
	padding: 10px;
	margin: 5px 5px 20px 5px;
	border: 1px solid #eee;
}
.bodytext .item:hover {
	background-color: #eee;
}
.bodytext .item img {
	width: 100%;
	max-width: 520px;
	height: auto;
}
.heading_bg {
	background-image: url("http://www.adventuregamers.com/images/_");
	background-color: #D7DBCF;
	border: 1px solid #aaa;
}
/*  HIDE */

.cright br:first-of-type,
.hd_bot,
.bodytext br {
	display: none!important;
}
.comment {
	padding: 5px 0px;
}
.comment_avatar {
	width: 100px;
	float: left;
	padding: 5px 0px;
}
.comment_content {
	padding: 5px 0px;
	margin-left: 120px;
	display: inline;
	float: none;
}
}
