css:
@-moz-document url-prefix("http://www.byfandom.org/") {
/*header nonsense*/
.ow_header_pic {display:none;}
.ow_site_panel {background-color:rgba(54,208,174,0.1);position:relative;}
.ow_page_wrap {padding-top:0px!important;}

/*blog tags*/
.ow_remark li {white-space: normal;margin-left:0;}
.ow_remark li a {font-size:14px;display: inline !important; padding-right:0px;}
.ow_ic_write, .ow_remark li a {text-transform:none;}

.ow_ipc_toolbar .ow_delimiter {display:none!important;}
.ow_remark .ow_ic_user {padding-right:40px;}
.ow_remark .ow_nowrap:last-child{display:block;padding-top:5px;max-width:620px;}
.ow_remark .ow_nowrap .ow_wrap_normal a {font-size:13px;padding-left:5px;}

.ow_supernarrow .ow_tags_cont a:after {content:','}

/*no bullet points*/
ul.ow_regular {list-style:none;}

/*hide rating stuff*/
.ow_supernarrow .ow_box_cap:nth-child(5), .ow_supernarrow .ow_box:nth-child(6), .ow_fw_btns [list-type="toprated"], .ow_rates_wrap, div[id^='rate_'], .ow_content_menu ._top-rated {display:none!important;}

/*blog post navigation*/
.ow_superwide .ow_box_empty:first-child+.ow_box {border:none;padding-top:10px;background:transparent;margin-bottom:15px;}
.ow_superwide .ow_box_empty:first-child+.ow_box center {margin-top:-22px;}

/*quoted text*/
.ow_quote_cont,.ow_quote_cont_wrap {background-image:none!important;}
.ow_quote_cont_wrap {border-bottom:none;border-right:none;border-left:2px solid #ddd;margin-top:5px;}
.ow_quote_cont {padding-top:0;padding-bottom:0;padding-left:15px;}
.ow_quote {margin-left:7px!important;}
.ow_quote_header {background-color:transparent;}
.ow_quote_header .ow_author {color:transparent;}
.ow_quote_header .ow_author b {color:#36d0ae;margin-left:-87px;}
.ow_quote_header .ow_author:after{content:':';color:#000;}

/*shrink customize option*/
.ow_content .customize_box {width:200px;float:right;margin-top:-60px;margin-bottom:0;}


}
