css:
@-moz-document domain("mashable.com") {
.box-cell.sidebar {
    display: none;
}
.follow-channel {
    display: none;
}
.posts.show article.post.full header.article-header .shares {
    display: none;
}
.posts.show article.post.full .microcontent-wrapper .microcontent-shares {
    display: none;
}
.posts.show article.post.full .microcontent-wrapper:before {
    display: none;
}
.posts.show article.post.full .article-info .byline:not(.basic) {
    display: none;
}
.posts.show article.post.full footer {
    display: none;
}
.main-menu>li.follow,
.main-menu>li.user-submenu,
.main-menu>li.more,
.main-menu>#sign-in {
    display: none;
}
#highlights-section {
    display: none;
}
article.post.short footer {
    display: none;
}
.post-text {
    width: 200%;
    margin-left: -42%;
}
.posts.show article.post.full section.article-content.blueprint blockquote.pullquote {
    display: none;
}
.posts.show .article-content .popout {
    position: relative;
    width: 50%;
    margin-left: .5%;
}
.posts.show #more-in-channel {
    display: none;
}
#site-footer .social {
    display: none;
}
}
