css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sellaband.com") {
#menu-block {height:40px !important;}
#header-block {margin-top:3px !important;}
#main #header-block {margin-top:0px !important;}
.searchres td .grey {clear:left !important;float:left !important;padding-right:5px !important;}
.searchres td a[onclick] {float:right !important; margin-top:-1.5em !important;}
.searchres td br {display:inline !important; line-height:1px !important;}
.tabs .tab {padding:2px !important; margin:0 5px 0 0 !important; width:85px !important;}
#summary-wrapper td {padding: 0 5px 2px 0 !important;}
#summary-wrapper td.bold {width:45% !important;}
td {vertical-align:top !important; }
.friend_tmb {float: left !important; width:130px !important; margin-bottom:10px !important; clear:right !important; min-height:100px !important;}
.greyband h2 {font-size:1.5em !important;}
.my_playlist td {margin: 0 5px !important; white-space:nowrap;}
#believer_myfriends a img {height:auto !important; width:auto !important; max-height:90px; max-width:140px;}
#partners .white-back {background: inherit !important;}
#partners .bordered {border: 0px !important;}
#partners p {margin-bottom:10px !important;}
#footer-links {display:block; padding:10px 5px 10px 5px !important; position:fixed; left: -80px; top: 180px; width: 90px; background-color: #efefef; -moz-border-radius-topright: 20px; -moz-border-radius-bottomright: 20px;}
#footer-links:hover {left:0px;}
.wrapper div.newsitem {padding-left: 50px; }
.wrapper .news-item {min-height:60px !important; height: inherit !important;}
.wrapper div.newsitem .float-left {margin-left: -50px;}
div.expand[name="bulletin-expand"] {display:none;}
#bulletin-content {height:auto !important;}
}
