css:
@-moz-document domain("reddit.com") {
/*
 * THEME BY: AMOR KUMAR -- amorkumar.com -- https://github.com/itsamoreh/reddit-no-frills
 *
 * SIDEBAR FROM: Reddit Stilig Mod -- https://userstyles.org/styles/88599/reddit-stilig-mod
 *
 * COLORS:
 *
 * RED:       #ED6A5A;
 * ORANGE:    #F99654;
 * YELLOW:    #F7D56F;
 * GREEN:     #7ABC84;
 * BLUE:      #69C3EA;
 * PURPLE:    #9C80CC;
 *
 * WHITE:     #F9F9F9;
 * LIGHTGRAY: #F2F2F2;
 * GRAY:      #DDDDDD;
 * DARKGRAY:  #888888;
 * BLACK:     #222222;
 *
 */

body {
  background-color: #F9F9F9 !important;
  color: #30292F !important;
}

input,
textarea {
  border: 1px solid #69C3EA;
  color: #30292F;
  background-color: #F9F9F9 !important;
}

/*
 * HEADER
 *
 */

body.with-listing-chooser #header .pagename {
  position: relative;
  bottom: 0;
}

#header {
  margin: 0 auto;
  max-width: 900px !important;
  border-bottom: 1px solid #69C3EA;
  background-color: #F9F9F9;
}

#header-img.default-header {
  display: none !important;
}

#sr-header-area {
  margin-bottom: 22px;
  height: 22px;
  border-bottom: none;
  background-color: #F9F9F9;
}

.flat-list li a.gold {
  color: #F7D56F;
}

.tabmenu li a {
  background-color: #F9F9F9;
}

.tabmenu li.selected a {
  border-color: #69C3EA;
  border-bottom-color: #F9F9F9;
  background-color: #F9F9F9;
}

#header-bottom-right {
  background-color: #F9F9F9;
}

.user .userkarma {
  color: #F99654;
}

/*
 * SIDEBAR
 *
 */

.side {
  position: fixed !important;
  top: 30px !important;
  right: -400px !important;
  z-index: 1000 !important;
  float: right !important;
  overflow-y:auto !important;
  padding: 10px 20px 10px 10px !important;
  width: 300px !important;
  height:100% !important;
  border: 1px solid #69C3EA !important;
  background: #F9F9F9 !important;
  transition-duration:0.4s !important;
}

.side::before {
  position: fixed !important;
  top: -3px !important;
  right: 0% !important;
  z-index: 5;
  margin-right: 0px !important;
  padding-top: 8px !important;
  padding-right: 5px !important;
  padding-left: 0px !important;
  min-width: 30px !important;
  max-width: 30px !important;
  height: 25px !important;
  background: transparent!important;
  color: #888888;
  content: "â—¨";
  text-align: center !important;
  font-size: 14px !important;
  transition: 0.5s color ease;
}

.side:hover::before {
  background: #F9F9F9 !important;
  color: #888888;
  border-bottom: 1px solid #69C3EA !important;
  transition: 0.5s color ease;
}

.side:hover {
  position: fixed !important;
  right: -20px !important;
}

/* BUTTONS */
.morelink {
  background-image: none !important;
  border-color: #69C3EA;
  background-color: #F9F9F9;
}

.morelink .nub {
  display: none !important;
}

.morelink a {
  color: #69C3EA;
}

.morelink:hover,
.mlh {
  color: #F9F9F9;
  background-color: #69C3EA;
}

/* SIDEBOX CREATE BOX */
.sidebox.create .spacer {
  background: #F9F9F9;
}

/* SIDEBOX BOXES */
.sidecontentbox .content {
  border: 1px solid #69C3EA;
}

/* SIDEBOX OTHER */
.md hr {
  background: #69C3EA !important;
}

/*
 * SUBREDDIT DROPDOWN, BY DATE DROPDOWN
 *
 */

.drop-choices a.choice:hover {
  background-color: #F2F2F2 !important;
}

.drop-choices,
#sr-header-area .drop-choices.srdrop {
  border: 1px solid #69C3EA;
  background-color: #F9F9F9;
}

/*
 * TOP INFO BOXES
 *
 */

/* LIVE THREAD BOX */
.happening-now-wrap .rank-spacer, .happening-now-wrap .midcol-spacer {
  display: none;
}

.happening-now-wrap .midcol-spacer {
  display: none;
}

.happening-now-logo {
  display: none;
}

.happening-now {
  border: 1px solid #84CC8F;
  background-color: #F9F9F9;
  color: #7ABC84;
}

.happening-now .state::before {
  background: #ED6A5A;
}

/* INFOBAR */
.reddit-infobar {
  border: 1px solid #ED6A5A;
  background-color: #F9F9F9;
}

.reddit-infobar.with-icon {
  border: 1px solid #ED6A5A;
  background-color: #F9F9F9;
}

.archived-infobar.with-icon::before {
  display: none;
}

.reddit-infobar.with-icon {
  padding: 5px;
  min-height: 5px !important;
}

.reddit-infobar .md {
  margin: 0 auto;
  max-width: 100%;
  text-align: center;
}

.reddit-infobar .md p {
  margin: 0;
  color: #ED6A5A;
}

.gold-accent {
  border: 1px solid #F7D56F;
  border-radius: 0;
  background-color: #F9F9F9;
  color: #222222 !important;
}

.gold-accent h1, .gold-accent th {
  color: #222222 !important;
  font-family: normal x-small verdana,arial,helvetica,sans-serif;
}

.gold-accent .pretty-form input#name[type="text"] {
  border-radius: 0;
}

.gold-accent .pretty-form button {
  border: 1px solid #222222;
  border-radius: 0;
  background-color: #F9F9F9;
  color: #222222;
}

/*
 * CONTENT
 *
 */

.content {
  margin: 10px auto !important;
  padding: 5px 20px;
  max-width: 900px !important;
}

/*
 * POSTS
 *
 */

.link {
  margin: 20px auto !important;
  padding: 3px !important;
}

.midcol {
  margin-top: 0;
  margin-left: 0;
}

.link .rank {
  display: none;
}

.nsfw-stamp {
  color: #ED6A5A;
}

.md code, .md pre {
  border: 1px solid #F0F0F0;
  background-color: #F2F2F2;
}

.thing.stickied.link a.title {
  color: #7ABC84;
}

/*
 * LINKS
 *
 */

a {
  color: #69C3EA;
}

.thumbnail {
  margin-top: 5px;
}

.md a {
  color: #69C3EA;
}

#sr-header-area .selected a {
  color: #F99654;
}

.tabmenu li.selected a {
  color: #F99654;
}

.flat-list li.selected a {
  color: #F99654;
}

.thing .title {
  color: #69C3EA;
}

.thing .title:visited {
  color: #9C80CC;
}

.expando-button {
  float: right;
}

/*
 * USERNAME COLOR ON LINKS AND COMMENTS
 *
 */

.tagline .submitter,
.search-result-meta .submitter {
  color: #69C3EA;
  font-weight: 800;
}

.tagline a,
.search-result-meta a {
  color: #F99654;
}

/* OP USERNAME COLOR */
.author a,
.moderator a {
  background-color: #F9F9F9 !important;
  color: #7ABC84 !important;
}

/* ADMIN USERNAME */
.tagline .admin,
.search-result-meta .admin {
  color: #7ABC84;
  font-weight: 800;
}

/* MOD USERNAME */
.thing .tagline .author.moderator {
  background-color: #F9F9F9 !important;
  color: #7ABC84 !important;
}

.green,
.tagline .moderator,
.search-result-meta .moderator {
  color: #7ABC84 !important;
}

/*
 * COMMENT PAGE
 *
 */

.entry {
  padding-left: 5px;
}

.link .usertext-body .md {
  border-radius: 0;
}

.comment .expand {
  color: #222222;
}

.tagline .stickied-tagline,
.search-result-meta .stickied-tagline {
  color: #7ABC84;
}

.link .usertext-body .md {
  border: none;
}

.panestack-title {
  border-bottom: none;
}

.morecomments a {
  color: #69C3EA;
}

.panestack-title a.title-button.gold {
  border: 1px solid #F7D56F;
  border-radius: 0;
  background-color: #F9F9F9;
}

/*
 * MESSAGES
 *
 */

.menuarea {
  border-bottom: 1px solid #F2F2F2;
}

.message .tagline,
.message .search-result-meta {
  color: #888888;
}

.roundfield {
  background-color: #F2F2F2;
}

/*
 * SUBMIT PAGE
 *
 */

.tabmenu.formtab .selected a {
  background-color: #69C3EA !important;
}

.formtabs-content {
  border-top: 2px solid #69C3EA !important;;
}

/*
 * OTHER
 *
 */

.error {
  color: #ED6A5A;
}

.searchpane {
  margin: 5px auto;
  padding: 20px;
  border: 1px solid #69C3EA;
  background: #F9F9F9;
  background-image: none;
}

.raisedbox h4 {
  text-align: center;
}

#search input[type="text"] {
  width: 100%;
  border: 1px solid #69C3EA;
}

.infobar {
  margin: 0 auto;
  margin-bottom: 22px;
  border: 1px solid #F99654;
  background-color: #F9F9F9;
}

.nextprev a,
.next-suggestions a {
  background: #F9F9F9 !important;
}

.nextprev a:hover,
.next-suggestions a:hover {
  border: 1px solid #69C3EA !important;
}

/* SUBSCRIBE BUTTONS */
.fancy-toggle-button .add {
  background-color: #7ABC84;
  background-image: none;
}

/*
 * DISABLED FEATURES
 *
 */

/* MULTI HOVER */
.hover-bubble.multi-selector {
  display: none !important;
}

/* SIMPLIFY TOP BAR */
#sr-more-link,
.sr-list > span:nth-child(2),
ul.sr-bar:nth-child(3) {
  display: none;
}

/* TOP AD */
.organic-listing {
  display: none;
}

/* FOOTER */
.debuginfo,
.footer-parent {
  display: none;
}

/* MULTIREDDIT BAR */
body.with-listing-chooser.listing-chooser-collapsed .listing-chooser {
  display: none;
}

/*
 * RES FEATURES
 *
 */

/* HEADER */
#srList {
  border: 1px solid #69C3EA;
  background-color: #F9F9F9;
}

table#srList {
  border-collapse: separate !important;
}

#srList tr:hover {
  background-color: #F2F2F2 !important;
}

#sr-header-area a.RESShortcutsCurrentSub,
#RESSubredditGroupDropdown .RESShortcutsCurrentSub a {
  color: #F99654 !important;
}

#userbarToggle {
  border: none;
  background-color: #F9F9F9;
}

/* POSTS */
.NERPageMarker {
  background-color: #F2F2F2 !important;
}

#progressIndicator {
  background-color: #F2F2F2 !important;
}

/* COMMENT PAGE */
.RES-keyNav-activeElement,
.RES-keyNav-activeElement .md-container {
  background-color: #F2F2F2 !important;
}

.RESSubscriptionButton {
  border: 1px solid #69C3EA;
  background-color: #F9F9F9;
  color: #222222;
}

.RESSubscriptionButton .res-icon {
  display: none;
}

.newComments {
  color: #F99654;
}

/* POPUPS */
.RESHover,
.RESHover.RESDialogSmall {
  border: 1px solid #69C3EA;
  background-color: #F9F9F9;
}

.RESHoverTitle {
  background-color: #F9F9F9 !important;
}

/* RES DISABLED FEATURES */
#RESShortcutsEditContainer {
  display: none;
}
}
