css:
/*AGENT_SHEET*/
@-moz-document url(chrome://global/content/customizeToolbar.xul) { 
/*_______________________________________________________________________________________________
 |_|___________________________________________________________________________________________|_|
 | |                                                                                           | |
 | | 'CUSTOMIZE TOOLBAR' WINDOW FOR PALEMOON / PRE-AUSTRALIS BUILDS OF FIREFOX - LIGHT VERSION | |
 | |     _________________________________________________________________________________     | |
 | |                                                                                           | | 
 | |      With min-width, 500px will show 4 columns flush with scrollbar, 618px 5 columns      | |
 |_|___________________________________________________________________________________________|_|
 |_|___________________________________________________________________________________________|_|*/

#CustomizeToolbarWindow #main-box {
    min-width: 500px; 
    margin: -6px !important;
    background: url("http://imageham.com/images/2016/03/13/papert.png") repeat fixed,-moz-linear-gradient(to bottom,#c8c8c8,#bebebe)
    }
#CustomizeToolbarWindow #palette-box {
    -moz-appearance: none !important;
    margin: 5px !important;
    padding: 2px 0 0 2px; 
    border: 1px solid !important; 
    border-color: rgba(200,200,200,.6) #f0f0f0 #f0f0f0 rgba(200,200,200,.6) !important;
    box-shadow: 1px 1px 2px rgba(40,40,40,.6) inset, -1px -1px 2px rgba(40,40,40,.5) inset; 
    background: url("http://imageham.com/images/2016/03/13/papert.png") repeat fixed, rgb(250,250,250) 
    }
#modelist,
#donebutton,
#newtoolbar,
#restoreDefault { 
    border-radius: 0px;
    -moz-appearance: none !important;
    margin-bottom: 5px !important; 
    color: rgb(60,60,60) !important;
    border: 1px solid rgb(160,160,160) !important;
    background: -moz-linear-gradient(rgb(245,245,245) 0%,rgb(220,220,220) 15%,rgb(160,160,160) 95%,rgb(50,50,50) 100%) 
    }
#modelist:hover,
#donebutton:hover,
#newtoolbar:hover,
#restoreDefault:hover { 
    color: rgb(30,30,30) !important; 
    background: -moz-linear-gradient(rgb(255,255,255) 0%,rgb(240,240,240) 15%,rgb(185,185,185) 95%,rgb(100,100,100) 100%) 
    }
#newtoolbar > hbox > label,
#restoreDefault > hbox > label {
    padding-top: 1px !important; 
    } 
#newtoolbar { 
    margin-left: -6px !important;
    margin-right: 2px !important;
    }
#modelist { 
    padding: 2px 0px 3px 0px !important;
    margin-bottom: 6px !important;
    width: 101px !important 
    }
#modelist > hbox > label { 
    margin-left: 2px !important;
    margin-bottom: -2px !important;
    }
#donebutton { 
    margin-top: -31px !important;
    margin-right: 5px !important;
    padding-top: 1px !important 
    }
#smallicons { 
    margin-bottom: 6px !important;
    margin-left: -3px !important 
    }
#smallicons > hbox > label {
    display:none !important
    }
#smallicons > hbox:after {
    content: 'Small Icons'; 
    display: -moz-box;
    color: rgb(60,60,60);
    margin-left: -2px;
    margin-right: 2px 
    }
#smallicons:hover > hbox:after {
    color: rgb(30,30,30);
    }
#CustomizeToolbarWindow  #main-box .groove,
#main-box > box > label {
    display: none !important 
    }
#CustomizeToolbarWindow #instructions { 
    color: rgb(90,90,90) !important;
    margin-bottom: -2px !important;
    padding-top: 4px !important;
    text-align: center !important;
    font: bold 13px verdana !important;  
    }
#wrapper-spacer > hbox,
#wrapper-spring > hbox { 
     -moz-appearance: none !important;
    border: 1px solid rgba(20,20,20,.3) !important;
    background-color: rgba(255,255,255,.6) !important
    }
#CustomizeToolbarWindow #palette-box toolbarpaletteitem[place="palette"] {
    border: 1px solid !important; 
    border-color: rgba(255,255,255,.7) rgba(255,255,255,.75) rgba(255,255,255,.75) rgba(255,255,255,.75) !important;
    box-shadow: 1px 1px 2px rgba(20,20,20,.23) inset,-1px -1px 1px rgba(20,20,20,.2) inset !important;
    color: rgb(50,50,50) !important;
    margin: 4px 4px 0px 4px !important; 
    }
#CustomizeToolbarWindow #palette-box toolbarpaletteitem[place="palette"]:hover:not(:active) {
    background-color: rgba(0,0,0,.05) !important;
    border: 1px solid !important; 
    border-color: rgba(255,255,255,.8) #fff #fff rgba(255,255,255,.8) !important;
    box-shadow: 1px 1px 2px rgba(20,20,20,.2) inset, -1px -1px 2px rgba(20,20,20,.1) inset !important  
    }
#CustomizeToolbarWindow #palette-box toolbarpaletteitem[place="palette"]:hover:active {
    border:none !important;
    box-shadow: none !important;
    }
.tooltip-label { 
    min-width: 5% !important; 
    max-width: 100% !important;
    text-align: center !important;
    word-wrap: break-word !important; 
    }
tooltip {  
    padding: 2px 8px !important;
    margin: 12px 0 0 -4px !important;
    -moz-appearance: none !important; 
    color: rgb(50,50,50) !important;
    background: rgb(240,240,240) !important;
    border: 1px solid rgb(100,100,100) !important; 
    }
menupopup {
    box-shadow: none !important;
    -moz-appearance: none !important;
    border-radius: 0px !important;
    border: none !important;
    padding: .4em 0 !important;
    color: rgb(20,20,20) !important;
    background: -moz-linear-gradient(rgb(255,255,255) 0%,rgb(215,215,215) 6%,rgb(180,180,180) 94%,rgb(120,120,120) 99%) !important
    }
menupopup .menu-text,
menupopup .menu-iconic-text {
    font: normal 14px candara !important;
    color: rgb(20,20,20) !important; 
    }
menuitem {
    -moz-appearance: none !important;
    box-shadow: none !important 
    }
menuitem[selected="true"]:not(hover) {
    background: transparent !important; 
    }
menuitem:hover,
menuitem[selected="true"]:hover { 
    background: rgba(255,255,255,.65) !important 
    }
scrollbar { 
    margin-left: -2px !important 
    }
scrollbar > slider > thumb,
scrollbarbutton[sbattr="scrollbar-up-top"],
scrollbarbutton[sbattr="scrollbar-down-bottom"] {
    border-radius: 0px !important;
    display: -moz-box !important;
    -moz-appearance: none !important;    
    border: 1px solid !important; 
    border-color: rgba(200,200,200,.4) rgba(30,30,30,.55) rgba(30,30,30,.4) rgba(250,250,250,.7)  !important;
    background-color: rgb(234,234,234) !important;
    box-shadow: inset -1px -1px 6px 0px rgb(225,225,225),inset 1px 1px 7px 0px rgb(140,140,140) !important; 
    }
scrollbar, scrollbar > slider {
    -moz-appearance: none !important;
    background: transparent !important; 
    } 
scrollbar > slider > thumb:-moz-any(:hover, :active),
scrollbarbutton:-moz-any(:hover, :active)[sbattr="scrollbar-up-top"],
scrollbarbutton:-moz-any(:hover, :active)[sbattr="scrollbar-down-bottom"] {
    background-color: rgb(250,250,250) !important;
    }
scrollbar:not(:hover) scrollbarbutton { 
    opacity: 0 !important 
    }

}
