css:
@-moz-document domain("pastebin.com") {
    
/* custom colors */             /*var(--alt-bg)*/
:root {
--main-bg: #1d1e2a !important;              
--dark-bg: #1a1b26 !important;                
--darker-bg: #14151f !important;               
--header-bg: #1d1e2a !important;       
--grey-text: #999 !important;    
--yellow-text: #8a7b70 !important;     
--blue-text: #556b82 !important;       
--green-text: #74857c !important;       
}      

                               /* bg*/                               
   
  #main_frame, body, td:hover, tr:hover, input.search_input, input.search_input_li, textarea.paste_code, textarea.paste_textarea, .select2-container--default .select2-selection--single, input.post_input, select.post_select, .button1, .button2, input.button1, input[type="checkbox"] + label span, .jq-dropdown .jq-dropdown-menu li.dd_pa {
    background-color: var(--darker-bg) !important;
  }  
  
.jq-dropdown-menu > li, ol, .de1, .de2, #b_facebook2, #b_twitter2 {
    background: var(--darker-bg) !important;
  }  
  
                              /* content */  
  
  div#content_frame, div#content_right, .jq-dropdown-menu > li:hover, .jq-dropdown .jq-dropdown-menu li.dd_pa:hover {
    background: var(--main-bg) !important;
  }    
  
  #notice, input.search_input_shop, input.search_input_self {
     background-color: var(--dark-bg) !important;
  }  
  
  #header, #header_top, #code_buttons, .buttonsm {
    background: var(--dark-bg) !important;
  }  
  
  #notice, input.search_input_shop, input.search_input_self, .td_smaller, input.post_input, select.post_select, .button1, .button2, input.button1, #header, #header_user_status, .de1, .de2, body > pre, body {
    color: var(--grey-text) !important;
  }  
  
  .maintable td a, a, .content_title, .css .re0, .css .kw1 {
    color: var(--blue-text) !important;
  }  
  
  a:hover, .paste_box_info h1, .right_menu span, .paste_box_info, div.form_frame, .name_frm, #b_facebook2, #b_twitter2 {
    color: var(--green-text) !important;
  }    
  
                   /* remove borders */
  #monster_frame, .maintable th, .maintable td, #notice, input.search_input_shop, input.search_input_self, .paste_box_icon, .form_avatar, .right_menu li, #header, .header_icon, #main_frame, textarea.paste_code, textarea.paste_textarea, .form_login_frame, .select2-container--default .select2-selection--single, .content_title, input.post_input, select.post_select, .button1, .button2, input.button1, input[type="checkbox"] + label span, #code_frame2, #code_buttons, .de1, .de2, .buttonsm, fieldset.login_field {
    border: none !important;
  }  
  
                 /* shadow */
  #monster_frame, .paste_box_icon, .form_avatar, textarea.paste_code, textarea.paste_textarea, #header {
    box-shadow: 2px 2px 2px 3px rgba(0,0,0,0.1) !important;
  }
  
                /* remove ads */
  #abrpm > div:nth-child(1), #steadfast, .cd-top, .new_paste_button, #footer_right {
    display: none !important;
  } 
  
a, .td_smaller, .maintable td a, .de1, .de2, textarea.paste_code, textarea.paste_textarea, .text, body > pre {
  font-size: 14px !important;
  font-family: open sans !important;
  }
  
  img.i_p0, img.i_p00, img.i_p1, img.i_p10, .right_menu li, .hi_mypastebin, .hi_messages, .hi_alerts, .hi_settings, #header_logo, #header_dropdown, button.i_highlight, .i_pm, img.t_us, img.t_da, img.t_vi, img.t_ex {
    opacity: 0.6 !important;
  }  
  
  #header {
    height: 55px !important;
  }  
  
  div#content_frame {
    margin-top: 100px !important;
  }
  
  #header_links {
    opacity: 0 !important;
  }  
#header_links:hover {
    opacity: 0.8 !important;
  }    
  

  
  
  
  
  
  
  
  
  
  
}
