css:
@-moz-document domain("youtube.com") {
#creator-sidebar>#creator-sidebar-section-id-dashboard.selected>h3 a {
    background-color: #fff;
    color: #249824;
}
#creator-sidebar>.creator-sidebar-section.selected>h3 a {
    color: #249824;
}
#creator-sidebar>.creator-sidebar-section.selected:hover>h3 a {
    color: #11EA11;
}
#creator-sidebar .studio-icon.creator-sidebar-channel.selected, #creator-sidebar a:hover .studio-icon.creator-sidebar-channel.selected {
    background: no-repeat url(http://i.imgur.com/OaIKEU0.png) -22px -261px;
    background-size: auto;
    width: 24px;
    height: 24px;
}
#creator-sidebar .studio-icon.creator-sidebar-dashboard.selected, #creator-sidebar a:hover .studio-icon.creator-sidebar-dashboard.selected {
    background: no-repeat url(http://i.imgur.com/OaIKEU0.png) -18px -950px;
    background-size: auto;
    width: 24px;
    height: 24px;
}
#creator-sidebar .studio-icon.creator-sidebar-video-manager.selected, #creator-sidebar a:hover .studio-icon.creator-sidebar-video-manager.selected {
    background: no-repeat url(http://i.imgur.com/OaIKEU0.png) -20px -570px;
    background-size: auto;
    width: 24px;
    height: 24px;
}
#creator-sidebar .studio-icon.creator-sidebar-live-streaming.selected, #creator-sidebar a:hover .studio-icon.creator-sidebar-live-streaming.selected {
    background: no-repeat url(http://i.imgur.com/OaIKEU0.png) -21px -50px;
    background-size: auto;
    width: 24px;
    height: 24px;
}
#creator-sidebar .studio-icon.creator-sidebar-community.selected, #creator-sidebar a:hover .studio-icon.creator-sidebar-community.selected {
    background: no-repeat url(http://i.imgur.com/OaIKEU0.png) -22px -317px;
    background-size: auto;
    width: 24px;
    height: 24px;
}
#creator-sidebar .creator-sidebar-analytics.selected, #creator-sidebar a:hover .studio-icon.creator-sidebar-analytics.selected {
    background: no-repeat url(http://i.imgur.com/OaIKEU0.png) -22px -1288px;
    background-size: auto;
    width: 24px;
    height: 24px;
}
#creator-sidebar .studio-icon.creator-sidebar-create.selected, #creator-sidebar a:hover .studio-icon.creator-sidebar-create.selected {
    background: no-repeat url(http://i.imgur.com/OaIKEU0.png) 0 -766px;
    background-size: auto;
    width: 24px;
    height: 24px;
}
.html5-play-progress,
.ytp-play-progress {
    background: #249824 !important;
}
#creator-sidebar .creator-sidebar-item.selected, #creator-sidebar .creator-sidebar-submenu .creator-sidebar-item.selected a:hover, #creator-sidebar .creator-sidebar-submenu a.creator-sidebar-section-link.selected, #creator-sidebar .creator-sidebar-single-section.selected a, #creator-sidebar .creator-sidebar-single-section.selected a:hover {
    background-color: #249824;
    color: #fff;
}
#start-upload-button-single.upload-element-hover .init-upload-action-icon {
    background-image:url(http://i.imgur.com/yecJNjA.png)
}
#footer-logo .footer-logo-icon {
    vertical-align: middle;
    background: no-repeat url(http://i.imgur.com/PhrJSIG.png) -24px -600px;
    background-size: auto;
    width: 73px;
    height: 30px;
}
#upload-btn {
    margin-right: 15px;
    border: solid 1px black;
}
.yt-uix-button-subscribe-branded:before {
    background: no-repeat url(http://i.imgur.com/PhrJSIG.png) 0 -507px;
    background-size: auto;
    width: 16px;
    height: 12px;
}
.yt-uix-button-subscribe-branded, .yt-uix-button-subscribe-branded[disabled], .yt-uix-button-subscribe-branded[disabled]:hover, .yt-uix-button-subscribe-branded[disabled]:active, .yt-uix-button-subscribe-branded[disabled]:focus {
    color: #fefefe;
    background-color: #249824;
}
.html5-scrubber-button:hover,
.ytp-scrubber-button:hover,
.ytp-swatch-background-color,
.ytp-swatch-background-color-secondary {
    background: #249824 !important;
}
.ytp-volume-slider-track,
.ytp-volume-slider-handle:before {
    background: #249824 !important;
}
a.yt-uix-button-epic-nav-item:hover,
a.yt-uix-button-epic-nav-item.selected,
a.yt-uix-button-epic-nav-item.yt-uix-button-toggled {
    border-color: #31CC31;
}
.ytp-settings-button.ytp-hd-quality-badge::after,
.ytp-settings-button.ytp-4k-quality-badge::after,
.ytp-settings-button.ytp-5k-quality-badge::after,
.ytp-settings-button.ytp-8k-quality-badge::after,
.ytp-settings-button.ytp-3d-badge::after {
    background-color: #249824 !important;
}
.ytp-swatch-color {
    color: #249824 !important;
}
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
    background: #249824 !important;
}
.ytp-chrome-controls .ytp-button.ytp-youtube-button:hover:not([aria-disabled="true"]):not([disabled]) .ytp-svg-fill-logo-tube-lozenge {
    fill: #249824 !important;
}
#guide-container {
    width: 231px;
    padding: 13px 22px;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}
#yt-masthead #logo-container .logo {
    background: no-repeat url(http://i.imgur.com/PhrJSIG.png) -24px -600px;
    background-size: auto;
    width: 73px;
    height: 30px;
}
.guide-item.guide-item-selected,
.guide-item.guide-item-selected:hover,
.guide-item.guide-item-selected .yt-deemphasized-text,
.guide-item.guide-item-selected:hover .yt-deemphasized-text {
    background: #249824;
    color: #fff;
}
#yt-masthead-container {
    background: #767676;
    border-bottom: 1px solid #767676;
    min-width: 0;
    padding-bottom: 8px;
    padding-top: 7px;
    position: relative;
}
#masthead-search-terms input {
    background: #ccc;
    border: 0;
    display: inline-block;
    font-size: 16px;
    height: 100%;
    left: 0;
    margin: 0;
    outline: none;
    padding: 2px 6px;
    position: absolute;
    width: 100%;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}
a.yt-uix-button-epic-nav-item.partially-selected:hover,
button.yt-uix-button-epic-nav-item:hover,
button.yt-uix-button-epic-nav-item.selected,
button.yt-uix-button-epic-nav-item.yt-uix-button-toggled,
.epic-nav-item:hover,
.epic-nav-item.selected,
.epic-nav-item.yt-uix-button-toggled,
.epic-nav-item-heading {
    height: 29px;
    line-height: 29px;
    border-bottom: 3px solid;
    border-color: #249824;
    padding-bottom: 0;
    display: inline-block;
}
li.guide-section h3 a {
    width: 155px;
    color: #249824;
    overflow: hidden;
    white-space: nowrap;
    word-wrap: normal;
    -o-text-overflow: ellipsis;
    text-overflow: ellipsis;
    display: block;
}
.yt-uix-button {
    display: inline-block;
    height: 28px;
    border: 0;
    padding: 0 10px;
    outline: 0;
    font-weight: 500;
    font-size: 11px;
    text-decoration: none;
    white-space: nowrap;
    word-wrap: normal;
    line-height: normal;
    vertical-align: middle;
    cursor: pointer;
    border-radius: 2px;
    box-shadow: 0 1px 0 rgba(65, 230, 23, 0);
}
#yt-masthead .search-btn-component .yt-uix-button-content {
    margin: 0 0px;
}
#yt-masthead #logo-container span.content-region {
    color: #222;
    font-size: 11px;
    font-weight: normal;
    position: absolute;
    text-decoration: none;
}
.masthead-search-terms-border {
    border: 1px solid #222;
    box-shadow: inset 0 1px 2px #eee;
}
#masthead-search .search-btn-component,
#masthead-search .search-btn-component .start {
    -moz-border-radius: 25px;
    border-radius: 25px;
    border: solid 1px black;
    margin-left: 6px;
    width: 37px;
}
.guide-item .display-name {
    vertical-align: bottom;
    color: black;
}
#progress {
    background-color: #249824 !important;
    /*box-shadow: 0 0 0 0;*/
}
}
