css:
@-moz-document domain("vk.com") {
/*аватарка в фиде*/
.post_img {
    border-radius: 3px;
}
.as_group .post_field_group .post_field_user_image, .post_field_user .post_field_user_image{
    border-radius: 3px;
}
/*аватарка в списке групп*/
.group_row_photo, .group_row_img {
    border-radius: 3px;
}
/*аватарка в списке друзей*/
.friends_photo_img {
    border-radius: 3px;
}
.friends_photo_wrap .ui_zoom_inner {
    border-radius: 3px;
}
.ui_zoom_inner {
    border-radius: 3px;
}
/*аватарка в попапе детализации лайков*/
.like_tt_image, .poll_tt_image {
    border-radius: 3px;
}
/*аватарка в списке рекомендации друзей*/
.right_list_img {
    border-radius: 3px;
}
/*иконка в списке поиска друзей*/
.friends_import_icon, .friends_import_box_icon {
    border-radius: 3px;
}
/*аватарка в списке друзей на странице*/
.module_body .people_cell_img {
    border-radius: 3px;
}
/*аватарка в списке пабликов на странице*/
.page_list_module .thumb {
    border-radius: 3px;
}
.page_list_module .cell_img {
    border-radius: 3px;
}
/*аватарка в коментах на стене*/
.wall_module .reply_img {
	border-radius: 3px;
}
/*аватарка в фиговинке чатов в углу*/
.chat_tab_img {
    border-radius: 3px;
}
img.fc_contact_photo {
    border-radius: 3px;
}
.chat_tab_counter {
    border-radius: 0 0 8px;
}
/*аватарка в попапе пабликов*/
.fans_idolph_wrap {
    border-radius: 3px;
}
/*аватарка в шапке ленты*/
.post_field_user_image{
    border-radius: 3px;
}
/*аватарка в посте в котором рекламируют паблик*/
.page_group_image{
     border-radius: 3px;
}
/*аватарки в шаринге поста*/
.like_share_ava.wdd_imgs{
    border-radius: 3px;
}
.wddi_img {
    border-radius: 3px;
}
/*аватарка в сообщении о сообщении*/
.notifier_image {
     border-radius: 3px;
}
/*аватарка в попапе музыки*/
.ow_ava.ow_ava_small {
	border-radius: 3px;
}
/*аватарка в попапе фидбека*/
.top_notify_cont .feedback_img {
	border-radius: 3px;
}
.feedback_img {
    border-radius: 3px;
}
.feedback_group_photo_img {
	border-radius: 3px;
}
/*аватарка сбоку в списке разговоров*/
.nim-peer .nim-peer--photo-w {
     border-radius: 3px;
}
/*аватарка в старом дизайне чата*/
.im-prebody img {
    border-radius: 3px;
}
.nim-peer.nim-peer_small .im_grid>img{
    border-radius: 3px;
}
.nim-peer .nim-peer--photo .im_grid>img, .nim-peer .nim-peer--photo>img{
    border-radius: 3px;
}
.nim-peer.nim-peer_small .nim-peer--photo .im_grid>img, .nim-peer.nim-peer_small .nim-peer--photo>img {
     border-radius: 3px;
}
.nim-dialog.nim-dialog_classic .nim-dialog--photo .nim-peer .nim-peer--photo .im_grid>img, .nim-dialog.nim-dialog_classic .nim-dialog--photo .nim-peer .nim-peer--photo>img{
    border-radius: 3px;
}
.nim-peer.nim-peer_smaller .nim-peer--photo .im_grid>img, .nim-peer.nim-peer_smaller .nim-peer--photo>img{
    border-radius: 3px;
}
/*кнопку коментов в фиде налево*/
.post_full_like_wrap .reply_link_wrap {
    float: left;
}
/*кнопку лайков в фиде - направо*/
.post_full_like{
	float: right;
}
.wall_module .post_views{
float: left;
}
/*мне нравится у коммента*/
.like_wrap:before{
	content: "Мне нравится";
    white-space: nowrap;
    font-style: normal;
    color: transparent;
    float: left;
    padding-right: 5px;
}

.like_wrap:hover:before{
    color: inherit;
}
/*растягивание ленты в ширрину
.wide_column {
    width: 105%;
}
.wide_column_left .narrow_column_wrap {
    left: 5%;
}
*/
/*кнопка сворачивания коментов*/
.replies_side_wrap > div{
	display: block;
}
}
