css:
@-moz-document url("https://www.forumhouse.ru") {
.footer,
.footerLegal,
.videoAnnouncement,
#fh_yandex_share_thread,
.likesSummary,
.navbar-sponsor,
.thread_view .threadAlerts,
.rating.StarsContainer,
#printThreadLinkContainer,
#Notices,
#pageDescription,
.breadBoxBottom,
.hasAttachments,
.privateControls.fhModToolsInPost.nodeList,
.ratingControl.positive {
    display: none !important;
}
.pageWidth {
    max-width: 900px;
}
/*================= шапка ===================== */

.bootstrap #menu-v1-lvl1 {
    background-image: none !important;
    background-repeat: repeat-x;
    height: 40px;
    position: relative;
    background-color: #333;
}
/*================= лого ===================== */

.bootstrap #menu-v1-lvl1 .top-bar .logo {
    margin: 10px 0px 0px;
}
.bootstrap #menu-v1-lvl1 .menu-main {
    margin-top: 0px;
}
.bootstrap #menu-v1-lvl1 .top-bar .menu-right .navbar-nav > li.divider {
    margin-top: 5px;
}
.bootstrap #menu-v1-lvl1 .top-bar .menu-right .navbar-nav > li.navbar-search > a {
    margin: 5px 24px 0px 0px;
}
.bootstrap #menu-v1-lvl1 .top-bar .menu-right .navbar-nav > li.navbar-avatar > a {
    padding: 0px;
    margin: 5px 0px 14px 19px;
}
.bootstrap #menu-v1-lvl1 .menu-second {
    top: 25px;
}
#headerMover #headerProxy {
    height: 80px;
}
.bootstrap #menu-v1-lvl1 .top-bar .menu-right .navbar-nav > li.navbar-auth > a {
    margin: 8px 7px 0px 22px;
    padding: 0px;
}
.discussionListItem .posterDate {
    float: right;
}
.bootstrap #menu-v1-lvl2 .menu-mn {
    top: 5px;
}
.quickReply {
    top: 90px;
    margin-bottom: 90px;
}
.discussionListItem .stats .major {
    font-size: 8pt;
}
.discussionListItem .lastPostInfo .username {
    font-size: 8pt;
}
.discussionListItem .lastPostInfo dt a {
    color: #969696;
}
body .muted a {
    color: #202020;
}
.bootstrap #menu-v1-lvl2 {
    height: 30px;
}
.messageList,
.thread_view .messageList {
    background: #333;
}
#content .pageContent {
    background-color: #333;
    padding: 10px;
}
#XenForo body {
    overflow: hidden;
    background-color: #333;
}
.message .messageInfo {
    background-color: #333;
    padding: 10px;
    border: 0 none #ebebeb;
    border-top-color: #f1f1f1;
    border-bottom-color: #e3e3e3;
    zoom: 1;
}
.messageText {
    font-size: 11pt;
    font-family: Arial, sans-serif;
    line-height: 1.4;
    margin: 0 0.769em;
    color: #babdb6;
}
.primaryContent .fhModToolsInPost a,
.primaryContent .privateControls a,
.primaryContent .publicControls a,
.adsTopContainerLink a {
    color: #FB1818;
}
.messageText a,
.thread_view .messageText a {
    color: #35FFCC;
}
body .message .messageUserInfo,
body .thread_view .message .messageUserInfo {
    background: #333;
}
body {
    -webkit-text-size-adjust: 100%;
    -moz-text-size-adjust: 100%;
    -ms-text-size-adjust: 100%;
    font-family: Arial, sans-serif;
    color: #babdb6;
    word-wrap: break-word;
    line-height: 1.27;
}
a:link,
a:visited {
    color: #F3EF01;
    text-decoration: none;
    -ms-word-break: break-all;
    word-break: break-all;
    -webkit-hyphens: auto;
    -moz-hyphens: auto;
    hyphens: auto;
}
body .attachedFiles .attachmentList {
    background: #333;
    border: none;
}
body .thread_view .attachedFiles {
    border: 1px solid #d7d5d1;
    background: #333;
}
.attachment .boxModelFixer {
    overflow: hidden;
    zoom: 1;
    margin-bottom: 10px;
    margin-right: 10px;
    padding: 5px;
    background-color: #333;
    border: 1px solid #e1e1e1;
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    -khtml-border-radius: 5px;
    border-radius: 5px;
}
.bbCodeQuote .quoteContainer {
    background: #252523;
}
.bbCodeQuote .quoteContainer {
    overflow: hidden;
    position: relative;
    /* font-style: italic; */
    
    font-size: 10pt;
    font-style: normal;
    background: rgb(244, 246, 252);
    padding: 10px;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    -khtml-border-radius: 4px;
    border-radius: 4px;
}
.bbCodeQuote .quoteContainer {
    overflow: hidden;
    position: relative;
    /* font-style: italic; */
    
    font-size: 10pt;
    background: #020202;
    padding: 10px;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    -khtml-border-radius: 4px;
    border-radius: 4px;
}
.titleBar h1 {
    font-size: 14pt;
    color: #F3EF01;
    text-shadow: 0;
    overflow: hidden;
    zoom: 1;
}
}
