css:
/*! TekSyndicate Dark Forum Theme v1.0 05/03/2016 
  * GitHub: https://github.com/sarhatabaot/discourse-teksyndicate
  * https://userstyles.org/styles/125193
  * License: https://creativecommons.org/licenses/by-sa/4.0/
  */

html,
.topic-list > tbody > tr,
.topic-area {
    background-color: #FFFFFF !important;
}
body > div:first-child {
    padding: 0 !important;
}
#post_1 .topic-body,
#post_1 .topic-avatar {
    border-top: none;
}
.topic-list .avatar.latest:not(.single) {
    box-shadow: 0 0 4px 1px #000000;
}
.user-right .item.notification {
    background-color: #FFFFFF !important;
}
#main-outlet .container {
    background: none;
}
.topic-list th,
.topic-list td {
    padding: 18px 10px;
}
.show-more.has-topics {
    top: 2px;
}
.topic-list > tbody > tr:first-of-type,
.topic-list > tbody > tr {
    border-top: none;
    border-bottom: none;
}
.topic-map {
    background-color: #FFFFFF !important;
    border: none;
}
.topic-map section {
    border-top: none;
}
.docked .d-header {
    background-color: #FFFFFF !important;
    border-top: none;
    border-bottom: 2px solid #FFFFFF;
}
body .grippie {
    border-top: 1px solid #FFFFFF;
    background-color: #FFFFFF;
}
#reply-control {
    transition: height 0.35s ease-in-out;
    background-color: #FFFFFF;
}
#reply-control .d-editor {
    border: 1px solid #FFFFFF;
}
#reply-control .contents .d-editor-preview {
    border: 1px dashed #FFFFFF;
}
#reply-control .wmd-control .d-editor-input,
#reply-control .wmd-control .d-editor-preview,
#reply-control .wmd-control .d-editor-button-bar {
    background-color: #FFFFFF;
}
#reply-control .d-editor-button-bar {
    border-bottom: 2px solid #FFFFFF;
}
#reply-control .wmd-controls .d-editor-input,
#reply-control .wmd-controls .wmd-control .d-editor-preview,
#reply-control .wmd-control .wmd-controls .d-editor-preview,
#reply-control .wmd-controls .d-editor-button-bar,
#reply-control .wmd-controls .d-editor-preview,
#reply-control .d-editor-button-bar {
    background-color: #FFFFFF;
}
body input[type="text"],
body input[type="password"],
body input[type="datetime"],
body input[type="datetime-local"],
body input[type="date"],
body input[type="month"],
body input[type="time"],
body input[type="week"],
body input[type="number"],
body input[type="email"],
body input[type="url"],
body input[type="search"],
body input[type="tel"],
body input[type="color"] {
    background-color: #FFFFFF;
    border: 1px solid #000000;
    border-radius: 3px;
    box-shadow: inset 0 2px 2px rgba(0, 0, 0, 0.3);
}
body input[type="text"]:focus,
body input[type="password"]:focus,
body input[type="datetime"]:focus,
body input[type="datetime-local"]:focus,
body input[type="date"]:focus,
body input[type="month"]:focus,
body input[type="time"]:focus,
body input[type="week"]:focus,
body input[type="number"]:focus,
body input[type="email"]:focus,
body input[type="url"]:focus,
body input[type="search"]:focus,
body input[type="tel"]:focus,
body input[type="color"]:focus {
    border-color: #666666;
    outline: 0;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.3), 0 0 8px gray;
}
.select2-container {
    border: 1px solid gray;
}
.select2-container .select2-choice,
.select2-container .select-container-active,
.select2-container .select2-dropdown-open a.select2-choice,
.select2-dropdown-open .select2-container a.select2-choice {
    border: 1px solid gray;
}
.select2-container .select-container-active {
    box-shadow: gray 0px 0px 6px 0px;
}
.select-drop-active {
    border: 1px solid gray;
    border-top: 0;
}
.badge-category-parent-bg,
.badge-category-bg {
    width: 5px !important;
}
.alert.alert-info {
    background-color: #EFEFEF;
}
.menu-panel {
    background-color: #FFFFFF;
    border: 1px solid #000000;
}
.lightbox-wrapper .meta {
    background-color: #FFFFFF;
}
code[class*=lang-],
pre[class*=lang-] {
    text-shadow: 0 1px black;
    font-family: Consolas, "Courier New", Courier, monospace;
    text-align: left;
    direction: ltr;
    white-space: pre;
    word-spacing: normal;
    word-break: normal;
    line-height: 1.5;
    -moz-tab-size: 4;
    -o-tab-size: 4;
    tab-size: 4;
    -webkit-hyphens: none;
    -moz-hyphens: none;
    -ms-hyphens: none;
    hyphens: none;
}
pre[class*=lang-] {
    padding: 1em;
    margin: .5em 0;
    overflow: auto;
    border-radius: 0.3em;
}
:not(pre) > code[class*="lang-"],
code {
    padding: .1em;
    border-radius: .3em;
}
.hljs {
    display: block;
    overflow-x: auto;
    padding: 0.5em;
    background-color: #C1C1C2;
    color: #2F2F2F;
}
.hljs-keyword,
.hljs-selector-tag,
.hljs-tag {
    color: #e3ceab;
}
.hljs-template-tag {
    color: #505050;
}
.hljs-number {
    color: #8cd0d4;
}
.hljs-variable,
.hljs-template-variable,
.hljs-attribute {
    color: #efddbd;
}
.hljs-literal {
    color: #efefae;
}
.hljs-subst {
    color: #8f8f8f;
}
.hljs-title,
.hljs-name,
.hljs-selector-id,
.hljs-selector-class,
.hljs-section,
.hljs-type {
    color: #efef8f;
}
.hljs-symbol,
.hljs-bullet,
.hljs-link {
    color: #dca3a3;
}
.hljs-deletion,
.hljs-string,
.hljs-built_in,
.hljs-builtin-name {
    color: #cc9393;
}
.hljs-addition,
.hljs-comment,
.hljs-quote,
.hljs-meta {
    color: #7f9f7f;
}
.hljs-emphasis {
    font-style: italic;
}
.hljs-strong {
    font-weight: bold;
}
aside.quote .title {
    border-left: 5px solid #929292;
    background-color: #E1E1E1;
    color: #bababa;
    padding: 12px 12px 1px 12px;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none
}
.quote aside .quote,
.quote aside .title,
.quote aside blockquote,
.quote aside .onebox,
.quote aside .onebox-result {
    background: #E1E1E1;
    border-left: 5px solid #4c4c4c
}
blockquote {
    border-left: 5px solid #212121;
    background-color: #E1E1E1;
    clear: both
}
span.highlighted {
    background-color: #CBCBCB
}
/* Text Coloring Below */

.badge-notification {
    background-color: #DBDBDB;
}
.nav-pills > li > a {
    color: #000000;
}
.alert.alert-info {
    background-color: #DBDBDB;
    color: #000000;
}
.topic-list a.title {
    color: #000000;
}
.btn {
    border: none;
    color: #000000;
    font-weight: normal;
    background: #DBDBDB;
}
.list-controls .category-breadcrumb li.bullet > .badge-category {
    background: #DBDBDB !important;
    color: #000000 !important;
}
.topic-list .posters a:first-child .avatar.latest:not(.single) {
    box-shadow: 0 0 3px 1px #000000;
    border: 2px solid #000000;
    position: relative;
    top: -2px;
    left: -2px;
}
.topic-list .topic-excerpt {
    color: #000000;
}
.relative-date {
    color: #000000;
}
.topic-list td {
    color: #000000;
}
body .heatmap-low {
    color: #000000 !important;
}
.heatmap-low a {
    color: #000000 !important;
}
body .heatmap-med {
    color: #000000 !important;
}
body .heatmap-high {
    color: #000000 !important;
}
.badge-wrapper.bullet span.badge-category {
    color: #000000 !important;
    display: inline-block;
    overflow: hidden;
    text-overflow: ellipsis;
}
.badge-notification.new-posts,
.badge-notification.unread-posts {
    background-color: #DBDBDB;
    color: #000000;
    font-weight: bold;
}
.badge-posts[href] {
    color: #000000;
}
notification.new-topic {
    color: #000000;
}
.badge-notification.new-topic {
    color: #000000;
}
.topic-list .fa-thumb-tack {
    color: #000000;
}
.d-header .icons .icon {
    color: #000000;
    cursor: pointer;
}
#topic-title h1 a {
    color: #000000;
}
.names span a {
    color: #000000;
}
nav.post-controls .highlight-action {
    color: #000000;
}
html {
    color: #000000;
    font-family: Helvetica, Arial, sans-serif;
    font-size: 14px;
    line-height: 19px;
    direction: ltr;
}
nav.post-controls button {
    color: #000000;
    margin-right: 2px;
    display: inline-block;
}
.topic-list th {
    color: #000000;
    font-weight: normal;
    font-size: 1em;
}
.topic-map h4 {
    margin: 1px 0 2px 0;
    color: #000000;
    font-weight: normal;
    font-size: 0.857em;
    line-height: 15px;
}
.topic-map .map .number,
.topic-map .map i {
    color: #000000;
    font-size: 130%;
}
.user-main .about .details h1,
h2,
h3,
h3 {
    color: #000000;
}
.badge-notification.clicks {
    font-weight: normal;
    background-color: #DBDBDB;
    top: -1px;
    color: #000000;
    position: relative;
    margin-left: 2px;
    border: none;
}
.topic-map .avatars,
.topic-map .links,
.topic-map .information {
    color: #000000;
}
.names .new_user a,
.names .user-title,
.names .user-title a {
    color: #000000;
}
a.mention,
a.mention-group {
    padding: 2px 4px;
    color: #000000;
    background: #DBDBDB;
    border-radius: 8px;
    font-weight: bold;
    font-size: 0.93em;
}
.small-action .small-action-desc {
    padding: 0.5em 0 0.5em 4em;
    margin-top: 5px;
    text-transform: uppercase;
    font-weight: bold;
    font-size: 0.9em;
    color: #000000;
}
.small-action .topic-avatar i {
    font-size: 35px;
    width: 45px;
    text-align: center;
    color: #000000;
}
.topic-list a.title.visited:not(.badge-notification) {
    color: #000000;
}
.topic-body .reply-to-tab {
    z-index: 400;
    font-size: 0.929em;
    float: right;
    margin: 1px 25px 0 0;
    color: #000000;
}
nav.post-controls button.create {
    color: #000;
    background: #DBDBDB;
}
#topic-progress {
    color: #000000;
}
#topic-progress .bg {
    background-color: #DBDBDB;
}
.extra-info-wrapper .topic-link {
    color: #000000;
    display: block;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}
.extra-info-wrapper .badge-wrapper.bullet span.badge-category {
    color: #000000 !important;
}
.extra-info-wrapper .topic-statuses i {
    color: #000000;
}
aside.quote .title {
    color: #000000;
}
.quote-controls,
.quote-controls .back::before,
.quote-controls .quote-other-topic::before {
    color: #000000;
}
#topic-footer-buttons p {
    line-height: 32px;
    color: #000000;
}
a.reply-new {
    margin-top: 3px;
    color: #000000;
}
.names .fa {
    font-size: 11px;
    margin-left: 3px;
    color: #000000;
}
#reply-control .contents .d-editor-input,
#reply-control .contents .d-editor-preview {
    color: #000000;
}
.topic-meta-data .post-info a {
    color: #000000;
}
a {
    color: #000000;
    text-decoration: none;
    cursor: pointer;
}
