css:
::-webkit-scrollbar {height:5px; width:1em; border-radius:3px;}
::-webkit-scrollbar-track-piece {
    background-color:#191919;
    border:2px solid #191919
}
 
::-webkit-scrollbar-thumb {
    width: 1em; height:5px;
    background-color: #323232;
    box-shadow: 0 0 2px rgba(0,0,0,0.6);
    border-radius:3px;
    border:1px solid #A9A9A9;
}
 
::-webkit-scrollbar-button:end { 
height: 0.75em; width: 0.75em; 
border-top-left-radius: 3px;
border-top-right-radius: 3px; 
border:1px solid #A9A9A9; 
background-color:#323232;
border-bottom: 0px; }
 
::-webkit-scrollbar-button:start { 
height: 0.75em; width: 0.75em; 
border-bottom-left-radius: 3px;
border-bottom-right-radius: 3px; 
border:1px solid #A9A9A9;
border-top: 0px; 
background-color:#323232; }
 
::-webkit-scrollbar-corner {
height: 0.75em; width: 0.75em;
border:1px solid #A9A9A9;
background-color:#323232;
}
 
::-webkit-resizer {
height: 0.75em; width: 0.75em;
border:1px solid #A9A9A9;
background-color:#323232;
}
 
::-webkit-scrollbar-button:end:horizontal { 
height: 0.75em; width: 0.75em; 
border-bottom-left-radius: 3px;
border-top-left-radius: 3px; 
border:1px solid #A9A9A9; 
background-color:#323232;
border-bottom: 0px; }
 
::-webkit-scrollbar-button:start:horizontal { 
height: 0.75em; width: 0.75em; 
border-bottom-right-radius: 3px;
border-top-right-radius: 3px; 
border:1px solid #A9A9A9; 
background-color:#323232;
border-bottom: 0px; }
 
::-webkit-scrollbar-thumb:hover {
    background: #386be0;
}
::-webkit-scrollbar-button:start:horizontal:active {
    background: #153684 !important;
}
::-webkit-scrollbar-button:end:horizontal:active {
    background: #153684 !important;
}
::-webkit-scrollbar-thumb:active {
    background: #153684 !important;
}
