css:
@-moz-document url-prefix("https://discordapp.com/channels/") {
code {
    background: purple !important;
}
body {
    background: black;
}
body {
    font-family: "determination mono", cursive;
}
#twitchcord-button-container {
    position: fixed;
    right: 5px;
    bottom: 50px;
    z-index: 4;
    transform: rotate(180deg);
}
#twitchcord-button-container button {
    border-top-left-radius: 14px;
}
#twitchcord-button-container * {
    box-shadow: -2px 2px 5px rgba(0, 0, 0, 0.4);
}
div.title-wrap {
    background: black !important;
    transform: rotate(0deg);
}
.channel-textarea-inner,
textarea {
    background: black !important;
    transform: rotate(0deg);
}
.avatar-large {
    width: 100px;
    height: 100px;
    background-size: 100px 100px;
    border-radius: 0px;
}
.avatar-large {
    transform: rotate(0deg);
}
.user-name::before {
    content: ">>";
}
.user-name::after {
    content: ":";
}
.username {
    font-family: "determination mono";
}
.username {
    text-transform: lowercase;
}
.username {
    transform: rotate(10deg);
}
.member {
    transform: rotate(5deg);
    transition: 1s all;
}
.member:hover {
    transform: rotate(100deg);
    transition: 1s all;
}
.channel-members h2 {
    padding: 0px 10px;
    transform: rotate(-10deg)
}
body::after {
    font-size: 1vw;
    color: red;
    font-weight: bold;
    content: 'I WANNA KILL MYSELF';
    z-index: 999;
    left: 50%;
    top: 1%;
    transform: translate(-40%, -50%);
    display: block;
    pointer-events: none;
}
.guild-channels {
    transform: rotate(0deg);
    background: black !important;
    BORDER: 5PX SOLID white;
}
.guilds {
    transform: rotate(180deg);
    background: black !important;
    margin-left: -50px;
}
.channel-members {
    background: black;
    transform: rotate(0deg);
}
.links {
    transform: rotate(0deg);
    background: black;
}
.account {
    transform: rotate(0deg);
    background: black;
    BORDER: 5PX SOLID white;
}
.user-name {
    transform: rotate(0deg);
    background: black;
}
.help-container {
    transform: rotate(0deg);
    background: black !important;
}
.guild-header {
    background: black;
    margin-left: -96px;
}
.messages {
    transform: rotate(0deg);
}
.channel-textarea-upload {
    transform: rotate(0deg);
}
.divider-red {
    transform: rotate(0deg);
    background: black !important;
}
.active {
    background: black !important;
}
.guilds-wrapper {
    background: black !important;
}
.private-channels {
    background: black !important;
}
.message {
    transform: rotate(0deg);
    BORDER: 1PX SOLID white;
    background: black !important
}
.message::before {
    content: ">>print(name, message.content[6:]";
    color: blue;
    transform: rotate(0deg);
}
.content {
    background-image: url('https://i.imgur.com/F3WhxgV.jpg');
    background-size: cover;
    background-position: 0px;
}
.markup {
    color: red!important;
}
.markup::before {
    content: ">>";
}
.guilds-add {
    color: black;
}
.typing {
    background: black !important;
}
.friends-icon {
    background: black;
}
.image {
    border-radius: 15%;
}
.flex-spacer {
    transform: rotate(0deg);
}
.hljs {
    transform: rotate(0deg);
}
.avatar-small,.avatar-large,.hljs{
    animation-name: rotation;
    animation-duration: 4s;
    animation-iteration-count: infinite;
}
.member-username,.user-name{
    animation-name: updown;
    animation-duration: 4s;
    animation-iteration-count: infinite;
}
.member-game{
    animation-name: downup;
    animation-duration: 4s;
    animation-iteration-count: infinite;
}
.member-inner{
    animation-name: colors;
    animation-duration: 4s;
    animation-iteration-count: infinite;
}
@keyframes rotation {
    0%   {transform: rotate(360deg)}
    100% {transform: rotate(-360deg);}
}
@keyframes downup {
    0% {transform: rotateX(-360deg);}
    100% {transform: rotateX(0deg);}
}
@keyframes updown {
    0% {transform: rotateX(0deg);}
    100% {transform: rotateX(-360deg);}
}
@keyframes colors {
    0%   {color: blue;}
    25%  {color: cyan;}
    50%  {color: pink;}
    75%  {color: purple;}
    100% {color: blue;}
}
@-webkit-keyframes colorPulse {
      0% {background-color: #1abc9c;}
     10% {background-color: #2ecc71;}
    20% {background-color: #3498db;}
    30% {background-color: #9b59b6;}
    40% {background-color: #34495e;}
    50% {background-color: #f1c40f;}
    60% {background-color: #e67e22;}
    70% {background-color: #ecf0f1;}
    80% {background-color: #95a5a6;}
    90% {background-color: #f39c12;}
    100% {background-color: #000000;}
}
.theme-dark .chat>.content, .theme-dark .friends-table, .theme-dark .messages-wrapper {
    background-color: transparent;
}
.theme-dark .channel-members, #voice-connection, .guild-header header, .guilds li .guild-inner {
    -webkit-animation: colorPulse 1s infinite alternate !important;
}
.message {
        animation: zoom .2s infinite;
}
@keyframes zoom {
    0% {
        -webkit-transform: scale(1, 1);
    }
    50% {
        -webkit-transform: scale(1.2, 1.2);
    }
    100% {
        -webkit-transform: scale(1, 1);
    }
}
.btn-group:hover {
 -webkit-animation: jump 1s ease 0s infinite normal ;
 animation: jump 1s ease 0s infinite normal ;
}
.account:hover {
 -webkit-animation: jump 1s ease 0s infinite normal ;
 animation: jump 1s ease 0s infinite normal ;
}
@keyframes jump {
 0%{
	transform: translateY(10);
 }
 20%{
	transform: translateY(100);
 }
 40%{
	transform: translateY(600px);
 }
 50%{
	transform: translateY(100);
 }
 60%{
	transform: translateY(15px);
 }
 80%{
	transform: translateY(-69);
 }
 100%{
	transform: translateY(0);
 }
}
.theme-dark #friends {
    background-color: transparent;
}
.theme-dark #friends .friends-header {
    background-color: #BF1E1E;
    border-bottom: 1px solid #282b30;
    box-shadow: none;
}
@keyframes bobble {
    0% {
        transform: translate3d(50px, 40px, 0px);
        animation-timing-function: ease-in;
    }
    50% {
        transform: translate3d(50px, 50px, 0px);
        animation-timing-function: ease-out;
    }
    100% {
        transform: translate3d(50px, 40px, 0px);
    }
}
.channels-wrap:hover {
    animation: bobble .1s ease 0s infinite normal ;
}
}
