css:
@-moz-document domain("youtube.com") {
.player-api.player-width.player-height {background: none;}

#yt-masthead-container {
    background: rgba(19, 19, 19, 0.58) !important;
    border-bottom: 1px solid red !important;
	
}
div#player {	min-width:initial !important;z-index:0; }

div#player-mole-container {
    height: 100%;
    position: absolute !important;
    /* left: 50%; */
    /* transform: translateX(-50%); */
    width: 100%;
    min-width: initial !important;
}

div#masthead-positioner {
    opacity: 0;
    transition: opacity .8s ease;
}

div#masthead-positioner:hover {
    opacity: 1;
}

.appbar-hidden #masthead-positioner-height-offset {
    height: 0;
}
#player, #placeholder-player {
    width: 100vw !important;
    max-height: 56vh;
	min-width:initial !important;
    left: 0;
    margin: 0;
    position: relative;
    background-color: bisque;
    padding-bottom: 56.2%;
}

.html5-video-player,.html5-video-container,.html5-main-video , div#player-api{
    width: 100% !important;
    height: 100% !important;
    position: absolute !important;
    top: 0;
    left: 0 !important;
    margin: 0 !important;
}

.html5-video-player {}

div#watch7-container {
    position: relative;
    top: 0;
}

div#placeholder-player {
    margin-bottom: 5em;
}

.ytp-chrome-bottom {
    width: 92vw !important;
    left: 50% !important;
    transform: translateX(-50%);
    display:block;
}


div#content {
	top: 4em !important;
	position:relative; z-index:2;
}

div#placeholder-player {
    height: 0;
    padding-bottom: 0;
}

div#watch7-sidebar {
    position: fixed;
    top: 0 !important;
    padding-top:4em;
    right: 0;
    height: 70vh;
    overflow:auto;
    transform:translateX(98%);
    transition:all .8s ease;
    background-color: rgb(0, 0, 0);
    opacity:0.2;
    box-shadow: rgba(255, 0, 0, 0.61) -2em 0 2em -2em ,rgba(0, 0, 0, 1.61) 0 0 10em;
    border-left: red 1px solid;
	
}
div#watch7-sidebar:hover {
    transform:translateX(2em);
    opacity:.9;
}
div#watch-appbar-playlist {
    position: fixed !important;
    top: 0 !important;
    left: 0 !important;
    z-index: 10 !important;
    height: 100vh !important;
    right: initial !important;
    transform: initial !important;
    width: 20em !important;
    opacity:0;
    transform:translateX(-90%) !important;
    transition:all 1s ease !important;
}

div#watch-appbar-playlist:hover
{
    opacity:1 !important;
    transform:translateX(0) !important;
}
}
