css:
@-moz-document url-prefix("http://www.swesub.ws/") {
.main-container {
    background-color: #333;
    box-shadow: 0 0 30px #000;
}
.header {
    background-image: -moz-linear-gradient(70deg, rgb(239, 83, 80) 0%, rgb(198, 40, 40) 100%);
    background-image: -webkit-linear-gradient(70deg, #444 0%, #444 100%);
    background-image: -ms-linear-gradient(70deg, rgb(239, 83, 80) 0%, rgb(198, 40, 40) 100%);
    padding-top: 30px;
}
.movie.list .movie-info {
    width: 81%;
    float: left;
    padding: 6px;
    background-color: #555;
    position: relative;
}
body {
    background: #222 center center !important;
    color: #f5f5f5;
}
.top-list {
    box-shadow: 0 0 25px #000 !important;
}
.top-title {
    background-color: #222 !important;
}
.movie.list .movie-stats {
    background-color: #222 !important;
}
.movie:not(.list) .movie-info:hover .movie-cover {
    display: block;
    background-color: rgba(34, 34, 34, 0.75) !important;
}

.movies.similar .movie-info:hover .movie-cover {
    background-color: rgba(34, 34, 34, 0.75) !important;
}
#CommentTree div.comment.odd {
    background-color: #222 !important;
}
#CommentTree {
    color: #eee !important;
}
#CommentTree .input {
    background-color: #222 !important;
}
iframe {
    background: #222 !important;
    border: 1px solid #111 !important;
    color: #f5f5f5 !important;
}
.torrent-info {
    background-color: #111 !important; 
}
.movie.list .movie-story {
    color: #f5f5f5;
}
.movies a, .movies a:visited, .movies a:hover, .movies a:focus {
    color: #f5f5f5;
}
.movie.list .movie-box:hover .movie-info {
    background-color: #333;
}
.top-list p span {
    background-color: #222;
}
}
