css:
@-moz-document url-prefix("http://courses.apexlearning.com/apps/AceWeb/launch2.do?enrollmentID=") {
html {
    display: block;
background: #eee url(http://deskbg.com/i/c/1366x768/wpp/3/3658/rihanna-diamons-desktop-background.jpg) repeat-x fixed bottom left;
    
/*background: #eee url(http://i.quoteaddicts.com/media/quotes/1/26988-swag-tumblr-quotes-about-life.jpg) repeat-x fixed bottom left; */

}

#frameBody.macaw #frame iframe {  
    width: 968px;
}

#frameBody.macaw #topNav {
    width: 760px;
    height: 36px;
    margin: 0 30px 0 480px;
    text-align: right;
}

#border > div {
    width: 968px;
}

html > frameset > frame {
    width: 968px;
}



#frameBody.macaw #frame #border {
    border-top: solid 12px rgba(255, 255, 255, 0.42);
    border-left: solid 1px rgba(255, 255, 255, 0.42);
    border-right: solid 1px rgba(255, 255, 255, 0.42);
    background: rgba(255, 255, 255, 0);
    margin: 0 10px;
    padding: 0;
    width: 968px;
    height: 520px;
}




#header
{
display: none
}

#controls
{
margin-top:-52px
}

#navfooter
{
margin-top:-52px
}

#footer
{
margin-top:-52px
}

#frameBody.macaw #framecontainer {
    margin: 0;
    padding: 0 0 10px 0;
    text-align: left;
    background: none;
    width: 1268px;
}

#frameBody.macaw #framecontainer a:link, #frameBody.macaw #breadcrumb a:link {
    color: #FFFFFF;
    text-decoration: none;
    border: none;
}

#footer {
    position: absolute;
    top: 535px;
    left: 20px;
    width: 182px;
    height: 12px;
    padding: 5px 20px 0;
    margin: 20px 0 0;
    font-size: 10px;
    text-align: center;
    background: rgba(204, 153, 51, 0);
    border-top: 1px solid rgba(204, 204, 204, 0);
}

body {
    margin: 0;
    background: rgba(255, 255, 255, 0);
    color: whitesmoke;
    font: 12px/15px Arial, Geneva, Verdana, Helvetica, sans-serif;
    text-align: left;
}

p {
    margin: 0 0 10px 0;
    padding: 0;
    color: #FFF;
}

#container {
    BACKGROUND: rgba(255, 255, 255, 0);
    position: relative;
    margin: 0;
    padding: 0 0 15px 0;
    text-align: left;
    width: 650px;
}

#search {
    width: 150px;
    height: 21px;
    background: rgba(255, 255, 255, 0);
    font-size: 10px;
    border-top: 2px solid #4F6C9F;
    border-bottom: 1px solid #CCC;
    padding: 5px 20px 5px;
}

#frameBody.macaw #frameprint {
    height: 32px;
    padding: 4px 10px;
    margin: 0 10px 0 10px;
    border: solid 1px #EDB934;
    background: rgba(34, 34, 34, 0.65);
    width: 948px;
}


#frame #border {
    border: 1px solid rgba(241, 199, 93, 0);
    margin: 0 10px;
    padding: 0;
    width: 668px;
    height: 520px;
}

#frameBody.macaw #leftnav {
    display: inline;
    padding: 0;
    border-top: solid 12px rgba(255, 255, 255, 0.75);
    border-left: solid 1px rgba(255, 255, 255, 0.75);
    border-right: solid 1px rgba(255, 255, 255, 0.75);
    background: rgba(255, 255, 255, 0.33);
}

.currentNode {
    font-weight: bold;
    width: 100%;
    height: auto;
    border-top: 1px solid rgb(255, 255, 255) !important;
    border-bottom: 1px solid #FFFFFF !important;
}

#frameBody.macaw #leftnav #currentNode {
    font-weight: bold;
    width: 100%;
    height: auto;
    color: rgb(0, 0, 0) !important;
    border-top: 1px solid #D0DADA;
    border-bottom: 1px solid #D0D4DA;
}

#html > #body > #table > #tbody > #tr > #td > #a > #img {
    padding: 11 ;
    border-top-width: 0px;
    border-right-width: 0px;
    border-bottom-width: 0px;
    border-left-width: 0px;
    border-top-style: solid;
    border-right-style: solid;
    border-bottom-style: solid;
    border-left-style: solid;
    width: 57px;
    height: 19px;
}

#frameBody > #framecontainer > #footer {
    position: absolute;
    top: 610px;
    left: 20px;
    width: 180px;
    height: 12px;
    padding: 5px 20px 0;
    margin: 20px 0 0;
    font-size: 10px;
    text-align: center;
    background: rgba(204, 153, 51, 0);
    border-top: 1px solid rgba(204, 204, 204, 0);
}

#frameBody.macaw #controls {
    position: absolute;
    top: 569px;
    left: 20px;
    width: 220px;
    height: 36px;
    padding: 0;
    border: solid 1px #EDB934;
    background: rgba(217, 217, 217, 0.33);
    text-align: center;
    vertical-align: middle;
}





#frameBody.macaw #framecontainer a:link, #frameBody.macaw #breadcrumb a:link {
    color: #3968BF;
    text-decoration: none;
    border: none;
}



#html > #framebody.macaw > #framecontainer > #frame > #border > #div > #iframe > #html > #frameset > #navBarFrame > #html > #body {
	display: none;
    background: #000;
}






#frameBody.macaw #framecontainer a:link, #frameBody.macaw #breadcrumb a:link {
    color: #ffffff;
    text-decoration: none;
    border: none;
}
}

@-moz-document url("https://www.apexvs.com/lms/#!page/DashBoard") {
body {
    margin: 0;
    background-color: rgba(183, 113, 113, 0);
    background: #eee url(http://deskbg.com/i/c/1366x768/wpp/3/3658/rihanna-diamons-desktop-background.jpg) repeat-x fixed bottom left;
    font-family: open_sansregular, Arial, sans-serif;
    min-width: 960px;
}
h2 {
    font-size: 18px;
    color: #ffffff;
    margin-bottom: 5px;
    font-weight: 400;
}
.announcementTitleLink {
    color: #ffffff!important;
    text-decoration: none!important;
}
.pageHeader .filterDetail {
    color: #ffffff;
    font-size: 12px;
    margin-top: 11px;
    margin-right: 2px;
}
.footer {
    visibility: hidden !important;
    height: 25px;
    background-color: #f4f5f6;
    color: #a2a2a2;
    font-size: 10px;
    border-top: 1px solid #9d9c9c;
    width: 100%;
}
}

@-moz-document url("http://courses.apexlearning.com/apps/AceWeb/ActivityLaunch/SIANavigation.jsp?") {
body {
    display: block;
    margin: 8px;
background: rgba(255, 255, 255, 0.2);
}

body > table > tbody > tr > td:nth-child(1) {
    width: 57px;
}

body > table > tbody > tr > td:nth-child(2) {
    width: 50px;
}

body > table > tbody > tr > td:nth-child(3) {
    width: 57px;
}
}

@-moz-document url-prefix("http://courses.apexlearning.com/apps/AceWeb/processRequest.do?activityID=") {
body {
    margin: 0;
    background: rgba(255, 255, 255, 0.2);
    color: #000;
    font: 14px/18px Arial, Geneva, Verdana, Helvetica, sans-serif;
    text-align: left;
}



html > body > table {
    width: 968px ;
}
}
