css:
@-moz-document url("https://www.apexvs.com/apexui/default.aspx") {
html, body {
    background: #1964A8 url(http://bgfons.com/upload/rainbow_texture679.jpg) repeat-x fixed bottom left;
    font-family: Arial, Regular;
    font-size: 14px;
    text-align: center;
    margin: 0px;
    padding: 0;
    height: 100%;
    width: 100%;
}

#loginCopyrightWrap {
    background-color: rgba(25, 100, 168, 0);
    position: relative;
    height: 30px;
    padding: 5px 0;
    font-family: Arial, Regular;
    font-size: smaller;
    color: rgba(48, 136, 200, 0);
    text-align: center;
    margin: 0 auto;
    display: inline-block;
}

#loginFooterWrap {
    font-family: Arial, Regular;
    font-size: smaller;
    color: rgba(102, 169, 217, 0);
    width: auto;
    display: inline-block;
    text-decoration: none;
    text-align: center;
    margin: 0 auto;
    word-wrap: normal;
}

#loginFooterWrap a, #loginFooterWrap a:visited {
    color: rgba(102, 169, 217, 0);
    text-decoration: none;
}

#systemCheckupButton {
    width: 270px;
    font-family: Arial, Regular;
    font-size: 13px;
    color: #b2defe;
    background-color: rgba(48, 116, 177, 0.5);
    padding: 7px;
    border: 3px solid rgba(22, 97, 166, 0);
    cursor: default;
    text-align: center;
    text-decoration: none;
}
}
