css:
@-moz-document regexp("https?://.*.bitrix24.ru/.*") {

.task-detail-content {
	position: relative;
	padding: 8px 10px;
	font-size: 14px;
	line-height: 22px;
	color: #535c69;
	font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
	overflow: hidden;
}

.workarea-content-paddings {
	padding: 4px 4px 4px 4px;
	overflow-x: auto;
	overflow-y: visible;
}

.task-detail-content {
	position: relative;
	padding: 4px 4px;
	font-size: 12px;
	line-height: 18px;
	color: #535c69;
	font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
	overflow: hidden;
}

.task-detail-sidebar-content {
	background: #fff;
	padding: 4px 0;
	color: #535c69;
	font-size: 13px;
	font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
}
	
	table.bx-layout-inner-inner-table td.bx-layout-inner-inner-cont {
	padding-right: 70px;
}

.template-bitrix24 .task-detail-sidebar-content {
	padding-left: 0px;
	padding-right: 0px;
	padding-top: 0px;
	padding-bottom: 0px;
}
	
	.task-detail-sidebar-status {
	background: #d5f1fc;
	font-weight: bold;
	padding: 7px;
}

	.task-detail-sidebar-info-link {
	font-size: 12px;
	color: #8d949c;
	float: right;
	line-height: 34px;
	cursor: pointer;
	padding-right: 4px;
}
	
	.task-detail-sidebar-info {
	padding: 10px;
	border-top: 1px solid #eef2f4 !important;	
	background: #FFF;
	line-height: 18px;
}
	
	.task-detail-sidebar-item {
	padding: 10px 0;
	border-bottom: 1px solid #eef2f4;
	overflow: hidden;
}
	
	
	
	
	.task-detail-sidebar-info-user {
	padding: 4px 2px 4px 12px;
	
}
	
	.task-detail-sidebar-info-user-pos {
	font-size: 11px;
	color: #8d949c;
}
	
	.task-detail-sidebar-item-delay {
	padding: 3px 0 0 0;
		margin: 10px 0 -7px 0;

}
	
.task-detail-buttons {
	padding: 4px 15px 4px;
	text-align: left;
	border-top: 1px solid #eef2f4;
	background: #fff;
	line-height: 50px;
}

.template-bitrix24 .task-detail-info {
	margin: 0px 0px 0px 0px;
}

.task-detail-comments {
	padding: 4px;
}

.pagetitle-wrap {
	min-height: 50px;
	padding: 2px 0 0px 0px;
	position: relative;
}

.bx-layout-inner-left {
	padding-left: 5px;
	width: 195px;
}

.task-detail-sidebar-info-title {
	padding-left: 8px;
	margin-top: 0px;
}
	
	.task-detail-extra {
	padding-top: 4px;
	overflow: hidden;
	border-top: 1px solid #e7e6d6;
	width: 100%;
}
	
	.task-detail-properties {
	border-top: 1px solid #e7e6d6;
	margin: 20px 0 0;
	padding-top: 4px;
}
	
	.task-detail-properties {
	border-top: 1px solid #e7e6d6;
	margin: 4px 0 0;
	padding-top: 4px;
}
	
	.task-detail-description {
	border-bottom: 1px solid #e7e6d6;
	margin-bottom: 4px;
	padding-bottom: 4px;
	word-wrap: break-word;
}
	.task-detail-files {
	margin-bottom: 4px;
	padding: 4px 0 0;
}
	.task-detail-list {
	background: #fff;
	padding: 4px;
	overflow-x: auto;
	}		

	.task-detail-list-title {
	margin: 0 0 6px;

}
	
	.task-list-table th:last-child {
	border-radius: 0 0px 0px 0;
	text-align: right;
}

	/*Панель в задачах*/
	
  .task-target-below_pagetitle .tasks-top-menu {
	padding: 0px 0px 0px 0px;
	background: #fff;
	border: 0;
	width: 100%;
	margin-bottom: 5px;
}
	
	#sidebar {
	padding-left: 8px;		
	position: relative;
	width: 280px;
	float: right;
}
	
	.tasks-top-item-counter {
	position: absolute;
	min-width: 12px;
	padding: 0 4px;
	font-size: 11px;
	height: 20px;
	border-radius: 10px;
	background: #f73100;
	color: #fff;
	text-align: center;
	line-height: 20px;
	top: 2px;
	right: 0px;
}
	
	.template-bitrix24 .task-detail-list {
	border-bottom: 8px solid #eef2f4;
	margin-bottom: 0;
}
	.template-bitrix24 .task-detail-buttons {
	border-bottom: 8px solid #eef2f4;
}
	
	/*Задача*/
	
	.task-detail-content {
	position: relative;
	padding: 12px 20px 12px 12px;
	font-size: 14px;
	line-height: 22px;
	color: #535c69;
	font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
	overflow: hidden;
	
}
	
	.feed-add-post-text {	
	padding: 0px;
}
	
	.task-detail-buttons {
	padding: 0px 15px 2px;	
}
	
	.feed-com-add-block {
	padding: 8px 8px 8px 71px;
}
	
	.task-comments-log-switcher {
	height: 32px;	
}
	
	.task-switcher-text {

	line-height: 32px;
	height: 31px;	
}
	
	.task-switcher {
	
	height: 32px;
}
	
	.task-switcher-selected {
	cursor: default;
	border-bottom: 1px solid #FFF;
}
	
	.task-switcher-block {
	
	border-radius: 0px;

}
	.task-info-panel {
	padding-bottom: 2px;
}
	.task-info-panel-title {
	padding: 0 25px 0 0px;

}
	
	.task-info-panel-important {
	float: right;
	line-height: 30px;
	background: orangered;
}
	
	.task-info-panel-important span::after {
	display: none;
}
	
	.task-info-panel-important label::after {
	display: none;
}
	
.task-field-drg-btn {
	
	height: 20px;	
}
	
	.task-field-title-edit, .task-field-title-del, .task-field-title-ok {
	height: 20px;
	top:0px;
	vertical-align: middle !important;
}
	
	.task-checklist-field-inner-add {
	padding: 0px 60px 0px 25px;
}
	
	.no-paddings .workarea-content-paddings {
	padding-left: 0;
	padding-right: 0;
	padding-bottom: 0;
	padding-top: 0;
}
	.task-detail-header {
	background: #fff4a3;
	padding: 0px 0px 0px 10px;
}
	
	.subtitle-mode .pagetitle-wrap {
	min-height: 37px;
	padding: 8px 0 6px 0px;
}
	
	.profile-menu {
	background-color: #eef2f4;
	padding-bottom: 1px;
	padding-top: 8px;
}
	
	.profile-menu-inner, .department-manager-info-block {
	background-color: #fff;
	background-image: linear-gradient(to bottom,#fbfbfb,#eff6f9);
	border: 1px solid #d7dee0;
	border-radius: 0px;
	box-shadow: 0 1px 0 #dde5e8;
	margin: 0 0 1px;
	padding: 9px 7px 10px 76px;
	position: relative;
}
	/*----*/
	
	/*Меню слева*/
#menu {
	margin: 8px 0px 8px 8px;
	width: 180px;
}
	
  .menu-create-but {
	background: #baed21;
	border-radius: 2px;
	color: #535b69;
	cursor: pointer;
	font-family: "OpenSans-Bold",Helvetica,Arial,sans-serif;
	font-size: 12px;
	height: 42px;
	line-height: 41px;
	margin: 0 0 16px -3px;
	padding: 0 0 0 42px;
	position: relative;
	text-transform: uppercase;
	width: 133px;
}
/*	
	.menu-item-block {
	height: 32px;	
}*/
	
	.menu-item-link {
	padding: 5px 12px 0 28px;
	width: 160px;
}
	
	.menu-item-block > .menu-item-link:hover {
	background-color: #e3e8eb;
	border-bottom: 0;
	color: #000;	
	text-decoration: none;
	width: 163px;
}
	
	.menu-item-index {
	height: 22px;
	line-height: 22px;
	padding: 0 2px;
}
	
	.menu-item-active .menu-item-link-text {
	border-radius: 3px;
		width:auto !important;

}
  /*---Конец меню*/
	
	/*Новая задача*/
  .task-options-item {
	position: relative;
	padding: 4px 4px 4px 0;
	border-bottom: 1px solid #e6e9ec;
	margin: 0 10px;
	overflow: hidden;

}
	
	.template-bitrix24 .task-info {
	margin: 0 0px;
}
  /*---Конец Новая задача*/
	
	/*Чеклист*/
	.task-checklist-field-inner {
	display: inline-block;
	padding: 0px 60px 0px 25px;
	margin: 0;

	position: relative;
}
	
	/*Живая лента*/
	.feed-add-post-form-variants {
	border-bottom: 1px solid #f0f1f2;
	min-height: 19px;
	padding: 10px 0 10px 15px;
	position: relative;
}
	
	.sidebar-widget-top {
	height: 40px;
	position: relative;
}
	
	.sidebar-widget-top-title {
	
	height: 40px;
	line-height: 40px;	
}
	
	.plus-icon {	
	top: 7px;	
}
	
	.feed-add-post-micro {
	color: #80868e;
	font-size: 18px;
	font-family: "OpenSans-Regular",sans-serif;
	margin: 0;
	height: 18px;
	padding: 10px 2px 20px 18px;
	position: relative;
	cursor: text;
}
	
	.feed-new-message-informer-place {
	background-color: #eef2f4;
	height: 33px;
	position: relative;
	z-index: 140;
}
	
	.pagetitle {
	font-size: 30px;
	margin: 0;
	padding: 3px;
	overflow: hidden;
}
	
	.sidebar-pulse-block {
	background-color: #2fc7f7;
	border-radius: 1px;
	cursor: pointer;
	margin-bottom: 5px;
	padding: 3px 3px 3px 7px;
	position: absolute;
	top: -42px;
	right: 0;
	width: 270px;
}
	
	.feed-post-block {
	padding-bottom: 0px;
}
	
	.bx-im-bar {
	top: 0px !important;	
}
	
	.bx-im-informer {
	height: 40px;	
	background-color: #FFF;
}
	
	.bx-im-informer-icon {
	
	top: 3px;	
}
	.bx-im-users-wrap {
	
	padding-top: 0px;
}
	
	.bx-im-users-wrap {
	top: 82px;
}
	

	
	.bx-layout-inner-inner-top-row .bx-layout-inner-inner-cont {
	height: 54px;
}
	/*Шапка*/

	
	.b24-app-block {
	box-sizing: border-box;
	font-family: "OpenSans-Bold",'Open Sans',sans-serif;
	padding: 0;
	margin: 3px 0 20px;
	display: none;
}
	
	.sidebar-pulse-block {
display: none;
	}
	

}
