css:
@-moz-document domain(docs.racket-lang.org), domain(jeapostrophe.github.io), regexp('https*:\/\/www.greghendershott.com\/fear-of-macros.*') {
  body, .RPartExplain {
    background-color: black !important;
    color: gray !important;
  }
  .RktMeta, .stt, .RktMod, .RktSym {
    color: gray !important;
  }
  .RktVar {
    color: green !important;
  }
  .RktRes {
    color: maroon !important;
  }
  .RktIn, .RktInBG, .navsettop a:hover, .navsetbottom a:hover {
    background-color: black !important;
  }
  .refcolumn {
    background-color: black !important;
    border-color: #262626 !important;
  }
  .boxed, blockquote.SVInsetFlow, .SVInsetFlow, .SVInsetFlow tbody, .defmodule, .hide_when_on_the_web .refcolumn {
    background: #353535 !important;
    lighting-color: #353535 !important;
    background-color: #353535 !important;
    color: #17174f !important;
    border-top: none !important;
  }
  .hide_when_on_the_web .refcolumn {
    border: none !important;
  }
  .SVInsetFlow .RBackgroundLabelInner {
    color: #17174f !important;
  }
  .SVInsetFlow a, .SVInsetFlow .RktValLink {
    color: green !important;
  }
  .SVInsetFlow .RktVar {
    color: #ccc !important;
  }
  .tocset {
    background-color: #353535 !important;
  }
  a:link, a:visited, .RktValLink, .RktStxLink {
    color:#4183C4 !important;
  }
  h1,h2,h3,h4,h5,h6 {
    color: green !important;
  }
  .searchbox {
    background-color: #666 !important;
    border: none !important;
  }
  #plt_search_container > div {
    background-color: rgb(57, 57, 57) !important;
  }
  input#search_box {
    background-color: rgb(142, 124, 124) !important;
  }
  #plt_search_container > div > div > div {
    background-color: black !important;
  }
  .SCodeFlow {
    border-left: 1px dotted gray !important;
  }
  *[style*="background-color: yellow"] {
    background-color: #4c4d00 !important;
  }
  .highlighted {
    background-color: darkgray !important;
  }
  .RktErr {
  background-color: #420e09 !important;
  color: #999999 !important;
  }
  #search_status>span {
    background-color: black !important;
  }
  .together {
    border-top: 2px solid #666 !important;
  }
  .Rfiletitle, .fileblock_filetitle {
    background-color: #0d0d0d !important;
  }
  .RktCmt {
    color: #595959 !important;
  }
  .terminal {
    background: #020719 !important;
    color: #7D9396 !important;
  }
  .RktKw {
    color: #a3a3a3 !important;
    font-weight: bold !important;
  }
  .foreign-code, .browser {
    background-color: #111 !important;
    color: gray !important;
  }
  a.tocviewselflink {
    font-weight: bold !important;
    text-decoration: underline !important;
  }
  .navbar {
    background: black !important;
  }
  img.logo[src*="racket-lang.org/logo-and-text-1-2.png"], .docsite-logo {
    -webkit-filter: invert(100%) hue-rotate(180deg) !important;
  }
}
