css:
/* Live Chat addon for Comfort Dark Red By Icculus574 */
@-moz-document domain(youtube.com) {

/* Main Form */
.live-chat-widget .comment {
    background-color: #1b1b1b !important;
	border-right: #2D2D2D 1px solid !important;
}

.live-chat-widget .comment-text {
    color: #aaa !important;
}

.live-chat-widget #all-comments {
    margin-right: 0;
	margin-bottom: -1px;
}

.live-chat-widget #comments-scroller {
    border: #2D2D2D 1px solid !important;
    margin-bottom: -1px;
}

.live-chat-widget.has-padded-container #comments-scroller {
    background: #252525;
}

.live-chat-widget #live-comments-setting-bottom-scroll {
    color: #E8E6E6;
    background-color: rgba(185, 48, 48, 0.94);
}

#hide-live-comments .yt-uix-button-expander {
        color: #999 !important;
}

/* Reposition PNG Display Box To Dark Images In Google PNG */
.live-chat-widget .overflow-menu-button:before {
    background: no-repeat url(https://s.ytimg.com/yts/imgbin/www-livechat-vfllutSzQ.webp) -417px -158px;
}

.live-chat-widget .live-comments-emoji-picker-open::before {
    background: no-repeat url(https://s.ytimg.com/yts/imgbin/www-livechat-vfllutSzQ.webp) -415px 0;
}

.live-chat-overflow-menu .chat-popout-menu:before {
    background: no-repeat url(https://s.ytimg.com/yts/imgbin/www-livechat-vfllutSzQ.webp) -332px -149px;
}


.live-comments-emoji-type-button.emoji_people {
    background: url(https://ssl.gstatic.com/chat/emoji/categories-ddae3017db1c61a02f4bcc375a42b91f.png) 0 -145px !important;
}

.live-comments-emoji-type-button.emoji_flora {
    background: no-repeat url(https://ssl.gstatic.com/chat/emoji/categories-ddae3017db1c61a02f4bcc375a42b91f.png) 0 -83px !important;
}

.live-comments-emoji-type-button.emoji_things {
    background: no-repeat url(https://ssl.gstatic.com/chat/emoji/categories-ddae3017db1c61a02f4bcc375a42b91f.png) 0 -207px !important;
}

.live-comments-emoji-type-button.emoji_transportation {
    background: url(https://ssl.gstatic.com/chat/emoji/categories-ddae3017db1c61a02f4bcc375a42b91f.png) 0 -114px !important;
}

.live-comments-emoji-type-button.emoji_symbols {
    background: no-repeat url(https://ssl.gstatic.com/chat/emoji/categories-ddae3017db1c61a02f4bcc375a42b91f.png) 0 -238px !important;
}

/* Emoticon Picker Pop-Up */

.live-comments-emoji-picker-tab-row {
    background-color: rgb(21, 21, 21);
}

.live-comments-emoji-picker-tab-cell .live-comments-emoji-type-button.active {
    background-color: rgba(86, 86, 86, 0.69) !important;
}

/* Add Missing Styling Portions To Chat Pop-Out */

.live-chat-page .live-chat-widget #comments-scroller {
    padding-top: 0px !important;
    background: #2F2F2F !important;
}

.live-chat-widget #live-comments-controls {
    background: #1b1b1b !important;
}    

.yt-uix-menu yt-uix-menu-flipped yt-uix-menu-reversed {
    display:none;
}

.live-chat-widget .comments-textarea {
    color: #aaa !important;
}
}
