css:
@-moz-document domain(4chan.org) {
    
	/* custom colors */             /*var(--alt-bg)*/
	:root {
	--post-bg: #F0F1F2 !important;                /* tab bg */
	--alt-bg: #DCDFE3 !important;                /* tab bg */    
	--header-bg: #F0F1F2 !important;                /* tab bg */        
	--main-text: #282828 !important;      /* tab fg */
	--secondary-text: #789922 !important;        /* selected tab fg*/
	--third-text: #666 !important;       /* hover tab fg & url bar fg*/
	}   
		
	body, html {
		
		
	  background: url('https://puu.sh/lR6yz/bf70d6b5ea.png') !important;
		 background: var(--alt-bg) !important;
		background-repeat: repeat !important;
		background-attachment: fixed !important;
		background-position: center center !important;
		margin: auto !important;  
		background-color: var(--alt-bg) !important;
	}   
		
	/* ~~~~~ customize colors to your liking ~~~~~ */        

												  /* block bg colors */
		
	/* qr and date block bg */
	.qr-shortcut, span.dateTime, .yotsuba.fixed:root #custom-board-list .current, .yotsuba-b.fixed:root #custom-board-list .current, .yotsuba-b:root #header-bar:hover, .yotsuba-b:root #board-list a:hover, .quotelink:hover, .settings-link, span.menu-button, #thread-stats, .container, .fileText {
	color: var(--third-text) !important;        
	}
		
	/* quote bg */
	div.post div.postInfo span.postNum, div.post div.postInfo span.postNum a {
	color: var(--third-text) !important;        
	}    

	/* watcher and name block bg, quote text */
	#watcher-link, .warning, div.post div.postInfo span.nameBlock span.name, div.post div.postInfo span.nameBlock span.postertrip, .nameBlock, .yotsuba_b_new .backlink, .quotelink, .yotsuba_b_new .backlink a, a:hover {
	color: var(--secondary-text) !important;        
	}

	/* EAI and subject block bg */
	.expand-all-shortcut, .subject, div.post div.postInfo span.subject, #custom-board-list, .yotsuba-b:root #board-list a, .yotsuba:root #board-list a {
	color: var(--main-text) !important;        
	}
				  
														   /* text colors */
	/* main text */
	body, #qr, #thread-watcher, #thread-watcher .refresh, .menu-button .fa, #watched-threads .watcher-title, #watched-threads .watcher-unread, #thread-watcher a, .entry, #qr textarea.field, .right-box, .field, .globalMessage, div#boardNavDesktop, table.postForm > tbody > tr > td:first-child, input[type="text"], input[type="password"], table.postForm > tbody textarea, #recaptcha_response_field, div#boardNavDesktopFoot, .gal-count, #index-search {
	color: var(--main-text) !important;
	}

	/* links & quoted */
	a, div.post div.postInfo span.postNum a:hover, a.deadlink, a.close, .tab-filter, a.tab-main.tab-selected, #qr-no-file, .yotsuba-b:root .entry, #boards a, .boxbar, .deadlink, .gal-name, #qr select, select {
	color: var(--third-text) !important;
	}

	/* green text */
	blockquote > span.quote, .catalog-thread > .comment > span.quote {
	color: var(--secondary-text) !important;
	}
		  
												  /* bg colors */
	/* reply bg */
	div.reply, .catalog-mode:root .board, .inline .post {
	background: var(--post-bg) !important;
	}   
		
	/* chat style reply - make the border color match the reply bg color   
	.replyContainer:after {
		content: ' '!important;
	  float: left !important;
		width: 0 !important;
		height: 0 !important;
		margin-left: 0px !important;
		margin-top: -8px !important;
		border: 15px solid !important;
	}
	.opContainer:after {
	  content: ' '!important;
	  float: left !important;
		width: 0 !important;
		height: 0 !important;
		margin-left: -10px !important;
		margin-top: -8px !important;
		border: 20px solid !important;
	}

	/* header bg and main site bg */
	.boxcontent, .top-box, .left-box, .right-box, #recent-images a {
	background: var(--post-bg) !important;
	}
		

	/* menus, watcher, linked posts and qr bg */
	#thread-watcher, #fourchanx-settings.dialog,
	fieldset legend, .yotsuba-b:root .suboption-list > div:last-of-type, .dialog:not(#qr):not(#thread-watcher):not(#header-bar), #fourchanx-settings code, kbd, .reply:target, .reply.highlight, .yotsuba-b:root .inline {
	background: var(--post-bg) !important;
	}
		
	#header-bar, .yotsuba-b:root:not(.fixed) #header-bar, span.boardList, .yotsuba:root .suboption-list > div:last-of-type {
		background: var(--header-bg) !important;
	}    
		
	/* quoted bg */
	 .yotsuba:root .inline, .boxbar, #qr select, select {
		 background: var(--alt-bg) !important;
	 }    
		
	#ft li, .right-box,  .boxcontent, .top-box, .left-box, #recent-images .boxcontent, #qr select {
		border: none !important;
	}    
		
	.top-box .menubutton, .right-box .menubutton {
		background-image: none !important;
	}    
		
	#ft li, #ft ul {
		background: none !important;
		border: none !important;
	}    

		
																 /* misc */    

	/* quotes to (you) border color */
	.highlight-you:root .quotesYou.opContainer, .highlight-you:root .quotesYou > .reply {
		border-top: 1px solid var(--secondary-text) !important;
	}    
		
	/* notifications */
	.message {
		background-color: var(--post-bg) !important;
		color: var(--main-text) !important;
		text-shadow: none !important;
	}    
		
	/* waifu 
	body::after {
	   content: url('https://puu.sh/l8I5S/792d7fc13d.png');
		position: fixed !important;
		margin: auto !important;
		left: 0 !important;
		bottom: 0 !important;
		margin-left: 0px !important;
		z-index: 1 !important;
	}   */
			  
	/*#qr input.field {
		opacity: 0 !important;
	}

	#qr input.field:hover {
		opacity: 1 !important;
	}*/

													   /* thread */    
	div.thread {
		width: 90% !important;
		/*margin-left: 400px !important;*/
		margin: 0 auto !important;
		/* 120 for header, 30 w/o */
		margin-top: 65px !important;
	}      
		
	/*div.thread, .replyContainer, .postContainer {
		background: transparent !important;
	}  */
		
	#skellington {
		display: none !important;
	}    
		
	div.op {
		margin-top: 0px !important;
	}  
		
	/*#fT1834928 {
		margin-top: -10px !important;
		margin-right: 141px !important;
	}    */   
		
	#header-bar {
		border: 0 !important;
		height: 40px !important;
		margin: 0 auto !important;
	   margin-top: 0px !important;
	/* max-width: 1096px !important;  */
		box-shadow: 1px 3px 2px rgba(0, 0, 0, 0.3);
		display: inline-block !important;
	}    
		
	#thread-stats, span.menu-button, .settings-link, #watcher-link {
		top: 8px !important;
	  }       
	.centered-links:root #custom-board-list {
		top: 7px !important;
		/*margin-left: 550px !important;*/
		display: inline-block !important;    
		left: 31% !important;
		text-align: center !important;
	}    
		
	span.boardList {
		margin-top: 0px !important;
		display: inline-block !important;
		}    
	.hide-board-list-button {
	   margin-top: 28px !important;
	   display: inline-block !important;
		}    
		
	#full-board-list, .centered-links:root #header-bar {
		z-index: 200 !important;
	}    
	#thread-stats:hover {
		z-index: -20 !important;
	}    
		
	.yotsuba-b:root #board-list {
		max-width: 396px !important;
	}      
		
	.centered-links:root #shortcuts, #custom-board-list {   
		left: 56px !important;     
	}    

	#shortcuts {
			float: none !important;
		}    
		
	#header-bar a.current, .yotsuba-b:root #board-list a {
		text-decoration: none !important;    
	}  
		
	.index-refresh-shortcut, span.shortcut:nth-child(5) {
		display: none !important;
	}    
			
	div.reply {
		box-shadow: 1px 3px 2px rgba(0, 0, 0, 0.3);   
		/*box-shadow: none !important;*/
	} 
	div.reply:hover {
		box-shadow: 1px 3px 2px rgba(0, 0, 0, 0.3);
		/*box-shadow: none !important;*/
	}     
		
		
	/* reply and op bg colors */    

	.replyContainer {
		padding: 0px !important;
	}    
		
	.postContainer.replyContainer {
		margin-top: 15px !important;
	}    
	   
	.post.reply, .opContainer, .post.reply:hover {
		width: 100% !important;
		padding: 10px !important;
		-moz-box-sizing:border-box!important;
	}    
		
	div.opContainer {
			width: 98% !important;
			 margin: 0 auto !important;
		}    
		
	.opContainer {
		padding-bottom: 3px !important;
	}

	.board.json-index .thread .postContainer {
		margin-top: -.5% !important;
	}
		
	.index .thread {
		background-color: var(--post-bg) !important;
		box-shadow: 3px 6px 10px rgba(0, 0, 0, 0.2) !important;
	}
		
	span.postNum a:hover {
		font-family:  Open Sans !important;
	}
		
	/* give reply borders  */
	div.reply, .post.reply, .opContainer {
		border: 0px !important;
		border-style: solid !important;
	}           
		
	/* op height */   
	div.opContainer {
		min-height: 180px !important;
	}     
		
	div.post blockquote.postMessage {
		width: 96% !important;
	}    
		  
		/* reply link, backlink location
		
	div.post div.postInfo span.postNum a {
		visibility: hidden !important;
		float:right;
	}                  
	div.post div.postInfo span.postNum a:before {
		content: url(https://puu.sh/k0oPB/098f590af7.png) !important;
		opacity: 0.6 !important;  
		visibility: visible;
		float:right;
		margin-left: -9px !important;
		margin-top: -10px !important;
		width: 8px !important;
	}     */
		
	div.post div.postInfo span.postNum {
		padding: 8px !important;
		float: right !important;
		margin-right: 4px !important;
		margin-top: -8px !important;
	}        
				  
	span.dateTime {
		padding: 4px !important;
		right: 27% !important;
		margin-top: -4px !important;
	}             
		
	/* menu dropdown */    
	.fa-angle-down::before, .op .menu-button, .postContainer .menu-button {
		/*content: url(https://puu.sh/k0rck/835a18128d.png) !important;*/
		content: url(https://puu.sh/kJK7r/66df022a75.png) !important;
		margin-right: 0px;
		margin-top: 1px;
		float:right;
		width: 10px !important;
		height: 10px !important;
		/*box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.3);*/
	}        

	.fa-angle-down::before {
		opacity: 0.5 !important;
	}       

	/* give image borders */    
	img {
		 border: 0px !important;
		border-color: var(--alt-bg) !important;
		border-style: solid !important;
	}    
		
	/* Replace backlinks by arrow icons */
	.backlink:first-child::before {
	margin-left: -5px !important;
	}
	.backlink {
	/*font-size: 0 !important;*/
	}
	.post.op .backlink:first-child {
	margin-left: 0px !important;
	}      

	/* fonts */
	body, .post.reply, .opContainer, #board-list, #custom-board-list, #menu.dialog, .entry, .yotsuba-b:root .suboption-list > div:last-of-type, option, #qr textarea.field, .field, #index-search.field, #updater.dialog, #thread-stats.dialog, #thread-stats, .boardSubtitle, #thread-watcher, div.post div.postInfo span.nameBlock span.postertrip, div.post div.postInfo span.nameBlock span.name, div.post div.postInfo span.subject, #qr:not(.captcha-open) .captcha-counter > a, #file-n-submit > input, #watcher-link, .settings-link, .expand-all-shortcut, .qr-shortcut, div#boardNavDesktop, .yotsuba_b_new .backlink, .quotelink, div#boardNavDesktop a, table.postForm > tbody > tr > td:first-child, input[type="text"], input[type="password"], table.postForm > tbody textarea, #recaptcha_response_field, div#boardNavDesktopFoot a, #qr select {
		font-family:  Open Sans !important;
		font-weight: 600 !important;
		font-size: 13px !important;
	}
		
	/* change the margin depending on whether you show the banner and the header */    
	hr {
		margin-top:0px !important;
		padding-top: 0px !important;
		border: 0 !important;
	}
		
	/* buttons */
		
	.settings-link.fa-wrench:before, .qr-shortcut:before, #watcher-link:before {
		font-family:  Open Sans !important;
		font-weight: 600 !important;
		font-size: 12px !important;
	}    
		
	#thread-stats {
		position: absolute !important;
		left: 14px !important;
	  }         
	#watcher-link {
		position: absolute !important;
		/*margin-left: 360px !important;*/
		left: 99% !important;
	}    
	#watcher-link:before {
		content: "Watch" !important;
		margin-right: 5px !important;
		z-index: 3 !important;
	  }     

	/*.qr-shortcut {
		margin-left: -45px !important;
	}    
	.qr-shortcut:before {
		content: "Reply" !important;
		margin-right: 5px !important;
		z-index: 3 !important;
	  }      */ 
		
	.settings-link {
		position: absolute !important;
		right: 19px !important;
	  }
	.settings-link.fa-wrench:before {
		content: "Options" !important;
		margin-right: 5px !important;
		z-index: 3 !important;
	  }   
				
	/* fade images 
		
	div.boardBanner, img {
		opacity: 0.5 !important;
	}   
		
	div.boardBanner:hover, img.hover, .expanded-image > .post > .file > .fileThumb > .full-image {
		opacity: 0.9 !important;
	}       */ 
		   
		
	/* Links */
	a, a:hover, div.post div.postInfo span.postNum a:hover, a.quotelink, a.deadlink {
	text-decoration: none !important;
	}    
		
	/*.backlink.deadlink:not(.forwardlink), .quotelink.deadlink:not(.forwardlink) {
		text-decoration: none !important;
	}  */  
		
	/* if you use thread conversations */
		
	.threadContainer {
		border-left: 1px solid #434359 !important;
	}    

	#header-bar .brackets-wrap:after,
	#header-bar .brackets-wrap:before,
	.shortcut:not(:last-child):after {
		content:""!important;
	}
		
	#update-timer {
		color: transparent !important;
	}    
		
	a.current:nth-child(5), .yotsuba.fixed:root #custom-board-list .current:hover, .yotsuba-b.fixed:root #custom-board-list .current {
		border-bottom: 0px;
	}
		
	/*#nav-links {
		right:0;
		padding:0px;
		position:fixed;
		bottom:0;
		z-index:10;
	}*/
		
	.navLinks {
	   margin-top: 50px !important; 
	}    
		
	#index-size, #index-sort, #index-mode {
		border: none !important;
	}    
		
	span.navLinks:nth-child(1) {
		display: none;
	}    
			 
	div.sideArrows,    
	.qr-link-container,    
	.boardSubtitle,    
	.bottomCtrl.desktop,
	#absbot.absBotText,
	.navLinks.desktop,
	.navLinks.navLinksBot.desktop {
		display: none !important;
	}

	div.center:nth-child(16), div.center:nth-child(6) {
		display: none !important;
	}
		
	div.center:nth-child(4) {
		border: none !important;
	}    

	.yotsuba:root .hide-post-button,
	.yotsuba-b:root .hide-post-button {
		display: none !important;
	}
		
	#unread-line {
		border:0!important;
		height:1px!important;
		margin-top:-10px!important;
	}
		
	.inline {
		padding-left: 15px !important;
		padding-right: 15px !important;
		border: 0 !important;
	}
	   
	:root a.backlink,
	:root .backlink-container > a.backlink.deadlink,
	.warning,
	:root a.replylink:hover,
	:root a:not(.entry):hover {
	   /* background: url(http://i.imgur.com/lFuGnSO.png) center no-repeat !important; */
	   /* content: url('http://i.imgur.com/lFuGnSO.png') !important;*/
	}
		
	:root a.backlink {
		margin-left: 5px !important;
	}       

	.postInfo > input,
	a.hide-reply-button > span {
		display: none !important;
	}

	.fileText a {
		text-decoration: none;
	}    

	#qp {
		border: 0 !important;
		box-shadow: none !important;
	}

	.close.fa.fa-times{
	display: none !important;
	}

	#qr.dialog.has-captcha.autohide{
	box-shadow: 1px 3px 2px #161721 !important;
	border: 0px solid #6b454f !important;
	}
		
	#qr.autohide:not(:hover):not(.has-focus) {
		max-height:21px;
		transition:max-height 0.7s ease 1s;
	}

	#qr > form {
		 padding-top:4px!important;
	}

	#qr {
		background: var(--header-bg) !important;
		box-shadow: 1px 3px 2px rgba(0, 0, 0, 0.3);
		border-radius: 0px !important;
		transition: all 0.2s ease 0s;
		transition:max-height 0.4s ease;
		z-index:1!important;
	}
		
	#qr > div {
			box-shadow: 1px 3px 2px rgba(0, 0, 0, 0.2);
		}    

	#qr label input[type="checkbox"] {
		opacity: 0.4 !important;
	}
		
	#qr label input[type="checkbox"]:hover {
		 opacity: 0.7 !important;
	}    
		
	#qr .textarea textarea {
		max-height:184px;
		transition:none!important;
	}
		
	.field, #qr option:hover,
	#dump-button:hover,
	:root .field:hover,
	#menu .entry.focused,
	:root .field:focus, #file-n-submit > input {
	  background: var(--header-bg) !important;
	  border-color: var(--header-bg) !important;
	   color: var(--main-text) !important;
	}    
		
	#qr form {
		bottom: 10px !important;
		right: 0% !important;
		width: 300px !important;
		border: 1px var(--header-bg) !important;
		padding: 0px !important;
		padding-top: 0px !important;
		padding-bottom: 0px !important;
		background: var(--header-bg) !important;
		border-color: var(--post-bg) !important;
		border-radius: 0px !important;
		box-shadow: 1px 3px 2px rgba(0, 0, 0, 0.2);
	}
		
	#dump-button {
		background: var(--header-bg) !important;
		border-color: var(--post-bg) !important;
		color: var(--main-text) !important;
	}

	#qr .field, .field {
		background: var(--header-bg) !important;
		border: 1px var(--header-bg) !important;
		color: var(--main-text) !important;
	}   
		
	.replyContainer {
		padding: 10px !important;
	}
		
	.fileText {
			opacity: 0.9 !important;
			transition: .4s ease-out !important;
		}

	.file:hover .fileText, .file.image-expanded .fileText {
			opacity: 1 !important;
		}    
			
	/* info box block */    
		
	.fileText {
		margin-top: 0px !important;
		margin-left: -3px !important;
		padding: 4px !important;
		max-width: 440px !important;
	}    
	   

	/* name block */    
		
	div.post div.postInfo span.nameBlock span.name, div.post div.postInfo span.nameBlock span.postertrip, .nameBlock {
		padding: 4px !important;
		margin-left: -3px !important;
	}       
		
	.warning {
		padding: 4px !important;
		margin-left: -3px !important;
	}  
		
	/* time block */   
		
	/* replies block */    
		
	.container {
		padding: 4px !important;
		margin-left: -3px !important;
	}    
		
	/* subject block */   
		
	.subject, div.post div.postInfo span.subject {
		padding: 4px !important;
		margin-left: -3px !important;
	}    
		
	.yotsuba-b.fixed:root #custom-board-list .current {
		padding: 6px !important;
	}    
		
	#thread-stats {
		padding: 6px !important;
		margin-left: -13px !important;
	}    
		
	#watcher-link {
		padding: 6px !important;
		margin-left: -134px !important;
		opacity: 1 !important;
	}    
		
	.expand-all-shortcut {
		padding: 6px !important;
		margin-top: -3px !important;
		margin-left: -168px !important;
		display: none !important;
	}    
		
	.expand-all-shortcut {
		position: absolute !important;
	}  
		
	.contract-all-shortcut {
		margin-left: -160px !important;
		top: 10px !important;
		display: none !important;
	}    
		
	#header-bar .menu-button i {
		left: 99% !important;
		top: 2% !important;
		position: fixed !important;
		}       
		
	/*.qr-shortcut {
		display: inline-block !important;
	   background: url('https://puu.sh/k0xOW/ddda4484a4.png');
		position: fixed !important;
		top: 0 !important;
		margin-left: 1140px !important;
		margin-top: 182px !important;
	}       */
		
	/*.qr-shortcut {
		padding: 6px !important;
		margin-left: -107px !important;
		opacity: 1 !important;
		margin-top: -3px !important;
		position: absolute !important;
	}    */
			
	.settings-link {
		padding: 6px !important;
		margin-left: -80px !important;
		position: absolute !important;
	}  
		
	span.menu-button {
		padding: 5px !important;
		margin-left: -20px !important;
		height: 20px !important;
		position: absolute !important;
	}    
	   
	#custom-board-list {
		position: absolute !important;   
		margin-left: 185px !important;
		 padding: 6px !important;
		margin-top: 2px !important;
	}    
		
	.yotsuba:root .watch-thread-link, .yotsuba-b:root .watch-thread-link {
		background-image: url("https://puu.sh/kJJS7/c9d5977d6b.png") !important;
		height: 5px !important;
		width: 30px !important;
	}    
		
	#fourchanx-settings.dialog,
	fieldset legend, .yotsuba-b:root .suboption-list > div:last-of-type, .dialog:not(#qr):not(#thread-watcher):not(#header-bar), #fourchanx-settings code, kbd {
		border: none !important;
	}

	#fourchanx-settings fieldset {
		border: 0px solid;
		border-radius: 3px;
	}      
		
	/* hide notifications */

	#blotter, .globalMessage {
	   display: none !important ;
