css:
@-moz-document url-prefix("https://www.reddit.com/robin") {
 
/* GENERAL */
body {
    font-size: 10px !important;
    background-color: #282c34 !important;
}

h1 {
    color: rgba(255,255,255,1) !important;
}

h4 {
    color: rgba(255,255,255,0.5) !important;
    letter-spacing: 0.06em !important;
    margin-bottom: 10px !important;
}

/* =================================================== */
/* REDDIT TOP NAV */

/* Toolbar BG and font color */
#sr-header-area {
    background-color: rgba(255,255,255,0.2) !important;
    border: none !important;
    color: rgba(255,255,255,0.7) !important;
}

#sr-header-area a {
    background-color: rgba(255,255,255,0) !important;
    border: none !important;
    color: rgba(255,255,255,0.7) !important;
}

#sr-header-area a {
    background-color: rgba(255,255,255,0) !important;
    border: none !important;
    color: rgba(255,255,255,0.7) !important;
}

#sr-header-area a.RESShortcutsCurrentSub, #RESSubredditGroupDropdown .RESShortcutsCurrentSub a {
    color: #FF5B6D !important;
    font-weight: bold;
}

div#RESShortcutsEditContainer, .res-nightmode div#RESShortcutsSort, .res-nightmode div#RESShortcutsRight, .res-nightmode div#RESShortcutsLeft, .res-nightmode div#RESShortcutsAdd, .res-nightmode div#RESShortcutsTrash {
        background-color: rgba(255,255,255,0) !important;
    border: none !important;
    color: rgba(255,255,255,0.7) !important;
}

.header-bottom-right, #res-userbar-toggle, #res-navTop, #userbarToggle {
    background-color: #1B1F24  !important;
}

#RESShortcutsSort {
    color: rgba(255,255,255,0.5) !important;
    background-color: rgba(0,0,0,0) !important;
}

#RESShortcutsRight {
    color: rgba(255,255,255,0.5) !important;
    background-color: rgba(0,0,0,0) !important;
}

#RESShortcutsAdd {
    color: rgba(255,255,255,0.5) !important;
    background-color: rgba(0,0,0,0) !important;
}


#RESShortcutsTrash {
    color: rgba(255,255,255,0.5) !important;
    background-color: rgba(0,0,0,0) !important;
}

#RESShortcutsLeft {
    color: rgba(255,255,255,0.5) !important;
    background-color: rgba(0,0,0,0) !important;
}
.res-icon {
    color: rgba(255,255,255,0.5) !important;
    background-color: rgba(0,0,0,0) !important;
}

#header-bottom-right {
    background-color: #1B1F24 !important;
    color: rgba(255,255,255,0.5) !important;
}

#header-bottom-right a {
    background-color: #1B1F24 !important;
    color: rgba(146, 187, 252, 0.5) !important;
}

/* =================================================== */
/* BANNER AREA */
/* Robin Logo */
#header-img {
    background: url('data:image/png;base64,') no-repeat !important;
    position: relative !important;
    background-position: 20px center !important;
    
}

/* Banner */
#header {
    background-color: #282c34 !important;
    border: 0 !important;
}

#header-img {
    margin-top: 14px !important;
}

.robin-chat--header {
    background-color: #282c34 !important;
    border: 0 !important;
}

/* Chat Name Truncation */
.robin-chat .robin-chat--header h1 {
    font-size: 14px !important;
    width: 460px !important;
    white-space: nowrap !important;
    overflow: hidden !important;
    text-overflow: ellipsis !important;
    letter-spacing: 0.1em !important;
}

/* Hide Footer */
.footer-parent {
	display: none !important; 
}

/* =================================================== */
/* CHANNEL TABS */

#robinChannelList {
    width: 72%!important;
    top: 120px!important;
    margin-left: 5px !important;
}

/* =================================================== */
/* CHAT WINDOW */ 

#robinChatWindow {
    background-color: #1B1F24 !important;
}

.content, .robin-chat, .robin-chat--body, .robin-chat--main, .robinChatWindow {
    padding: 0 0 0 0 !important;
    margin: 0 0 0 0 !important;
}

.robin-message {
    line-height: 1.45em !important;
}

.robin-chat--main  {
	background-color: #282c34 !important;   
}

/* Message Color */
.robin-message--message {
   	color: rgba(255,255,255,0.5) !important; 
}

/* Time/Username/Message Column spacing */
.robin-message--timestamp {
	margin-right: 24px !important;   
}

.robin--username {
	margin-left:24px !important;   
}

/* Get rid of stuff for spacing issues */
.debuginfo {
    display:none !important;
}

.robin-chat--report {
    padding: 0 0 0 0 !important;
	display: none !important;
}

#robinUserList {
    padding: 0 0 0 0 !important;
    margin: 0 0 0 20px !important;
    border: 0 !important;
    box-shadow: none !important;
}

/* =================================================== */
/* CHAT INPUT AREA */
#robinChatInput {
    background-color: #282c34 !important;
    padding: 10px 10px 0 10px !important;
}

input[type="text"], input[type="password"], .c-form-control {
    background-color: #1B1F24 !important;
    border:0 !important;
	margin: 0 0 10px 10px !important;
    color: rgba(255,255,255,0.5) !important;
}

input[type="text"], input[type="password"], .c-form-control {
    background-color: #1B1F24 !important;
    border: 0 !important;
    margin: 0 0 10px 10px!important;
}

.robin-chat .robin-chat--vote {
    background-color: #282c34 !important;
    border:0 !important;
    box-shadow: 0px 6px 4px rgba(63, 86, 121, 0) inset !important;
    position: relative !important;
    top: 4px !important;
}

#robinChatInput {
    background-color: #282c34 !important;
    border:0 !important;
}

.chat-prepend-area {
    float: left !important;
    position: relative !important;
    width: 300px !important;
}

#chat-prepend-select, .chat-prepend-select {
    width: 60px !important;
}

.text-counter {
    margin: 0 0 0 0 !important;
    padding: 0 0 10px 0 !important;
    position: relative !important;
    top: 0px !important;
    right: -8px !important;
    width:80px !important;
    float: right !important;
}

/* Set background to your own sent messages */
.robin--user-class--self {
    background: rgba(0,0,0,0.4) !important;
    font-weight: bold;
}

/* Set background to messges mentioning you */
.res-nightmode .robin-message[style*='background: rgb(255, 162, 127)'], .robin-message[style*='background: rgb(255, 162, 127)'] {
            background-color: rgba(2550,255,255,0.4) !important;
} 

ul#robinChannelList li a:hover { 
            background-color: #ff0000 !important; 
            outline: none !important; 
        }

/* =================================================== */
/* BUTTON STYLES */

#sendBtn {
    background-color: #FF5B6D !important;
    border-radius: 40px !important;
    margin: 24px 0 60px 10px !important;
    width: 200px !important;
    color: #FFF !important;
    float:  left !important;
}

#clear-chat-button {
    border: 2px rgba(255,255,255,0.5) solid !important;
    background-color: rgba(0,0,0,0) !important;
    border-radius: 40px !important;
    margin: 24px 0 60px 0 !important;
    color: #FFF !important;
    float:  right !important;
    width: 196px !important;
    position: relative !important;
    right: -10px !important;
}

#closeBtn {
    background-color: #FF5B6D !important;
    border-radius: 40px !important;
    margin: 0 auto 30px 40px !important;
    width: 200px !important;
    color: #FFF !important;
    float:  left !important;
}

#standingsBtn {
    border: 2px rgba(255,255,255,0.5) solid !important;
    background-color: rgba(0,0,0,0) !important;
    border-radius: 40px !important;
    margin: 20px 0px 20px 60px !important;
    width: 156px !important;
    color: #FFF !important;
}

#update-script-button {
    border: 2px rgba(255,255,255,0.5) solid !important;
    background-color: rgba(0,0,0,0) !important;
    border-radius: 40px !important;
    margin: 20px 0px 30px 40px !important;
    width: 200px !important;
    color: #FFF !important;
}

#openBtn {
    background-color: #FF5B6D  !important;
    border-radius: 40px !important;
    margin: 0px 20px 20px 60px !important;
    color: #FFF !important;
    width: 160px !important;
}

#openBtn_wrap > div.robin-chat--sidebar-widget > a > div {
    background-color: #5c98ff  !important;
    border-radius: 40px !important;
    margin: 20px 0 16px 44px !important;
    color: #FFF !important;
    width: 160px !important;    
}

#standingsContent > a > div {
    border: 2px rgba(255,255,255,0.5) solid !important;
    background-color: rgba(0,0,0,0) !important;
    border-radius: 40px !important;
    margin: 0px 0px 20px 60px !important;
    width: 156px !important;
    color: #FFF !important;
}

#closeStandingsBtn {
    border: 2px rgba(255,255,255,0.5) solid !important;
    background-color: rgba(0,0,0,0) !important;
    border-radius: 40px !important;
    margin: 0px 0px 20px 60px !important;
    width: 156px !important;
    color: #FFF !important;
}

#robinVoteWidget {
	background-color: #282c34  !important;
    padding-top: 0 !important;
    margin-top: 0 !important;
    position: relative !important;
    top: -10px !important;
}

.robin-chat--buttons {
    background-color: #282c34 !important;
    border:0 !important;
    box-shadow: none !important;
}

/*
#openBtn_wrap > div.robin-chat--sidebar-widget > a > div > img {
  display: block;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  width: 15px; 
  height: 15px; 
*/

/* Replace Parrot icon in Parrot Button with Light Green Icon */
#openBtn_wrap img {
	-moz-box-sizing: border-box;
	box-sizing: border-box;
	height: 15px !important;
    width: 15px !important;
	margin: 0 !important;
    padding-right: 15px !important;
}

#openBtn_wrap > div.robin-chat--sidebar-widget > a > div {
	background-image: url(data:image/png;base64,);
    background-repeat: no-repeat !important;
    background-position: 48px 8px !important;
    background-size: 15px 15px !important;
}

/* =================================================== */
/* SIDEBAR */

.robin-chat .robin-chat--quit, .robin-chat .robin-chat--vote {
    color: rgba(255,255,255,0.5) !important;
}

.robin-chat--sidebar-widget {
	border: 0 !important;   
}

#robinVoteWidget > div:nth-child(2) > div {
    margin: 0 !important;
    padding: 0 !important;
}

#robinVoteWidget > div:nth-child(3) {
    margin: 0 !important;
    padding: 0 !important;
    text-decoration: none !important;
    font-weight: normal !important;
}

/* Userlist */
#robinUserList {
    background-color: #282c34 !important;
    border:0 !important;
    box-shadow: none !important;
    white-space: nowrap;
	overflow: hidden;
	text-overflow: ellipsis;
}


.robin-chat--sidebar {
    background-color: #282c34  !important;
}

#robinUserList > div:nth-child(1):hover {
    background-color: rgba(0,0,0,0.2) !important;
    border-right: none !important;
    border-left: none !important;
    border-bottom: none !important;
    border-top: none !important;
    color: rgba(255,255,255,0.6) !important;
    
}

.robin-room-participant:hover {
    background-color: rgba(0,0,0,0.2) !important;
    border: none !important;
    color: rgba(255,255,255,0.6) !important;
}

.parrot-user-submenu a {
    color: #83b1ff !important;
    margin: 6px 0px 4px 50px !important;
    line-height: 1.5 !important;
}

.parrot-user-submenu {
    border-top: 1px solid rgba(255,255,255,0.2) !important;
    border-right: 0 !important;
    border-left: 0 !important;
    border-bottom: 0 !important;
}

/* SCROLLBARS */

::-webkit-scrollbar {
    width: 6px;
    height: 6px;
}

::-webkit-scrollbar-track-piece {
    background: rgba(0,0,0,0);
    -webkit-border-radius: 6px;
}

::-webkit-scrollbar-thumb:vertical {
    height: 6px;
    background-color: rgba(255,255,255,0.2);
    -webkit-border-radius: 6px;
}

::-webkit-scrollbar-thumb:horizontal {
    width: 6px;
    background-color: rgba(255,255,255,0.2);
    -webkit-border-radius: 6px;
}


/* =================================================== */
/* Settings */

#settingContent {
    color: rgba(255,255,255,0.5) !important;
}

#settingContent li {
    padding: 8px 8px 8px 0 !important;
}

#settingContent ul {
    padding: 10px 0 0 0 !important;
}

/* =================================================== */
/* TEXT COLOR SCHEME FOR CHAT */
.robin--flair-class--flair-0 .robin--username {
    color: #ff5b6d !important;
}

.robin--flair-class--flair-2 .robin--username {
    color: #ffc35c !important;
}

.robin--flair-class--flair-3 .robin--username {
    color: #5cff72 !important;
}

.robin--flair-class--flair-4 .robin--username {
    color: #5c98ff !important;
}

.robin--flair-class--flair-5 .robin--username {
    color: #9a5cff !important;
}





}
