css:
@-moz-document url-prefix("http://bf.mine.nu/") {

body {
 color: #ACB1BC;
 background-color: #000000;
 font-family: verdana,tahoma,helvetica;
 scrollbar-face-color:#444D57;
scrollbar-highlight-color:#535D68;
scrollbar-3dlight-color:#49535D;
scrollbar-darkshadow-color:#000000;
scrollbar-shadow-color:#49535D;
scrollbar-arrow-color:#FFFFFF;
scrollbar-track-color:#48515B;
}

body, td { font-size: 8pt; }

body a:link, body a:visited, body a:active {
 color: #BDC2CC;
 text-decoration: underline;
 
}
body a:hover {
 color: #FF9900;
 text-decoration: none;
 
}


.logobackground {
 
}



.mainpage {
 background-image: url(http://i.imgur.com/VxYVE1J.jpg);
}



.tableoutborder {
 background-color: #000000;
}



.tableinborder {
 background-color: #FFF8DC;
}

.threadline {
 color: #000000;
}


.tabletitle {
 color: #C0CCCC;
 background-color: #464F59;
 
border-left: 1px  #5B6771 solid;
border-top: 1px  #5B6771 solid
}


.tabletitle_fc {
 color: #E8ECF3;
}


.inposttable {

 border-left: 1px  #777E86 solid;
border-top: 1px  #777E86 solid;
}

.tabletitle a:link, .tabletitle a:visited, .tabletitle a:active { 
 color: #E8ECF3;
 text-decoration: none;
 
}
.tabletitle a:hover { 
 color: #FF9900;
 text-decoration: underline;
 
}

.smallfont {
 font-size: 11px;
 font-family: verdana,tahoma,helvetica;
 
 
}

.normalfont {
 font-size: 12px;
 font-family: verdana,tahoma,helvetica;
 
 
}

.tablecat {
 color: #E8ECF3;
 background-color: #4A525B;
 
border-left: 1px  #67727B solid;
border-top: 1px  #67727B solid
}


.tablecat_fc {
 color: #E8ECF3;
}


.tablecat a:link, .tablecat a:visited, .tablecat a:active {
 color: #E8ECF3;
 text-decoration: none;
 
}
.tablecat a:hover { 
 color: #FF9900;
 text-decoration: none;
 
}

.tableb {
 color: #ACB1BC;
 background-image: url(http://i.imgur.com/gf3xXcX.png);
 border-left: 1px  #5B6771 solid;
 border-top: 1px  #5B6771 solid;
 text-align: left;
}


.tableb_fc {
 color: #ACB1BC;
}


.tableb a:link, .tableb a:visited, .tableb a:active { 
 color: #BDC2CC;
 text-decoration: underline;
 
}
.tableb a:hover { 
 color: #FF9900;
 text-decoration: none;
 
}

.tablea {
 color: #ACB1BC;
 background-image: url(http://i.imgur.com/gf3xXcX.png);
 border-left: 1px  #E53300;
 border-top: 1px   #E53300;
 text-align: left;
}


.tablea_fc {
 color: #ACB1BC;
}


.tablea a:link, .tablea a:visited, .tablea a:active {
 color: #BDC2CC;
 text-decoration: underline;
 
}
.tablea a:hover { 
 color: #FF9900;
 text-decoration: none;
 
}

.prefix {
 color: #cc0000;
 
 
 
}

.time {
 color: #9199A7;
 
 
 
}

.highlight {
 color: red;
 font-weight: bold;
 
 
}

select {
 font-size: 10px;
 font-family: verdana,tahoma,helvetica;
 color: #ACB1BC;
 background-color: #535C66;
 
}

textarea {
 font-size: 10px;
 font-family: verdana,tahoma,helvetica;
 color: #ACB1BC;
 background-color: #535C66;
 
}

input {
 font-size: 10px;
 font-family: verdana,tahoma,helvetica;
 color: #ACB1BC;
 background-color: #535C66;
 border:1px solid #000000;
 border-top-width : 1px;
 border-right-width : 1px;
 border-bottom-width : 1px;
 border-left-width : 1px;
 text-indent : 2px;
}

.publicevent {
 color: red;
 
}

.privateevent {
 color: blue;
 
}

.obenmitte {

}

.untenmitte {

}

.topbuttons { 
}

form {
 padding:0px;
 margin:0px;
}

pre {
 padding:0px;
 margin:0px;
}
.ucuser          { color: #ACB1BC; }
.ucpoweruser     { color: #339999; }
.ucextremeuser     { color: #CC99CC; }
.ucvip           { color: #FF50FF; }
.ucuploader      { color: #00C8FF; }
.ucmoderator     { color: #00DC00; }
.ucadministrator { color: #FFB400; }
.ucguteam   { color: #8B008B; }
.ucsupermod      { color: #9999ff; }
.ucsysop         { color: #FF3338; }

.ol_fontClass {
padding: 1px;
color: #000000;
}

.ol_capfontClass {
padding: 1px;
font-family: Arial, sans-serif; font-size: 9px;
font-weight: bold;
}

.ol_capfontClass A {
font-size: 9px;
}

.ol_fgClass {
background-color: #FFFFCC;
}

.ol_bgClass {
border: 2px solid #444444;
background-color: #FFFFCC;
}
}
