css:
@-moz-document url-prefix(https://calendar.google.com/) {
/* ---------------
  name: Google Calendar Material
  author: @Bal치zs Orb치n
  e-mail: info@balazsorban.com
  matches: https://calendar.google.com*
  version: 3.1
  ------------------ */

#bottomcontainerBorderwk, ::-webkit-scrollbar, .tg-nowptr, .gb_Vb, #gb#gb span.gb_P, #navfooter, #mg-more, .date-top, .qnb-qab, .qnb-container .goog-imageless-button-content, .trans-strip, .applogo, .wk-dummyth, .bottom-prong, .neb-rb-default, .delete-button {
	display: none;
}
/* ---------------
		NAVBAR
------------------ */

#onegoogbar {
	display: flex;
	position: fixed;
	top: 1vw;
	z-index: 1001;
}
.gb_xf {
	min-width: 0
}
/*Navbar logo*/

.gb_Sb {
	left: -24px;
	background: url(https://lh3.googleusercontent.com/Ixidy5j7AE_l59ZiQgMHEjMfThn8u_yPdI5FddUWcPaPBFqHOi3UwVr9nC2qM4gDPK82mQvw=s50-h50-e365-rw) center no-repeat;
	background-size: 42px;
	min-width: 36px;
	max-width: 36px;
	cursor: pointer
}
/*Navbar background*/

.gb_Ce {
	width: 0;
	background-color: transparent
}
/*SEARCHBAR*/

#gbqfw {
	position: fixed;
	width: 13vw;
	left: 6vw;
	z-index: 1
}
#gbqfw * {
	border: none;
}
#gbqfw div {
	background: transparent;
}
#gbqfqwb {
	background: rgba(255, 255, 255, .9) !important;
	border-top-left-radius: 5px;
	border-bottom-left-radius: 5px;
}
#onegoogbar input {
	margin-left: -2px !important;
	border-radius: 5px;
	padding: 2px !important;
	background: #e0e0e0 !important
}
/*Background image*/

#vr-nav {
	top: 0;
	position: fixed;
	width: 100vw;
	height: 100vh;
	background-image: linear-gradient(to bottom, rgba(0, 0, 0, 0.5) 0%, transparent 20%);
	background-size: cover;
	background-position: center;
	background-repeat: no-repeat;
	margin: 0
}
/*Buttons*/

.clstMenu, .zippy-arrow {
	background-image: url('data:image/svg+xml;base64,PHN2ZyBmaWxsPSIjMDAwMDAwIiBoZWlnaHQ9IjQ4IiB2aWV3Qm94PSIwIDAgMjQgMjQiIHdpZHRoPSI0OCIgeG1sbnM9Imh0dHA6Ly93d3cudzMub3JnLzIwMDAvc3ZnIj4KICAgIDxwYXRoIGQ9Ik03LjQxIDcuODRMMTIgMTIuNDJsNC41OS00LjU4TDE4IDkuMjVsLTYgNi02LTZ6Ii8+CiAgICA8cGF0aCBkPSJNMC0uNzVoMjR2MjRIMHoiIGZpbGw9Im5vbmUiLz4KPC9zdmc+');
	background-position: center !important;
	background-size: 16px
}
.goog-imageless-button, .navbuttonouter {
	border: none;
	margin: 0
}
/*Date chooser*/

#mainnav {
	position: fixed;
	top: 4.2vh;
	right: 5vw;
	background: transparent;
}
#topnav-container {
	display: flex
}
#topnav-container div {
	padding: 0;
	border-radius: 0;
	box-shadow: none;
	margin-left: 0;
}
#topnav-container .goog-imageless-button {
	color: white;
	background: none
}
#mg-settings {
	position: absolute;
	right: -62px;
	padding: 0
}
#mg-settings .goog-imageless-button-outer-box {
	background-image: url('data:image/svg+xml;base64,');
	background-size: 24px;
	background-position: center;
	opacity: 1
}
#topnav-container td {
	padding: 0
}
#topLeftNavigation tr {
	display: flex;
}
.date-nav-prev {
	order: -1;
}
.date-nav-prev > div, .date-nav-next > div {
	background: transparent
}
.date-nav-prev > div:hover, .date-nav-next > div:hover {
	background: rgba(255, 255, 255, .5)
}
.navBack, .navForward {
	background-position: center;
	background-size: 24px;
	background-repeat: no-repeat;
	opacity: 1
}
.navBack {
	background-image: url('data:image/svg+xml;base64,PHN2ZyBmaWxsPSIjRkZGRkZGIiBoZWlnaHQ9IjQ4IiB2aWV3Qm94PSIwIDAgMjQgMjQiIHdpZHRoPSI0OCIgeG1sbnM9Imh0dHA6Ly93d3cudzMub3JnLzIwMDAvc3ZnIj4KICAgIDxwYXRoIGQ9Ik0xNS40MSAxNi4wOWwtNC41OC00LjU5IDQuNTgtNC41OUwxNCA1LjVsLTYgNiA2IDZ6Ii8+CiAgICA8cGF0aCBkPSJNMC0uNWgyNHYyNEgweiIgZmlsbD0ibm9uZSIvPgo8L3N2Zz4=')
}
.navForward {
	background-image: url('data:image/svg+xml;base64,PHN2ZyBmaWxsPSIjRkZGRkZGIiBoZWlnaHQ9IjQ4IiB2aWV3Qm94PSIwIDAgMjQgMjQiIHdpZHRoPSI0OCIgeG1sbnM9Imh0dHA6Ly93d3cudzMub3JnLzIwMDAvc3ZnIj4KICAgIDxwYXRoIGQ9Ik04LjU5IDE2LjM0bDQuNTgtNC41OS00LjU4LTQuNTlMMTAgNS43NWw2IDYtNiA2eiIvPgogICAgPHBhdGggZD0iTTAtLjI1aDI0djI0SDB6IiBmaWxsPSJub25lIi8+Cjwvc3ZnPg==')
}
#topLeftNavigation, #topRightNavigation {
	margin: 0;
	position: static;
	left: auto;
	right: auto
}
#topLeftNavigation *:hover, #topRightNavigation *:hover {
	border: none
}
#topLeftNavigation .goog-imageless-button-content, #topRightNavigation .goog-imageless-button-content {
	padding: 0 26px
}
.date-nav-today .goog-imageless-button-content {
	background-image: url('data:image/svg+xml;base64,');
	margin-right: -16px
}
#topRightNavigation .button-strip .goog-imageless-button, .date-nav-today .goog-imageless-button-content {
	background-size: 24px;
	background-repeat: no-repeat;
}
#topRightNavigation .button-strip .goog-imageless-button:nth-child(2) {
	background-image: url('data:image/svg+xml;base64,');
	margin-left: 16px
}
#topRightNavigation .button-strip .goog-imageless-button:nth-child(3) {
	background-image: url('data:image/svg+xml;base64,')
}
#topRightNavigation .button-strip .goog-imageless-button:nth-child(4), #topRightNavigation .button-strip .goog-imageless-button:nth-child(5) {
	background-image: url('data:image/svg+xml;base64,')
}
#topRightNavigation .button-strip .goog-imageless-button:last-child {
	background-image: url('data:image/svg+xml;base64,');
}
/*New event*/

.qnb-container {
	position: fixed;
	bottom: 10vh;
	right: 6vw;
	transform: translate(-50% -50%);
	border-radius: 50%;
	box-shadow: 0 0 4px rgba(0, 0, 0, .14), 0 4px 8px rgba(0, 0, 0, .28);
}
.qnb-container .goog-imageless-button {
	padding: 0;
	width: 56px;
	height: 56px;
	border-radius: 50%;
	background-image: url(data:image/svg+xml;base64,PHN2ZyBmaWxsPSIjRkZGRkZGIiBoZWlnaHQ9IjQ4IiB2aWV3Qm94PSIwIDAgMjQgMjQiIHdpZHRoPSI0OCIgeG1sbnM9Imh0dHA6Ly93d3cudzMub3JnLzIwMDAvc3ZnIj4KICAgIDxwYXRoIGQ9Ik0xOSAxM2gtNnY2aC0ydi02SDV2LTJoNlY1aDJ2Nmg2djJ6Ii8+CiAgICA8cGF0aCBkPSJNMCAwaDI0djI0SDB6IiBmaWxsPSJub25lIi8+Cjwvc3ZnPg==);
	background-size: 50% 50%;
	background-repeat: no-repeat;
	background-position: center;
	background-color: #db4437 !important;
	border: none
}
.qnb-container .goog-imageless-button:hover {
	transform: rotate(90deg);
	transition: 0.3s
}
/*Sidebar*/

#nav {
	left: 1.5vw;
	max-height: 75vh;
	min-height: 75vh;
	overflow: scroll;
	top: 15vh;
	width: 14%;
	z-index: 1;
	background: white;
	padding: 10px 20px;
	border-radius: 5px;
	box-shadow: 0 0 15px rgba(0, 0, 0, .3);
}
#nav * {
	border: none;
	background-color: transparent;
}
.goog-zippy-expanded .monthtableBody {
	padding: 0
}
#dp_h, #dp_0, #clst_my, #clst_fav, .calList {
	position: relative;
	width: 92.5%;
	margin-left: 7.5%
}
.dp-weekday, .dp-weekend, .dp-with-events {
	width: 2.5em;
	line-height: 2.5em
}
.dp-today-selected, .dp-today, .dp-onhover {
	line-height: 2.5em;
	width: 2.5em;
	color: white;
	background-color: #4285f4 !important;
	border-radius: 50%;
	border: none;
	box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.1);
}
/*Weekdays*/

.wk-weektop, .wk-allday {
	margin-top: -24px;
	background: transparent;
}
.wk-dayname {
	border-top: none;
	font-size: 1.8em;
	font-weight: bold;
	color: white;
}
.wk-today {
	font-size: 2em;
	color: #444;
}
/*Main Calendar*/

#gridcontainer {
	position: absolute;
	top: 15vh;
	right: 3vw;
	width: 73vw;
	height: 77vh !important;
	border-radius: 5px;
	box-shadow: 0 0 15px rgba(0, 0, 0, .3);
	background-color: white
}
#gridcontainer * {
	background-color: transparent;
	/*font-weight: bold;*/

	border-top: none;
}
/*Reminders*/

.st-c .te, .st-c .rb-n {
	padding: .5rem 0;
}
.rb-ni {
	text-align: center;
	font-size: 1.2em;
	color: white;
	font-weight: bold
}
/*Table texts*/

.chip dd, .chip dt {
	color: #fff;
	font-weight: bold;
}
.chip dt, .chip dd {
	padding: .5rem 0 0 .5rem;
}
.chip dd {
	text-shadow: 0 0 1px black;
	order: -1
}
.cbrd {
	display: flex;
	flex-direction: column;
	left: 0;
	padding: 0;
	margin: 0 5px !important;
	background-image: linear-gradient(rgba(0, 0, 0, .6), transparent 40%);
	background-size: cover;
	background-position: center center;
	background-repeat: no-repeat;
}
.chip-color, .chip-border, .tg-time-pri, .t5-time-sec, .tg-times-pri, .tg-times-sec, .mask-top, .mask-left, .mask-bottom, .mask-right, .chip dl, #weekViewAllDayBgwk, .wbkt .tg-timedevents, #searchAddCal, .st-c-pos div, .st-bg, .wk-allday .st-bg-next, .wk-allday .st-bg-today, .wk-allday .st-bg-td-first, .wk-allday .st-bg-td-last, .wk-dayname.wk-today, .wk-dayname.wk-tomorrow, #vr-nav {
	border: none !important;
}
.mask-top, .mask-left, .mask-bottom, .mask-right, .wk-dayname.wk-today, .wk-allday .st-bg-today {
	background: transparent !important;
}
/*Events*/

.chip dl {
	border-radius: 0.3rem;
	min-height: 70px !important;
}
.hin-bubble .hin-ic, .cic-hin {
	background: url(data:image/svg+xml;base64,PHN2ZyBmaWxsPSIjRkZGRkZGIiBoZWlnaHQ9IjQ4IiB2aWV3Qm94PSIwIDAgMjQgMjQiIHdpZHRoPSI0OCIgeG1sbnM9Imh0dHA6Ly93d3cudzMub3JnLzIwMDAvc3ZnIj4KICAgIDxwYXRoIGQ9Ik0wIDBoMjR2MjRIMHoiIGZpbGw9Im5vbmUiLz4KICAgIDxwYXRoIGQ9Ik0xNC40IDZMMTQgNEg1djE3aDJ2LTdoNS42bC40IDJoN1Y2eiIvPgo8L3N2Zz4=) no-repeat;
	background-size: 12px;
}
/*Event bubble*/

.bubblemain {
	background: linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6));
	background-size: 100%;
	background-position: bottom center;
}
.bubble.has-bottom-prong {
	border-radius: 5px
}
.bubble, .hin-bubble .title-text, .hin-bubble .when {
	box-shadow: none;
	color: white;
	border: none;
}
.bubbleclose {
	background: url(data:image/svg+xml;base64,) center center;
	padding: 8px;
	background-size: 80%
}
.neb-header .ui-sch-schmedit {
	color: white
}
.details-button, .details-button:hover, .edit-btn, .edit-btn:hover, .done-btn, .done-btn:hover, .delete-btn, .delete-btn:hover {
	background: url(data:image/svg+xml;base64,) no-repeat center center;
	background-size: auto 50%;
	padding: 0;
	border: 0;
	border-radius: 50%;
	background-color: #4285f4;
	position: absolute;
	bottom: 10px;
	right: 10px;
	font-size: 0;
	min-width: 40px !important;
	height: 40px !important;
	transition: 0.2s
}
.done-btn, .done-btn:hover {
	background-image: url(data:image/svg+xml;base64,PHN2ZyBmaWxsPSIjRkZGRkZGIiBoZWlnaHQ9IjQ4IiB2aWV3Qm94PSIwIDAgMjQgMjQiIHdpZHRoPSI0OCIgeG1sbnM9Imh0dHA6Ly93d3cudzMub3JnLzIwMDAvc3ZnIj4KICAgIDxwYXRoIGQ9Ik0wIDBoMjR2MjRIMHoiIGZpbGw9Im5vbmUiLz4KICAgIDxwYXRoIGQ9Ik05IDE2LjJMNC44IDEybC0xLjQgMS40TDkgMTkgMjEgN2wtMS40LTEuNEw5IDE2LjJ6Ii8+Cjwvc3ZnPg==);
}
.edit-btn, .edit-btn:hover {
	right: 60px
}
.delete-btn, .delete-btn:hover {
	background-color: #db4437;
	left: 20px;
	right: auto;
	background-image: url(data:image/svg+xml;base64,)
}
.delete-btn:hover {
	background-color: #e06e55
}
.neb-footer {
	margin: 0;
	width: 100%;
	background-color: white;
	position: absolute;
	left: 0;
	top: 100%;
	box-sizing: border-box;
	border-bottom-left-radius: 5px;
	border-bottom-right-radius: 5px;
}
.neb-bold-action {
	padding-left: 10px;
}
/*Now marker*/

#tgnowmarker {
	display: none;
	width: 100%;
	z-index: 1000
}
/* CREATE EVENT*/

.ep-ea, .ep-title, .ep-title .textinput {
	margin: 0;
	border: none;
	background: #4285f4
}
.ep-title {
	padding-left: 0
}
.ep-title .textinput {
	width: 100%;
	color: white;
	text-align: center;
	padding: 10px;
}
.neb-data {
	display: block;
	visibility: visible
}
.ep-title:before {
	position: relative;
	top: 26px;
	color: white;
	padding-left: 10px;
	content: 'Enter title, times, people, places...'
}
}
