css:
@-moz-document url-prefix("https://www.roblox.com"), url-prefix("http://www.roblox.com"), url-prefix("https://web.roblox.com"), url-prefix("http://web.roblox.com") {
body {
    font-family:"Source Sans Pro";
}

body,.text,pre {
    font-family:"Source Sans Pro";
}

.friend,.friend-name {
    font-family:"Source Sans Pro";
}

h1,h2,h3,h4,h5 {
    font-family:"Source Sans Pro";
}

.rbx-header {
    background-color:#00AAFF;
}

.chat-main .chat-header {
    background-color:#00AAFF;
}

.chat-container {
    box-shadow:0 2px 5px rgba(20,20,20,0.8);
}

.chat-search {
    border:0px;
}

.icon-default-bc,.icon-bc,.icon-tbc,.icon-obc {
    top:110px !important;
}

.profile-avatar-right {
    background-color:#00AAFF;
}

.slide-switcher.games {
    background-color:#00AAFF;
}

.btn-secondary-xs {
    background-color:#000000;
    border-color:#111111;
}

.btn-secondary-xs:hover {
    background-color:#222222;
    border-color:#333333;
}

.friend .friend-avatar img {
    border:0px;
}

.container-main .game .game-item,.container-main .game .card-item,.container-main .card .game-item,.container-main .card .card-item {
	box-shadow:0px 5px 10px rgba(0,0,0,0.8);
    border-radius:10px;
}

.cash-out-progress .bar-base .bar {
    background-color:#00AAFF;
}

#CashOutWidget .graphic-green {
    background-color:#00AAFF;
}

.profile-header .profile-avatar-image .avatar-image-link {
    border:0px;
}

.profile-container .asset-item span[title] {
    border:0px;
}

.text-footer {
    color:#111111;
}

body.banner {
    display:none;
}

.profile-ads-container {
    display:none;
}

#GamesPageRightColumnSidebar {
    display:none;
}

.abp {
    display:none;
}

.games-page-left {
    margin-right: 10px;
}

.section-content {
    box-shadow:0px 2px 5px rgba(0,0,0,0.8);
    border-radius:10px;
}

.rbx-tabs-horizontal .nav-tabs {
    box-shadow:0px 2px 5px rgba(20,20,20,0.7);
    border-radius:10px;
}

.chat-friend-list .chat-friend-avatar {
    border:0px;
}

.btn-neutral,.btn-neutral:link,.btn-neutral:active,.btn-neutral:visited {
    border-color:#0852B7;
    background-color:#0852B7;
    background-image:url(https://www.roblox.com/images) !important;
    border-radius:5px;
}

.btn-neutral:hover {
    border-color:#2A74D9;
    background-color:#2A74D9;
}

.btn-primary,.btn-primary:link,.btn-primary:active,.btn-primary:visited {
    border-color:#02B755;
    background-color:#02B757;
    background-image:url(https://www.roblox.com/images) !important;
    border-radius:5px;
}

.simplemodal-container .btn-primary, .simplemodal-container .btn-primary:link, .simplemodal-container .btn-primary:active, .simplemodal-container .btn-primary:visited {
    border-color:#02B755;
    background-color:#02B757;
    background-image:url(https://www.roblox.com/images) !important;
    border-radius:5px;
}

.btn-disabled-primary, .btn-disabled-primary:link, .btn-disabled-primary:active, .btn-disabled-primary:visited {
    border-color:#99C699;
    background-color:#99C699;
    background-image:url(https://www.roblox.com/images) !important;
    border-radius:5px;
}

.btn-primary:hover {
    border-color:#3FC679;
    background-color:#3FC679;
}

.simplemodal-container:hover .btn-primary:hover {
    border-color:#99C699;
    background-color:#99C699;
}

.btn-disabled-primary:hover {
    border-color:#99C699;
    background-color:#99C699;
}

.GenericModalButtonContainer .btn-primary,.GenericModalButtonContainer .btn-primary:link,.GenericModalButtonContainer .btn-primary:active,.GenericModalButtonContainer .btn-primary:visited {
    border-color:#02B755;
    background-color:#02B757;
    background-image:url(https://www.roblox.com/images) !important;
    border-radius:5px;
}

.GenericModalButtonContainer:hover .btn-primary:hover {
    border-color:#3FC679;
    background-color:#3FC679;
}

.GenericModalButtonContainer .btn-negative,.GenericModalButtonContainer .btn-negative:link,.GenericModalButtonContainer .btn-negative:active,.GenericModalButtonContainer .btn-negative:visited {
    border-color:#565656;
    background-color:#565656;
    background-image:url(https://www.roblox.com/images) !important;
    border-radius:5px;
}

.simplemodal-container .btn-negative, .simplemodal-container .btn-negative:link, .simplemodal-container .btn-negative:active, .simplemodal-container .btn-negative:visited {
    border-color:#565656;
    background-color:#565656;
    background-image:url(https://www.roblox.com/images) !important;
    border-radius:5px;
}

.GenericModalButtonContainer:hover .btn-negative:hover {
    border-color:#787878;
    background-color:#787878;
}

.simplemodal-container:hover .btn-negative:hover {
    border-color:#787878;
    background-color:#787878;
}

.btn-control.btn-control-large {
    border-color:#B8B8B8;
    background-color:#FFFFFF;
    background-image:url(https://www.roblox.com/images) !important;
    border-radius:5px;
}

.btn-control.btn-control-medium {
    border-color:#B8B8B8;
    background-color:#FFFFFF;
    background-image:url(https://www.roblox.com/images) !important;
    border-radius:5px;
}

.btn-control.btn-control-small {
    border-color:#B8B8B8;
    background-color:#FFFFFF;
    background-image:url(https://www.roblox.com/images) !important;
    border-radius:5px;
}

.btn-control,.btn-control:active,.btn-control:link,.btn-control:visited,.btn-control:hover {
    border-color:#969696;
    background-color:#DDDDDD;
}

.btn-control.btn-control-large:hover,.btn-control,.btn-control:active,.btn-control:link,.btn-control:visited,.btn-control:hover {
    border-color:#969696;
    background-color:#DDDDDD;
}

.btn-control.btn-control-medium:hover,.btn-control,.btn-control:active,.btn-control:link,.btn-control:visited,.btn-control:hover {
    border-color:#969696;
    background-color:#DDDDDD;
}

.btn-control.btn-control-small:hover,.btn-control,.btn-control:active,.btn-control:link,.btn-control:visited,.btn-control:hover {
    border-color:#969696;
    background-color:#DDDDDD;
}

.roblox-message-large-btn {
    border-color:#DADADA;
    background-color:#FFFFFF;
    background-image:url(https://www.roblox.com/images) !important;
    border-radius:5px;
}

.GroupControlsBox input,.GroupControlsBox button {
    border-color:#DADADA;
    background-color:#FFFFFF;
    background-image:url(https://www.roblox.com/images) !important;
    border-radius:5px;
}

button {
    border:1px outset buttonface
}

input[type="button"],input[type="submit"],input[type="reset"],input[type="file"]::-webkit-file-upload-button,button {
    border-color:#DADADA;
    background-color:#FFFFFF;
    border-radius:5px;
    border:1px outset buttonface;
}

input[type="button"]:hover,input[type="submit"]:hover,input[type="reset"]:hover,input[type="file"]::-webkit-file-upload-button:hover,button:hover {
    border-color:#B8B8B8;
    background-color:#DDDDDD;
    border-radius:5px;
}

.game-main-content .game-play-buttons {
    border:0px;
}

img.original-image {
    border:0px;
}

.SmallCatalogItemView:hover .CatalogItemInner {
    box-shadow:0px 5px 10px rgba(0,0,0,0.8);
    border-radius:10px;
}

.pager.next {
	background-image:url(https://www.roblox.com/images/NextStyleGuide/generic_03282016.svg);
    background-position:0px -392px;
}

.pager.next:hover {
    background-position:1px -952px;
}

.pager.previous {
    background-image:url(https://www.roblox.com/images/NextStyleGuide/generic_03282016.svg);
    background-position:0px -420px;
}

.pager.previous:hover {
    background-position:1px -980px;
}

.pager.disabled,.pager.disabled:hover {
    background-position:0px 27px;
}

.store-card .store-card-caption {
	border:0px;
}

.divider-top {
    border:0px;
}

.rbx-modal-dialog .modal-content {
	border-radius:10px;
}

.rbx-modal-dialog .modal-content .modal-header {
    border:0px;
    font-weight:bold !important;
}

.profile-accoutrements-container .profile-accoutrements-slider .accoutrement-item a,.accoutrement-image {
    border-radius:5px;
}

.profile-header .profile-avatar-image .profile-avatar-status {
    right:-5px;
    top:104px;
}

.profile-header .profile-avatar-image .avatar-image-link,.profile-header .profile-avatar-image .avatar-image-link img {
    border-radius:55px;
}

.unifiedModal {
    border:0px;
    box-shadow:0 5px 15px rgba(0,0,0,0.5);
    border-radius:10px;
}

.text-footer-nav, .text-footer-nav:link, .text-footer-nav:visited, .text-footer-nav:active {
    color:#555555;
}

.text-footer-nav:hover {
    color:#888888;
}

.rbx-divider {
    display:none;
}
}
