css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('https?://addons\\.mozilla\\.org/(?![-A-Za-z]+/(firefox|thunderbird)/compatibility/reporter/.+).*') {

  
  .header-bg {
    background-color: #414547; 
	}
	
	.addon-details .header-bg, .home .header-bg {
    background-image: linear-gradient(to bottom,transparent 0,transparent 100px),linear-gradient(to bottom,rgba(12,153,213,0) 0,#658e9f 500px,#5f8fa4 0),repeating-linear-gradient(118deg,rgba(12,153,213,0) 0,#4a8ca2 550px,rgba(12,153,213,0) 550px,#4d8fa5 800px,rgba(12,153,213,0) 800px,#4f8192 950px);
	
	}
	
	
   #masthead h1 a {
    color: rgba(255, 255, 255, 0.58);
}


#tabzilla a {
    
    background-image: none;
	
	}
	
	.header-search #search-q, .header-search .search-button {
    height: 30px;
    background-color: RGBA(255, 255, 255, 0.55);
    color: rgba(0, 0, 0, 0.78);
	
	}
	
	.restyle .previews {
    background: #c0c0c0;
	
	}
	
	#main-wrapper {
    background: #ccc;
}

body.restyle {
    color: RGBA(15, 83, 141, 0.6)  ;
	
	}
	
.prose strong, .prose, .prose em, .prose i, .prose li {
    color: RGBA(51, 51, 51, 0.93) !important;
    line-height: 1.41em;
	
	}
	
	
	#footer a:link, #footer a:visited, .hovercard a h3, .secondary li a {
    color: rgba(15, 111, 182, 0.78);
	
	}
	
.more-info, .more-info:active, .more-info:focus, .more-info:hover, .more-info:link, .more-info:visited, a, a:active, a:focus, a:hover, a:link, a:visited {
    color: rgba(0, 87, 143, 0.8);
}


* {
    font-family: 'Open Sans',X-LocaleSpecific,sans-serif !important;
    color: rgba(0, 72, 119, 0.91);
}



.secondary h2, .secondary h3 {
    color: rgba(86, 84, 84, 0.81);
	
	}
	
	
	#footer a:link, #footer a:visited, .hovercard a h3, .secondary li a {
    color: rgba(0, 61, 140, 0.85);
}


.stars {
    
    background: url(data:image/png;base64,) no-repeat left top;
	
}


#footer {
    background-color: rgba(1, 35, 74, 0.93);
    background-image: linear-gradient(to bottom,#ccc 0,#90a5b4 123px,rgba(119, 119, 119, 0.52) 123px,rgba(80, 119, 177, 0.2) 200px,#c6c6c6 200px,rgba(12,153,213,0) 400px);
    color: #666;
    font-family: inherit;
    font-style: normal;
}


#footer #lang_form label {
    color: rgb(3, 100, 167);
	
}	

  a, abbr, acronym, address, applet, article, aside, audio, b, big, blockquote, body, canvas, caption, center, cite, code, dd, del, details, dfn, div, dl, dt, em, embed, fieldset, figcaption, figure, footer, form, h1, h2, h3, h4, h5, h6, header, hgroup, html, i, iframe, img, ins, kbd, label, legend, li, mark, menu, nav, object, ol, output, p, pre, q, ruby, s, samp, section, small, span, strike, strong, sub, summary, sup, table, tbody, td, tfoot, th, thead, time, tr, tt, u, ul, var, video {
  
    color: rgba(248, 248, 248, 0.8);
  
  }
  
  .addon-details h2 {
    color: #016fab;
    font-size: 1.75rem;
}
	
  
	.secondary li {
    color: rgba(1, 105, 171, 0.88);
}
	
	#reviews .review h3 b:not(:empty), #reviews .review h4 b:not(:empty) {
       color: rgba(3, 96, 164, 0.9);
}
	
	
	.gutter .secondary, .html-rtl .secondary {
      background: rgba(179, 179, 179, 0.7);
}
	

	#reviews .review .byline {
        color: #606060;
	
	}
	
		
	.paginator p {
       font-size: 13px;
    color: RGBA(1, 8, 87, 0.8);
}
	
	
	
 

b {
    
    color: RGB(15, 28, 99) ;
}
	
	
	
	#social-footer ul li b {
       color: #90A4BD;
}
	
 .stars.large {
  
    background: url(data:image/png;base64,) no-repeat left top;
}
 
	
	.primary, .secondary {
    background: rgba(147, 147, 147, 0.49);
}
	

	.listing .item p {
    font-size: 13px;
    color: rgba(0, 0, 0, 0.76);
}
	
#sorter h3, .island h2, .primary h1 {
       color: rgba(38, 56, 66, 0.9);	
}

.toplist small {
    color: #292929;
}

.hovercard .category, .hovercard .vital {
    color: #3B3B3B;
}

.version .meta, .version .source {
    color: #1B1B1B !important;
 
}


.listing .warning {
    background-color: #8BA4AE !important;
    
}

.listing .item .desc {
    color: #353535 !important;
    font-size: 1.02rem;
}
  
  h3 a {
    color: #515151;

}

.secondary .categories li a, .secondary .other-categories li a {
    color: #044471 !important;
    font-size: 13px;
    font-weight: 500 !important;

}

.personas-grid {
    background: #bababa;
}
 
  .prose > ol:first-child, .prose > p:first-child, .prose > ul:first-child {
    
    color: black !important;
}



.modal p, .primary p {
    color: #515151;
}

.version .filesize {
    color: #192984 !important;
}

code  {
    
    color: black !important;
}

.beta-version, time  {
    
    color: black !important;
}
  
.version .source li, #install-beta > p:nth-child(1) > strong:nth-child(1) {
    
    color: #630b0b;
}

body.file-viewer #files #files-tree a > span {
    border-radius: 0;
    color: #272727;
}


#file-viewer, #file-viewer table tr td, #files-wrapper, div.featured-inner {
    background: #e6e6e6;
}

label {
    color: rgba(0, 0, 0, 0.67) !important;
}

a.selected > span {
	background-color: RGBA(173, 199, 239, 0.79) !important;
    
}


#content-wrapper > div:nth-child(1) > p:nth-child(2) {
    color: rgba(0, 0, 0, 0.67) !important;
}

#reviews {
    background: rgba(128, 128, 128, 0.09);
}  
  
 .addon-details .widgets {          /*  Ajoute Favoris et Collection */
    display: initial !important; 
} 
    
 
}
