css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://steamcommunity.com/id/"),
url-prefix("http://steamcommunity.com/profiles/") {
    /*body*/
    
    body {
        font-family: Arial, Helvetica, Verdana, sans-serif;
        font-size: 0.75em !important;
        background-color: #2D2D2B !important;
        background-image: url("http://web.archive.org/web/20120818164507/http://cdn.steamcommunity.com/public/images/skin_1/bg.gif") !important;
        background-position: 0px 0px !important;
        background-repeat: repeat-x !important;
        color: #939393 !important;
        margin: 0px !important;
        padding: 0px !important;
    }
    div.profile_page {
        background: none !important;
    }
    /*header1*/
    
    div#global_header {
        background-image: url("http://storage3.static.itmages.ru/i/15/0907/h_1441662829_2399661_fa1efca70b.jpg") !important;
        border-bottom: 1px solid #4D4B48 !important;
    }
    div#global_header .content {
        background: none !important;
    }
    /*header2*/
    
    .profile_header_bg,
    .profile_header_bg_texture {
        background-image: none !important;
    }
    .profile_header_bg {
        max-width: 660px !important;
        height: 300px !important;
        top: 21px !important;
        left: -132px !important;
    }
    .profile_header_bg_texture {
        margin-top: 22px !important;
    }
    .profile_header_badgeinfo {
        display: none !important;
    }
    .persona_name {
        position: absolute !important;
        font-family: inherit !important;
        font-size: 26px !important;
        top: -64px !important;
        left: -186px !important;
        color: #E1E1E1 !important;
    }
    .profile_header_bg:after {
        content: "Profile _";
        padding-top: 2px !important;
        font-size: 11px !important;
        display: block !important;
        position: absolute !important;
        top: 67px !important;
        border-top: 1px solid #4D4D4E !important;
        border-width: 1px 0 0 0 !important;
        width: 669px !important;
    }
    .profile_header_content {
        color: #939393 !important;
        top: 5px !important;
        font-size: 11px !important;
    }
    .profile_header .profile_summary {
        line-height: 14px !important;
    }
    .profile_header_bg:before {
        color: #898989 !important;
        content: "Steam Profile";
        font-size: 26px !important;
    }
    .playerAvatar.profile_header_size {
        width: 188px !important;
        height: 188px !important;
        border-radius: 3px !important;
    }
    .playerAvatar.profile_header_size .playerAvatarAutoSizeInner {
        top: 3px !important;
        right: 3px !important;
        bottom: 3px !important;
        left: 3px !important;
    }
    .playerAvatar img {
        border-radius: 3px !important;
    }
    .header_real_name {
        margin-top: 10px !important;
        margin-left: 16px !important;
    }
    .profile_header_summary {
        margin-top: 6px !important;
    }
    .header_real_name bdi {
        display: block !important;
        margin-left: 6px !important;
    }
    .profile_header .playerAvatar.profile_header_size {
        margin-right: 12px !important;
    }
    .profile_header .profile_header_centered_persona {
        right: auto !important;
    }
    /*main*/
    
    .responsive_page_template_content {
        background-image: url("http://web.archive.org/web/20120818164103/http://cdn.steamcommunity.com/public/images/skin_1/contentBG_top.gif"), url("http://web.archive.org/web/20120818164101/http://cdn.steamcommunity.com/public/images/skin_1/contentBG_fill.gif") !important;
        background-repeat: no-repeat, repeat-y !important;
        background-position: center 0px !important;
    }
    .profile_content {
        background-image: none !important;
        margin-top: 14px !important;
        left: 0px !important;
        font-size: 12px !important;
    }
    .profile_comment_area .commentthread_header,
    .profile_leftcol_header {
        background-color: #1E1E1F !important;
    }
    .recent_game {
        background-color: #1B1B1B !important;
    }
    .customization_edit {
        display: none !important;
    }
    .game_info_stats {
        background-color: #3A3A3A !important;
        border-top: 2px solid #4A4A4A !important;
    }
    .profile_recentgame_header {
        padding: 0px 6px !important;
        border-radius: 3px !important;
        display: inline-block !important;
        width: 360px !important;
        text-align: right !important;
        background-color: #1B1B1B !important;
    }
    .commentthread_header {
        padding: 2px 6px !important;
    }
    .profile_comment_area .commentthread_header .commentthread_count {
        font-size: 12px !important;
        font-family: inherit !important;
        font-weight: bold !important;
        color: #626467 !important;
    }
    .profile_content h2 {
        font-family: inherit !important;
        font-weight: bold;
        font-size: 12px !important;
        color: #626467 !important;
    }
    .recent_game .game_info .game_name {
        font-size: 12px !important;
    }
    .profile_content.has_profile_background {
        overflow: unset !important;
        padding-top: 0px !important;
    }
    .profile_background_image_content,
    .profile_background_overlay_content,
    .profile_background_holder_content {
        background: none !important;
    }
    .profile_customization {
        background-image: none !important;
    }
    .profile_customization_header {
        color: inherit !important;
        font-family: inherit !important;
        font-size: 14px !important;
    }
    .game_info_details,
    .game_info_badge_description {
        font-size: 11px !important;
    }
    .showcase_content_bg {
        background: none !important;
    }
    .profile_customization {
        background-color: #3A3A3A !important;
        padding: 0 !important;
    }
    .profile_customization_block {
        border: 1px solid #4A4A4A !important;
        border-width: 0px 2px 2px 2px !important;
    }
    .profile_customization_header {
        background-color: #565656 !important;
        color: #000 !important;
        font-weight: bold !important;
        font-size: 12px !important;
        padding: 0px 6px !important;
    }
    /*right-sidebar*/
    
    .profile_rightcol {
        width: 253px !important;
        position: absolute !important;
        top: -255px !important;
        right: -10px !important;
    }
    .profile_badges,
    .profile_item_links,
    .profile_group_links {
        margin-bottom: 16px !important;
    }
    .profile_badges_badge.last {
        display: none !important;
    }
    .profile_item_links,
    .profile_badges,
    .profile_group_links {
        background-image: url("http://web.archive.org/web/20120818164105/http://cdn.steamcommunity.com/public/images/skin_1/rightActionBlockBG.gif") !important;
        background-repeat: repeat-y !important;
    }
    .profile_friend_links {
        border-left: 1px solid #4A4A4A !important;
        border-bottom: 1px solid #4A4A4A !important;
        border-radius: 3px 0 0 3px !important;
    }
    .friendBlock {
        margin-left: 3px !important;
        border-radius: 3px !important;
        background-image: url("http://web.archive.org/web/20120818164524/http://cdn.steamcommunity.com/public/images/skin_1/friendBlockBG2.gif") !important;
        background-repeat: repeat-y !important;
    }
    .profile_friend_links .profile_count_link {
        margin: 5px 0 5px 6px;
    }
    .friendBlockContent {
        font-weight: bold !important;
    }
    .friendBlock.inGame {
        color: #B1FB50 !important;
    }
    .friendSmallText {
        font-weight: normal !important;
    }
    .profile_badges .profile_badges_badge:first-of-type {
        margin-left: 5px !important;
    }
    .profile_badges .profile_count_link {
        padding: 5px 0 0 12px !important;
    }
    .profile_item_links:before {
        content: "Actions" !important;
        color: #000 !important;
        font-size: 12px !important;
        font-weight: bold !important;
        padding: 5px 0 0 8px !important;
        height: 18px !important;
        width: 246px !important;
        display: block !important;
        background-image: url("http://web.archive.org/web/20120818164112/http://cdn.steamcommunity.com/public/images/skin_1/rightColHeadRibbon.gif") !important;
    }
    .profile_badges:before {
        content: "Steam Badges" !important;
        color: #000 !important;
        font-size: 12px !important;
        font-weight: bold !important;
        padding: 5px 0 0 8px !important;
        height: 18px !important;
        width: 246px !important;
        display: block !important;
        background-image: url("http://web.archive.org/web/20120818164112/http://cdn.steamcommunity.com/public/images/skin_1/rightColHeadRibbon.gif") !important;
    }
    .profile_group_links:before {
        content: "Groups" !important;
        color: #000 !important;
        font-size: 12px !important;
        font-weight: bold !important;
        padding: 5px 0 0 8px !important;
        height: 18px !important;
        width: 246px !important;
        display: block !important;
        background-image: url("http://web.archive.org/web/20120818164112/http://cdn.steamcommunity.com/public/images/skin_1/rightColHeadRibbon.gif") !important;
    }
    .profile_friend_links:before {
        content: "Friends" !important;
        color: #000 !important;
        font-size: 12px !important;
        font-weight: bold !important;
        padding: 5px 0 0 8px !important;
        height: 18px !important;
        width: 246px !important;
        display: block !important;
        background-image: url("http://web.archive.org/web/20120818164112/http://cdn.steamcommunity.com/public/images/skin_1/rightColHeadRibbon.gif") !important;
    }
    .profile_item_links:after {
        content: " ";
        height: 9px !important;
        width: 254px !important;
        display: block !important;
        background-image: url("http://web.archive.org/web/20120818164059/http://cdn.steamcommunity.com/public/images/skin_1/rightActionBlockFooter.gif") !important;
    }
    .profile_badges:after {
        content: " ";
        height: 9px !important;
        width: 254px !important;
        display: block !important;
        background-image: url("http://web.archive.org/web/20120818164059/http://cdn.steamcommunity.com/public/images/skin_1/rightActionBlockFooter.gif") !important;
    }
    .profile_group_links:after {
        content: " ";
        height: 9px !important;
        width: 254px !important;
        display: block !important;
        background-image: url("http://web.archive.org/web/20120818164059/http://cdn.steamcommunity.com/public/images/skin_1/rightActionBlockFooter.gif") !important;
    }
    .profile_count_link {
        font-size: 12px !important;
        min-height: auto !important;
    }
    .profile_item_links .profile_count_link {
        padding: 3px 0px 0px 12px !important;
    }
    .profile_group_links .profile_group {
        margin-left: 9px !important;
    }
    .profile_primary_group {
        margin-top: 8px !important;
        margin-bottom: 2px !important;
    }
    .profile_group_links .profile_count_link {
        margin: 5px 0 0 6px !important;
    }
    .profile_item_links .profile_count_link:first-of-type {
        padding-top: 9px !important;
    }
    .profile_count_link_total {
        font-family: inherit !important;
        color: #9B9B9B;
        font-size: 12px !important;
    }
    .profile_in_game_header {
        font-family: inherit !important;
        position: absolute !important;
        font-size: 14px;
        font-weight: bold;
        color: #62A7E3;
        top: -59px;
    }
    .profile_in_game_name {
        margin-bottom: 8px !important;
        margin-top: -24px !important;
    }
    .profile_in_game {
        margin-bottom: 56px !important;
    }
    .profile_in_game persona {
        margin-bottom: 64px !important;
    }
    /*footer*/
    
    #footer_spacer {
        height: 75px !important;
        padding-top: 0px !important;
    }
}
