css:
/* i really want this to be global */
a#upload-btn {
    background-color: #E62117;
    border-style: none;
    color: #FFFFFF;
    font-weight: bold;
    border-radius: 5px;
}
a#upload-btn:hover {
    background-color: #EE6159;
}
#yt-masthead-container {
    background-color: #E62117;
}
#masthead-search-terms {
    border-left-style: none;
    border-top-style: none;
    border-bottom-style: none;
    border-right-style: solid;
    border-color: #D3D3D3;
    border-bottom-left-radius: 5px;
}
#masthead-search {
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
}
.search-btn-component {
    border-style: none;
    border-color: #D3D3D3;
    border-bottom-right-radius: 5px;
}
#yt-masthead-user .yt-uix-button-icon-bell {
    background: url('http://orig01.deviantart.net/a019/f/2016/099/b/b/youtube_bell_white_by_craftyinventor-d9yd37o.png');
    opacity: 80;
}
#yt-masthead-user .yt-uix-button-icon-bell:hover {
    background: url('http://orig01.deviantart.net/a019/f/2016/099/b/b/youtube_bell_white_by_craftyinventor-d9yd37o.png');
    opacity: 100;
}
.guide-item.guide-item-selected,
.guide-item.guide-item-selected:hover {
    background: #E62117;
}
#yt-masthead #logo-container span.content-region {
    color: #FFFFFF;
}
#yt-masthead #logo-container .logo {
    background: url('http://orig04.deviantart.net/6116/f/2016/099/6/1/youtube_logo_by_craftyinventor-d9yd3sp.png');
    margin-left: -10px;
}
.yt-uix-button-icon-appbar-guide {
    background: url('http://orig07.deviantart.net/84fc/f/2016/099/d/e/youtube_bars_by_craftyinventor-d9yd3y3.png') !important;
}
.yt-uix-button-default:hover,
.yt-uix-button-text:hover {
    box-shadow: 0 0 0 rgba(0, 0, 0, 0);
}
