css:
@-moz-document domain("twitch.tv") {
.player-slider__left {
   background: url("data:image/png;base64,") repeat-x !important; 
   height: 12px !important;/*[[glow]]*/
}
.player-slider:before {
   background: url("data:image/gif;base64,") !important
}
.player-volume__slider-thumb {
   background: url("data:image/gif;base64,") !important;
   width: 34px !important;
   height: 21px !important;
   border: none !important;
   margin-left: -18px !important;
   margin-top: 0px !important;
}
.player-slider:before, .player-slider__muted-segments, .player-slider__muted {
   height: 12px !important;
}
.player-seek > .player-slider {
   transform: scaleY(0.6);
   transition: transform .1s cubic-bezier(0.4,0.0,1,1);
   padding-top: inherit;
}
.player-seek > .player-slider:hover {
   transform: scaleY(1);
}
.player-button {
   margin-top: .4em
}
.player-quality {
   margin-top: .4em;
}
.player-slider__buffer {
   background: none !important;
}
.player-volume__slider-container {
   padding: .35em 0 .3em !important;
}
.player-hover p, .player-hover span {
   cursor: pointer !important;
}
.player-slider--roundhandle .ui-slider-handle {
   transition: opacity .2s ease !important;
}
.player-seek__time-container{
   margin-bottom: .5em !important;
}
/*[[playerglow]]*/
}
