css:
@-moz-document domain("nytimes.com") {

body{
 color:#FFFFFF !important; 
 background-color:#333  !important;
}

.portal-container .portal-post, .portal-container .portal-post-loading, .portal-container .portal-post-about{
background-color:#652727  !important;
}
.masthead.masthead-theme-standard{
 background-color:#983232 !important;
}
.masthead.theme-pinned-masthead{
 background:#333333  !important;
}

.marginalia.related-combined-coverage-marginalia .headline{
color:#FFFFFF !important;
}


/*side menu*/
.viewport-medium .modal-container .modal{
background-color:#333 !important;
}
.navigation{
background-color:#333 !important;
}
.navigation .sections .section .menu li:hover > a,.navigation-modal .modal-content .secondary li:hover >a,.navigation-modal .modal-content .secondary li:hover >a
{
background-color:#E22727 !important;
}






.masthead .editions.tab{
background-color:#652727 !important;
}
.masthead .masthead-menu li{
color:#FF7D7D !important;
}

a:link{
color:#FFFFFF !important;
}

.span-ab-bottom-region .story.theme-summary .summary{
color:#ADD2CA !important;
}
.masthead .quick-navigation .sections-button, .masthead .quick-navigation .search-button{
color:#EA1515 !important;
}

a:visited{
color:#FFFFFF !important;
}

.navigation-edge{
background-color:#333  !important;
}

.site-index .menu a:link, .site-index .menu a:visited{
color:#FF8F00 !important;
}

.trending-module .story-link .headline{
color:#75C3FF !important;
}

.story.theme-main>.story-header .headline{
color:#FFFFFF !important;
}

.kicker-label a{
color:#FB5A5A !important;
}
.story.theme-summary .story-heading{
color:#FF8080 !important;
}

/*top menu*/
.theme-in-content .mini-navigation .mini-navigation-menu{
background:#333 !important;
}
/*Audio title*/
.story.theme-main .media.audio .headline{
color:#F95D5D !important;
}

/*video*/
.video-section .video-playlist .story .headline{
color:#F91717 !important;
}
/*Date*/
.kicker{
color:#F00 !important;
}
/*caption*/
.caption, figcaption{
color:#B39696 !important;
}

.tabs-container .tabs li.selected{
color:#af1a1a !important;}

.stream .summary{
color:#FFFFFF !important;}

.story.theme-feature .summary{
color:#FFFFFF !important;}

.story.theme-main.interactive.interactive-headline{
color:#F30000 !important;}

.viewport-medium-50 .whats-next.whats-next-collection.story.theme-summary.headline{
color:#FFFFFF !important;}
}
