css:
@-moz-document url-prefix("http://sc2tv.ru/") {
:root { --color1: #060a10; --color2: #111823; --color3: #1d242e; --color4: #141D2B; --color5: #828282; --color7: #c0c0c0; --color8: #ffefb6; --color9: #ffefb6; --color10: rgba(33, 124, 182, 0.35);
}
body { background: var(--color1);
}
.main-frame { top: 0px !important;
}
#content, .left-column, .block-vbbridge ul li.light, .column-top, #right-content { background: var(--color2)
}
.vud-widget-updown .up-inactive, .vud-widget-updown .down-inactive, .vud-widget-updown .reset-inactive, .list-content .vud-widget-updown div.n-hover, .list-content .vud-widget-updown div.p-hover, .vud-widget-updown .updown-score, .left-column, #right-content, .resizable-textarea .grippie { border: 1px solid var(--color3) !important; color: var(--color5); line-height: 150% !important; text-shadow: none !important
}
#content .center.news .list-content p, body, .block-vbbridge ul li li span, .challenges-block-streamer { color: var(--color5) !important; border-bottom: none !important; text-shadow: none !important;
}
#content .block-vbbridge ul li .topic a, #content a, #content .list-block .list-content a, #content .center.news .list-content h2 a, #content .block-vbbridge ul li li.nickname a, #content .comlink, #content .block-sc2tv_recentcomments ul li .user { color: var(--color7); transition: 0.2s !important; text-shadow: none; text-decoration: none
}
#content a:hover, #content .list-block .list-content a:hover { color: var(--color8) !important; transition: 0s !important;
}

.block-vbbridge ul li:hover, .block-vbbridge ul li.light:hover, .center, .list-block ul li, #comments .comment, .center .center-header { background: var(--color2)
}
p.update { background-color: var(--color2)
}
.vud-widget-updown .updown-score, .resizable-textarea .grippie, .vud-widget-updown .up-inactive, .vud-widget-updown .down-inactive, .vud-widget-updown .reset-inactive, .list-content .vud-widget-updown div.n-hover, .list-content .vud-widget-updown div.p-hover { background-color: var(--color3)
}
#comments .comment:hover, .center.news .list-header { background: var(--color2)
}
.vud-widget-updown .up-inactive:hover, .vud-widget-updown .down-inactive:hover, .vud-widget-updown .reset-inactive:hover, .list-content .vud-widget-updown div.n-hover:hover, .list-content .vud-widget-updown div.p-hover:hover { background: var(--color9); font-size: 15px
}
ul.tabs li { background: var(--color10)
}
.center .center-header h2 { font-size: 16px; margin: 0
}
.center { padding: 24px;
}
.center .center-header, .challenges-wrapper, .center, #content .center.news .list-block { width: 452px;
}
input[type='submit'], button[type='submit'], .block-advpoll .submit { height: 24px; border: none; background: var(--color1); color: var(--color7); padding: 0 11px 3px 7px; margin: 8px 12px 0 0; width: 100px; box-shadow: none
}
input[type='submit']:hover, button[type='submit']:hover, .block-advpoll .submit:hover { height: 24px; border: none; background: var(--color10); color: var(--color8); padding: 0 11px 3px 7px; margin: 8px 12px 0 0; width: 100px; box-shadow: none
}
textarea { border: 1px solid var(--color5) !important; background: var(--color2)!important; transition: 0.2s; color: var(--color5)!important; box-shadow: none !important
}
textarea:hover { border: 1px solid var(--color8) !important; transition: 0s;
}
textarea:focus { border: 1px solid var(--color8) !important; background: var(--color2)!important; box-shadow: rgba(0, 0, 0, .3) 0 1px 4px !important; color: var(--color5)!important; transition: 0.2s
}
.bue-e6 .bue-sprite-button { -webkit-filter: invert(1);
}

#content .list-block div.list-content>ul, #content .list-block div.list-content>ol
{border-color: var(--color4)
}
}
