css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.ex.ua") {
body {
    background: #E0E0E0!important;
    font-family: 'Helvetica Neue', sans-serif!important;
    color: #405469!important;
}
body > table {
    position: relative!important;
    max-width: 80%!important;
    margin: auto!important;
    background: #F7F7F7!important;
    border-left: 1px solid #bbb!important;
    border-right: 1px solid #bbb!important;
}
.fox_col1, .fox_col2, #body_element center>div {
    background-color: #F8F8F8!important;
}
#body_element, .table tr {
    padding: 30px 40px 0 40px!important;
}
  #announce, #ad_block_1, #ad_block_2, #ex_block_1, #ex_block_2, div[id*="admaster_0"], div[id*="adriver_banner"], #search_help, #html-id, .list td.small p:nth-child(2n+2), .list span.small, #claim_button, #admixBG_container, div[id*="admixer_"], div[id*="mixer_"], #body_element > div > p:nth-child(3), #body_element > table:nth-child(12) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > p:nth-child(8), #body_element>p:nth-child(13), #body_element>p:nth-child(14), .list > tbody > tr > td > p, body > table > tbody > tr:nth-child(1) > td > div {display: none!important;} 
td {
    font-size: 11pt!important;
    height: 22px!important;
}
p {
    margin-top: 10px!important;
    margin-bottom: 12px!important;}
p:empty {
    display: none!important;}    
.menu {
    position: fixed!important;
    max-width: 100%!important;
    top: 0!important;
    padding: 0 9.5%!important;
    height: 44px!important;
    background-color: transparent!important;
    background-image: linear-gradient(#6f6f6f, #5a5a5a)!important;
    box-shadow: rgba(0, 0, 0, .5) 0px 0px 18px 0px!important;
    z-index: 99999!important;
    border: none!important;
}
.menu a {
    color: #ddd!important;
    font-family: 'Helvetica Neue', 'Arial', 'Verdana', sans-serif!important;
    font-size: 20px!important;
}
.menu a:hover {
    color: #fff!important;
}
td.menu_text select { 
    min-width: 65px!important;-moz-appearance: none!important;-webkit-appearance: none;
    }    
.menu_text {
    color: transparent;
}
.menu_login, td.menu_text select {
    border: none!important;
    background: transparent!important;
    font-weight: bold;
    color: #aaa!important;
}
.include_0 td, .include_1 td, .panel td {
    background: #F9F9F9!important;
    padding: 12px 10px 8px 10px!important;
    border: 1px solid #d5d5d5!important;
    border-radius: 3px;
    box-shadow: 0 0 5px rgba(0, 0, 0, .14);
}
 .include_0 td:active, .include_1 td:active, .panel td:active, .list img, #search_button { 
    position: relative; top: 2px;
}    
.include_1 td, .panel td {
    padding: 10px 10px 2px 11px !important;
}
.include_0 td:hover, .include_1 td:hover, .panel td:hover {
    box-shadow: 0 1px 6px rgba(0, 0, 0, .3);
}
.include_0 td > a, .include_0 td > small, .list img {
    position: relative!important;
    top: 3px!important;
}
.include_0 div, .panel .r_button_small, table select {
    position: relative!important;
    bottom: 3px!important;
}
.include_1 div {
    position: relative!important;
    bottom: 8px!important;
}
.panel p, .include_0 p {
    margin: 5px 0!important;
}
.panel img {
    margin-top: 5px!important;
}
.include_0 img:hover, .include_1 img:hover, .panel img:hover {
    filter: contrast(1.15);
    -webkit-filter: contrast(1.15);} 
td > img {
    box-shadow: 0 0 10px 5px #fff;}

#search_box, #search_form, #search_link {
    position: fixed!important; 
    max-height: 0!important; 
    max-width: 0!important; 
    background: transparent!important; 
    box-shadow: none!important; 
    padding: 0!important;
    margin: 0 0 0 3px!important;
    z-index: 99999!important;
    } 
#body_element > div:nth-child(4) {
    padding: 0!important;
}
#search_form > div:nth-child(2) {
    max-height: 0!important;
    max-width: 0!important;
    padding: 0!important;
}
#search_form > div:nth-child(2) > div:nth-child(1) {
    left: 4px!important;
    height: 0!important;
}
#search_form > div > div:nth-child(1) {
    right: 11px!important;
    bottom: 16px;
}
div:nth-child(4) form {
    position: relative!important;
    top: 8px!important;
    left: 9px!important;
}
form:nth-child(5) {
    position: relative!important;
    top: 40px!important;
    left: 9px!important;
}
#body_element>p:nth-child(3), div:nth-child(4)>p {
    margin-top: 20px!important;
    margin-bottom: 5px!important;
}
#search_text {
    width: 400px!important;
    height: 31px!important;
    box-sizing: border-box!important;
    border-radius: 3px!important;
    font-weight: bold!important;
    font-size: 20px!important;
    box-shadow: 0 1px 8px rgba(0, 0, 0, .24);
}
#search_line {
    width: 400px!important;
    top: 7px!important;
}
#search_hint {
    box-shadow: 0 1px 10px rgba(0, 0, 0, .45);
    font-weight: bold!important;
    font-size: 12pt!important;
}
#search_button {
    top: 7px!important;
    left: 405px!important;
    height: 31px!important;
    width: 140px!important;
    font-size: 18px!important;
    color: #d5d5d5!important;
}
div:nth-child(4) > p, #body_element > p:nth-child(3) {
    padding: 0 0 0 580px!important;
}
.list {
    max-width: 80%!important;
    font-style: italic!important;
}
.list td, .comment td {
    padding: 0!important;
    border-bottom: 1px dashed #ABB2BE!important;
    color: #344454!important;
}
.list td.small b, .list td.small p, a:nth-child(9), p:nth-child(12), p:nth-child(15), table:nth-child(11), table:nth-child(13), table:nth-child(14), table:nth-child(16):not(.list) {
    display: inline-block!important;
}
#body_element td:nth-child(2)> p:nth-child(2), #body_element > p:nth-child(10) {     
    margin: 0!important;
}  
#body_element > a:nth-child(9) + p:nth-child(10) {
    display: inline-block!important;
    position: relative!important;
    top: 4px!important;
    margin: 0!important;
}
table:nth-child(13),table:nth-child(16) {
    max-height: 28px!important;
}   
#body_element a:nth-child(9), .list td.small b {
    padding: 0 10px 0 0!important;
}
#body_element td:nth-child(1){
    position: relative!important;
}
span.r_button>a.fox-play-btn {
  position: absolute!important; bottom: -28px!important; right: 155px!important;
}  
table:nth-child(9), #body_element > table:nth-child(1) > tbody:nth-child(1) td {
    padding-top: 4px!important;
}
#post p {
    display: block!important;
}
#post p:last-child {
    margin-bottom: 0!important;
}    
.vjs-poster {
    z-index: 1!important;
}
a {
    color: #344454!important;
}
a:hover {
    color: #0091AD!important;
}
.include_0 b, .list b {
    font-size: 14px!important;
}
small {
    font-size: 12px!important;
}
h1 {
    color: #344454!important;
    font-size: 13pt!important;
    font-weight: 600!important;
}
h2, #body_element > table:nth-child(9) h1 {
    color: #344454 !important;
    font-size: 14pt!important;
    font-weight: 700!important;
}
a.info {
    font-size: 13px!important;
    border: none!important;
}
.button, .r_button a, .small_button, .list .r_button_small a {
    border: 1px solid #5F5F5F!important;
    border-radius: 2px!important;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .4)!important;
    background: #636464 linear-gradient(rgba(255, 255, 255, .11), rgba(255, 255, 255, .01))!important;
    color: #ddd!important;
}
.small_button, .list .r_button_small a {
    font-weight: 600!important;
}
.button:hover, .r_button a:hover, .small_button:hover, .r_button_small a:hover, #search_button:hover {
    background: #777!important;
    color: #fff!important;
}
.panel .r_button_small a, div.r_button_small a, .comment .r_button_small a {
    background: #f1f1f1!important;
    color: #777!important;
    border: 1px solid #aaa!important;
    border-radius: 2px;
    margin: 0!important;
}
.panel .r_button_small a:hover, div.r_button_small a:hover, .comment .r_button_small a:hover {
    background: #fff!important;
    color: #777!important;
}
input {
    background: #F8F8F8!important;
    border: 1px solid #999!important;
    font-family: sans-serif!important;
    font-size: 14px!important;
    font-weight: 600!important;
    color: #304050 !important;
    padding-left: 5px;
}
textarea {
    border: 1px solid #bbb!important;
    font-family: 'Segoe UI', sans-serif!important;
    font-size: 11pt!important;
    padding: 0 4px!important;
}
#body_element > table > tbody > tr > td > table > tbody > tr > td:nth-child(2) {
    min-width: 610px
}
}
