/* AGENT_SHEET */
/*
 * YouTube - dark gray redesign
 *
 * Author      : Valacar
 * Updated     : October 24th, 2008
 * Website     : http://www.valacar.com/userstyles/
 * Description : Modifies the colors, logos, and a few other images on YouTube (http://youtube.com/)
 * Usage       : Use with Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {

body {
	background: #000 !important;
	color: #ddd !important;
}

a:link, a:visited, a:active { color: #fff !important; }
:focus { outline: none !important; }
.eLink { border-bottom: 1px solid #666 !important; }


/* Google suggest */
.aAutoComplete .cAutoComplete, .google-ac-a .google-ac-c { color: #000 !important; }
.aAutoComplete .dAutoComplete, .google-ac-a .google-ac-d { color: green !important; }
.google-ac-e td, .google-ac-e td span { color: blue !important; }

/* Text colors */
span, div, p, li, td, label { color: #ddd !important; }
input *, textarea * { color: -moz-fieldtext !important; }


/* Text headers */
h1, h2, h3, h4, h5, h6, .hpBlockHeadingGray, .name, .title, #vidTitle, .wsHeading, .header, #catIndexGridHeading, #footer .links th {
	color: #fff !important;
}

/* Default for most borders */
hr, div, [class], [id], [style*="border"], tr, td, th, h1, h2, h3 {
	border-color: #444 !important;
}

/* Highlight Blocks */
#footContent, #footer, #old-footer, #channelVidsDiv, [style*="background-color: rgb(238, 238, 238)"], [style*="background-color: rgb(230, 230, 230)"], [style*="background-color: white"], #catIndexFeaturedWrapper, .highlightBoxes, .highlightBoxesOpacity[class], .highlightBox, .attentionBox, #actionsAndStatsDiv, #statsBox, .settingDiv, #vResponseParent, #vrParentDiv, #localePickerBox, .notopbox, #relatedVidsBody, .changeSearchTypeLinks, .lowlightBox, .vNowPlaying, #guidelines, .highlightBoxBrowse, fieldset, .commentsTooltip, .cat-spotlight-box, #actionsAreaDiv .moreAction, #sendMessage td, #addToFavesResult, #copyrightText, #videoOwnerDiv, #loginForm, #signupForm, #accountQuickLinksMore div, .spotlightItem, #search-settings-form, #addToPlaylistResult, #addToFavesResult, #addToBlogResult, [id^="reportConcernResult"], table[bgcolor="#bbccee"], #notopbox, .subtab b, .confirmBox, #search-video-results-link-box, .contentBox, #watch-channel-vids-div, .search-setting-inner, #watch-video-response, #watch-video-owner, .signInBoxContent, #yt-static-info-box, .signup-table, #search-duplicates-message, #wsSearchForm {
	background: #282828 !important;
	border-color: #444 !important;
}

/* Various background color fixes */
[bgcolor="#ffffff"], [bgcolor="#ffffbb"], [style*="background-color: rgb(255, 255, 255)"], #exContentWrapper, #exRelatedDiv, #exUserContentDiv, #exPlaylistDiv, #hpEmbedUnderBlock, .basicBoxes, .basicBoxesOpacity[class], .pagingDiv, #hpContentBlock, .vListBox, .previewBox, .vGridBox, .dBG, .rBG, #vlogBox div, .empty_player_list, .form_fieldsets, .form_fieldset, #ticker, .user-thumb-large, .footerBox, #pageContainer, #manageContent[style*="background"], #watch-customize-embed-div, .video-thumb-medium, #watch-actions-area .watch-more-action-share, .topic_body, .feedmodule-anchor, .feedmodule, .watch-comment-spam, .smallChannelMastheadTable {
	background: #000 !important;
}
[bgcolor="#eeeeee"], .eeeBG, #groupTopicTopEntry, .field_inputs, #watch-refer-div .header, #yt-safetytips, #yt-safetytips *, [id$="PickerBox"], #upload-file-list {
	background-color: #282828 !important;
}
.headerBox *, #watch-channel-brand-div, .headerRCBox, #search-options-container .search-type .search-type-selected, .profile-box .box-head .box-bg[class], .profile-box .box-foot .box-bg[class], .watch-playlist-row-playing, div.module h2, .color-offset {
	background: #454545 !important;
}

.errorBox { background-color: #600 !important; color: #ffc !important; }
#watch-remove-faves, .greenBG, #watch-actions-area #watch-add-faves { background: #060 !important; }
#loginBoxZ { background: #555 !important; }

/* thumbnail borders */
[class$="WrapperInner"], .videobarthumbnail_white, .videobarthumbnail_gray, .video-thumb { border-color: transparent !important; background: transparent !important; }
[class$="WrapperOuter"], [class^="vimg"] { border-color: transparent !important; }
.video-thumb-medium, .video-thumb-small, .user-thumb-large, .video-thumb-large, .video-thumb-big, .video-thumb img { border: none !important; }
.user-thumb-xlarge, .user-thumb-medium { background: transparent !important; }
.feedmodule-thumbnail, .vimg70, .user-thumb-jumbo { border: none !important; }

div.module h2 { border-color: #666 !important; }

/* Ads */
#bottomAdDiv, #leaderboardAd, .spOffersDiv, #sideContent, img[src^="/img/ad_"], a[href*="doubleclick.net"], #sideAd, img[src^="/img/pic_verizonwireless"], img[src*="pic_verizon_yellow_146x22.gif"], #FLASH_AD, #channelBrandCap, #ad300x250, #channelBrandDiv, [id^="myAd_"], [id^="ad_"], object[width="160"][height="600"], img[src*="fastclick"]
{
	display: none !important;
}

/* remove "Videos being watched right now" */
#active_sharing_div, #active_sharing
{
	display: none !important;
}

/* hide stuff */
img[class^="ratingVS-"] { display: none !important; }


/* Purple and Yellow blocks
--------------------------*/
.hpPurpleBlock, .hpYellowBlock, .homepage-yellow-block, .purpleBlock, .search-channel-onebox, .blue-box {
	background: #333 !important;
	padding: 8px 10px !important;
	border: 1px solid #444 !important;
}

.hpPurpleBlockBot, .hpYellowBlockBot, .homepage-yellow-block-bot, .purpleBlockBot {
	display: none !important;
}


/* Channels masthead
---------------------*/
.channelMastheadTable, #mastheadTable {
	background: #333 !important;
	padding-bottom: 10px !important;
	-moz-border-radius-bottomleft: 9px !important;
	-moz-border-radius-bottomright: 9px !important;
}

img[src="/img/masthead_round.gif"], #smallMastheadBottom { display: none !important; }


/* Comments
------------*/
.watch-comment-head { background: #333 !important; border: 1px solid #444 !important; padding-top: .15em !important; }
.watch-comment-auth-head { background: #444 !important;  border: 1px solid #555  !important; border-left: none !important; }
.watch-comment-head-hidden { background: #333 !important; }

.watch-comment-entry-reply { padding-left: 0 !important; }

.watch-comment-action a, .watch-comment-head-link {
	color: #fff !important;
	border-color: #666 !important;
}

.watch-comment-pagination { background: #444 !important; padding: .5em !important; margin-top: 5em !important; }

.watch-comment-spam .watch-comment-body { opacity: 0.6 !important; }
.watch-comment-entry { border-top: none !important; }


/* Subscription box
-------------------*/
img[src*="pic_subsbox_"] { display: none !important; }
#hpSubsBoxTop, #hpSubsBox, #homepage-subscriptions, #homepage-subscriptions-top { background: #000 !important; border: none !important; }
#vbror, #vbrol { opacity: 0.3 !important; }
#hpSubsBox table td { background: #222 !important; }
#homepage-subscriptions { border: none !important; }


/* Profiles */
.profile-highlightbox .box-body .box-bg[class] { background: #000 !important; }
.profile-box .box-body .box-bg[class] { background: #000 !important; }
.box-head .box-bg[class] { background: #555 !important; }

/* Pager
--------*/
.pagerNotCurrent { background: transparent !important; color: #ccc !important; }
.pagerCurrent { background: #666 !important; color: #fff !important; border: 1px solid #999 !important; }

/* Main tabs
-------------*/
#masthead .nav-item .content { color: #999 !important; }
#masthead .nav-item.selected .content { color: #fff !important; }

#old-masthead .nav-item .content { color: #999 !important; }
#old-masthead .nav-item.selected .content { color: #fff !important; }


/* My Account
------------- */
#manageNav .selected, #manageContent [bgcolor="#cccccc"], #manageContent .controls, #manageContent #compose {
	background: #333 !important;
}

#manageContent tr[bgcolor="#cccccc"] td { background-color: #333 !important; }

#manageContent [bgcolor="#f7f7f7"] {
	background: transparent !important;
}

#manageContent, #manageContent [style*="border"] {
	border-color: #333 !important;
}

#mymedia #table, #mymedia #view-pane .actions, #playlist-form {
  background: #000 !important;
}

#mymedia #view-pane .settings {
  background: #333 !important;
}

.video.odd { background: #000 !important; }
.video.even { background: #222 !important; }

.yt-button .yte-text, .yt-button-alt .yte-text, .yt-button-compact .yte-text {
  background: #555 !important; border: 1px outset;
}

.folder.selected {
  background: #555 !important;
}

.subfolder.selected {
  background: #444 !important;  
}

.subfolder > a { color: #ccc !important; }
.subfolder.selected > a { color: #fff !important; }

.videoDiv[style*="background-color: rgb(204, 204, 204)"] { background: #555 !important; }

.column-details { background: #000 !important; }


/* Home page tabs (Featured / Most Viewed / Most Discussed / Top Favorites)
-----------------------------------------------------------------------*/
#hpFeaturedAndSmallTabs, #header-wrapper + .videoModifiers {
	background-color: #282828 !important;
}

#hpFeaturedAndSmallTabs * {
	background-color: transparent !important;
}


#hpFeaturedAndSmallTabs a:not([class~="hilite"]), .videoModifiers a {
	opacity: 0.7 !important;
}


/* History tabs
----------------*/
#tabset { background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAjCAIAAADaE/fjAAAABGdBTUEAAK/INwWK6QAAABl0RVh0U29mdHdhcmUAQWRvYmUgSW1hZ2VSZWFkeXHJZTwAAAAfSURBVHjaYrKxsWFiYGDAwP///8dK45PDp4YIDBBgALN2G+DpvkDNAAAAAElFTkSuQmCC) !important; }
#tabset .tab, .fauxBtn { background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAjCAIAAADaE/fjAAAABGdBTUEAAK/INwWK6QAAABl0RVh0U29mdHdhcmUAQWRvYmUgSW1hZ2VSZWFkeXHJZTwAAAAjSURBVHjaYraxsWFiYGDAwP///4fT6BibOLIYNvOIwAABBgB2eCTYI8eCYAAAAABJRU5ErkJggg==) !important; }

#tabset, #tabset .tab, .fauxBtn, .fauxBtn a { border-color: #555 !important; }
#tabset #vh, #tabset #as, #tabset #ql { border-bottom-color: #303030 !important; border-right-color: #555 !important; }
.tabpad a { color: #aaa !important; }


/* Static sidebar 
-----------------*/
.yt-static-sidebar-list { background: #222 !important; }
.yt-static-sidebar-subhead { background: #333!important; }
#yt-static-sidebar-fade { visibility: hidden !important; }


/* Collapsable boxes
--------------------*/
.module-item-wrapper { background: #222 !important; border-bottom: 1px solid #444 !important; }
.statModule-item-line { background: transparent !important; }
.statModule-title { background: #333 !important; border: 1px solid #444 !important; }
.img-stat-top-cap, .img-stat-btm-cap-close, .img-stat-btm-cap-open { display: none !important; }
.module-item-wrapper > .statModule-item-line:first-child { border-top: none !important; }


/* Watch tabs
--------------*/
.watch-tabs td:not(.watch-tab-sel) { background: #222 !important; border: 1px solid #333 !important; border-bottom: 1px solid #444 !important; }
.watch-tabs td:not(.watch-tab-sel) .watch-action-text { color: #999 !important; }

.watch-tab-sel, .watch-sharetab-options { background: #000 !important;  }

td.watch-tab-sel { border: 1px solid #444 !important; border-bottom: none !important; }

.watch-tab-contents { border: 1px solid #444 !important; border-top: none !important; }

#watch-actions-area, #watch-comments-stats { border: 1px solid #222 !important; }

#watch-actions-area .watch-login-action { background: #300 !important; }


/* Menus
-------- */
.browseDropDownMenu, .browseDropDownMenu div a , .dropdown, .dropdown div a, .dropdown div {
	background-color: #333 !important;
	border-color: #444 !important;
	color: #aaa !important;
}
.browseDropDownMenu div a:hover, .dropdown div a:hover {
	background-color: #666 !important;
	color: #fff !important;
}

#videoListsBtn, #videoTimeBtn, .subNav-arrowbox {
	border-color: #4f4f4f !important;
}

/* Inbox and Contacts (work in progress)
---------------------*/
.message_title, .ab-users td { background: #333 !important; }
.open td, .ellipsis-dots, .ellipsis.m_nohighlight, .ellipsis.m_highlight { background: transparent !important; }
.sorterbar th, .ab-users-checks { background: #555 !important; }
.action-button .content, .default-button .content { background: #555 !important; border: 1px outset; }
.action-button img { display: none !important; }
.groupnamecont.selected, .ab-leftpane-sel { background: #333 !important; }
td[style*="width: 9px; background-color: rgb(204, 204, 204)"], .ab-divider { background: #444 !important; }
.m_highlight { background: #282828 !important; }
.m_nohighlight { background: #000 !important; }
.subject { background: transparent !important; }
[class*="button"] [class*="cap"] { display: none !important; }
.ab-cd, .ab-cd-info, .ab-cd-txtlbl, .ab-cd-details div[style*="background"] { background: #000 !important; }
.inner-dialog { background: #000 !important; }
.dialog { background: #333 !important; }
#headings td, #view-pane .footer { background: #555 !important; }
#mymedia #view-pane .splitter { background: #333 !important; }
.vert-bar { background: #555 !important; }


/* Video Insight
----------------- */

.gwt-TabPanelBottom, .gwt-Image { background-color: #000 !important; }
.insight-toptable > tbody > .even { background: #444 !important; }
img[src^="http://www.google.com/chart?"] { background-color: #fff !important; }
.insight-ErrorPanel { background: #900 !important; }
.insight-sourcestable .footer { background: #000 !important; }
.main .gwt-TabBarItem, .gwt-TabBarRest {  border-color: #666 !important; }
.insight-Popup { background: #444 !important; border: 1px solid #666 !important; }
.weekend-end, .weekend-start, .weekday, .week-names { color: #000 !important; }
.goog-date-picker .grid td.selected { background: #ffa!important; }
.goog-date-picker { background: #666 !important; }


/* Tweaks
----------------- */

/* expand thumbnail listings on videos page */
#mainContentWithNav {
	width: 705px !important;
}

/* horizontal menu at top right of page is an iframe sometimes */
iframe[src="/user_info"] {
	display: inline !important;
}

/* removed rounded corners on fieldsets */
.fieldsetTop, .fieldsetBottom {
	visibility: hidden !important;
}

/* QuickList current selection */
.playlistRowPlaying {
	background-color: #383838 !important;
	border-color: #555 !important;
}

/* "no video" icon */
img[src="/img/no_videos_140.jpg"] {
	opacity: 0.25 !important;
}

/* various buttons */
#upload-button .action-button-text, #subscribeDiv .action-button-text, a[href="/my_profile"] .action-button-text { color: #772600 !important; }
#unsubscribeDiv .action-button-text { color: #000 !important; }
#unsubscribeDiv { opacity: 0.2 !important; }
#unsubscribeDiv:hover { opacity: 1 !important; }

/* highlight left menu selected item */
.category-selected { background: #444 !important; }

.QLIconImg { opacity: 0.4 !important; }

/* hide feed icons on hp */
.feedmodule2-title img { display: none !important; }

.tooltip-wrapper-box * { color: #000 !important; }

/* Home page module containers
------------------------------*/
.fm2-title-border-box-gray, .fm2-title-border-box-blue { 
  background: #333 !important; 
  padding-top: 6px !important;
  -moz-border-radius-topleft: 6px !important;
  -moz-border-radius-topright: 6px !important;
  border: 1px solid #444 !important;
  border-bottom: none !important;
}
.fm2-title-border-box-blue {
  background: #444 !important;   
  border-color: #555 !important;
}

.fm2-top-cap { display: none !important; }

.feedmodule-normal { background: transparent !important; }

.img-gray-bottom-cap, .img-blue-bottom-cap { 
  height: 0 !important; 
  margin-bottom: 6px !important; 
}

.feedmodule-upper-left-right-border, .feedmodule-upper-left-right-border-blue { 
  border-bottom: 1px solid #444 !important; 
  -moz-border-radius-bottomleft: 6px !important;
  -moz-border-radius-bottomright: 6px !important;
}

/* Image Replacements
----------------------*/

#masthead .logo img,
#old-masthead .logo img,
#logoHome,
#iconMail,
#iconMail.noMail,
.activeSharingGreen,
.activeSharingRed,
.globalFlag,
#mastheadUploadBtn,
