css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.charazay.com") {
  
  /*--------overall-------*/
  .green {
    color: #006;
  }
  
  body#bgchangable {
    background: #dedede url("http://www.onefivesports.com/wp-content/uploads/2012/06/basket_ball_court.png") no-repeat fixed 0 0 / cover 
  }
  
  .greenbg {
    background-image: linear-gradient(#444, #777);
    color: #fff !important;;
  }
  
  form.loginformcontainer {
    float: left;
  }
  
  .container {
    margin: 0 auto;
    width: 1200px;
  }
  
  #main_container {
    padding-top: 20px;
  }
  
  input.submitbutton.greenbg {
    background: rgba(255, 255, 255, 0.2) linear-gradient(#444, #777) repeat scroll 0 0; 
    border-color: #d93;
    color: #fff;
    margin-left: 10px;
  }
  
  input.submitbutton.greenbg:hover {
    background: #d93;
  }
  
  .greenbar {
    background: #fff none repeat scroll 0 0;
  }
  
  .button {
    background: #777 none; 
    border: 1px solid #777; 
    box-shadow: initial; 
    color: #fff; 
    text-shadow: initial;
  }
  
  .button:hover {
    background: #d93;
  }
  
  .button.white {
    text-shadow: initial;
  }
  
  .button, input.inputSubmit, input.inputButton {
    box-shadow: initial;
    color: #fff;
  }
  
  
  .TabbedPanelsTab {
     background-color: #ddd;
  } 
  
  .TabbedPanelsTabSelected {
     background-color: #FFFFFF; 
     background-image: linear-gradient(#444, #777);
     color: #fff;
   }  
  
  a {
    text-decoration: initial;
  }
  
  a:hover {
    text-decoration: underline;
  }
  
  a.linkButton {
    background: rgba(255, 255, 255, 0.2) linear-gradient(#444, #777) repeat scroll 0 0; 
    border: none;
    color: #fff !important;
    text-shadow: initial;
    font: inherit;
    box-shadow: initial;
  }
  
  a.linkButton:hover {
    opacity: 1;
    background: #d93;
  }
  
  button.btngreen, input.btngreen, input.inputSubmit, input.inputButton {
    border: initial;
  }
  
  button.btngreen:hover, input.btngreen:hover, input.inputSubmit:hover, input.inputButton:hover {
    background: #d93;
  }
  
  input.btngeneric {
    border:  1px ridge #777;
  }
  
  a.btngeneric {
    border:  1px ridge #777;
  }
  
  a.btngreen {
    background-color: #d93; 
    transition: all 0.3s;
  }
  
  a.btngreen:hover {
    background-color: #d93; 
    transform: scale(1.1); 
    transition: all 0.3s;
  }
  
  .TabbedPanelsContentGroup, .PanelContainer, #mc {
    margin-bottom: 25px;
  }
  
  /*----------------menu--------------*/
  #primary-nav-wrap ul {
    background: rgba(0, 0, 0, 0) linear-gradient(#444, #777) repeat scroll 0 0;
  }
  
  #primary-nav-wrap ul a {
    color: #fff;
  }
  
  a.textlink {
    font-size: 100%;
    color: #ffffff;
  } 
  .mainnavcontainer {
    padding: 3px 20px;
  }
  
  nav {
    background-color: rgba(0,0,0,0.9);
  }
  
  #primary-nav-wrap ul ul {
    background: #777 none;
  }
  
  
  #primary-nav-wrap ul ul a {
    color: #fff;
  }
  
  #primary-nav-wrap ul ul a {
    text-transform: initial; 
    font-weight: 400;
  }
  
  #primary-nav-wrap .separatorb {
    border-left: 1px solid #fff;
  }
  
  .loggedin > div {
     display: inline-block;
  }
  
  .loggedin > ul {
    display: inline-block;
  }
  
  nav div.loggedin {
    float: left; 
    padding: 5px 0;
  }
  
  .helplinks {
    float: initial;
    font-size: 100%;
    margin-left: 10px;
  }
  #servertime.date {
    float: initial; 
    margin: 0 10px;
  }
  #mainnav div.mainnavcontainer a img {
    transform: scale(0.5);
  }
  
  #mainnav div.mainnavcontainer > a {
    display: none;
  }
  
  ul.topmenu li::before {
    display: none !important;
  }
  
  nav div.logincontain {
    display: inline-flex;
  }
  
  .nounderline:hover {
    text-decoration: initial;
  }
  ul.topmenu li:hover {
    background: #d93;
  }

  ul.topmenu li {
    border: 1px #d93 solid;
  }

  #primary-nav-wrap ul {
    background-color: #d93;
  }  

  #primary-nav-wrap ul li:hover {
     background: #d93;
  }

  button.btngreen, input.btngreen, input.inputSubmit, input.inputButton {
    border: 1px ridge #777;
    background: rgba(255, 255, 255, 0.2) linear-gradient(#444, #777) repeat scroll 0 0;
  }
  button.btngreen:hover, input.btngreen:hover, input.inputSubmit:hover, input.inputButton:hover {
    background-color: #d93;
  } 
  /*-----------toolbox------------*/
  #site-info .date {
    text-align: right;
    margin-left: 0px;
  }
  
  div#site-info.site-info-s.site-info_toolbox {
    position: static !important;
    width: 100%;
  }
  
  div#site-info.site-info-s.site-info_toolbox > div {
    display: inline-block;
  }
  #site-info.site-info-s.site-info_toolbox > p {
    display: inline-block;
    float: left;
  }
  #site-info.site-info-s.site-info_toolbox div p {
    display: inline;
    margin-right: 40px !important;
  }
  #site-info.site-info-s.site-info_toolbox p {
    margin-right: 40px !important;
  }
  
  sidebar {
    background: #d93;
  }

  .site-info_toolbox {
    margin: 0; 
    width: 100%;
    max-width: 1200px;
    box-sizing: border-box;
  }

  #site-info.site-info-s.site-info_toolbox div#notification_box {
    display: none !important;
  }
  
  #site-info.site-info-s.site-info_toolbox div#notification_container2 {
    display: none !important;
  }

  #site-info.site-info-s.site-info_toolbox div.helplinks {
    display: none !important;
  }

  #site-info.site-info-s.site-info_toolbox div {
    width: 100%;
  }

  .site-info-t {
    background-image: linear-gradient(#444, #777);
    color: #fff !important;
    text-shadow: initial;
    -webkit-border-top-left-radius: 2px;
    -webkit-border-top-right-radius: 2px;
    -moz-border-radius-topleft: 2px;
    -moz-border-radius-topright: 2px;
    border-top-left-radius: 2px;
    border-top-right-radius: 2px;
  }

  .site-info-s {
    background-color: #fff;
    border: 2px solid #d93;
    -webkit-border-top-left-radius: 2px;
    -webkit-border-top-right-radius: 2px;
    -moz-border-radius-topleft: 2px;
    -moz-border-radius-topright: 2px;
    border-top-left-radius: 2px;
    border-top-right-radius: 2px;
  } 

  #site-info {
    border: 0 solid #d93;
    border-radius: 2px !important;
    -webkit-border-radius: 2px;
    -moz-border-radius:  2px;
  }
  /*----------notifications-----------*/
  #notification_contents ul#notification_ul {
    max-height: 480px !important; 
    margin-bottom: 45px;
  }
  
  #notification_contents > div {
    height: 30px;
  }
  
  #notification_box, #notification_box2 {
    border: 1px solid #d93;
  }
  
  div#notification_box div#notification_contents ul#notification_ul li.old {
    background: #666;
  }
  
  div#notification_box div#notification_contents ul#notification_ul li {
    border-bottom: 1px dashed #d93;
  }
  
  #notification_box ul li a:hover, #notification_box2 ul li a:hover {
    background: #d93 none;
  }
  
  #notification_contents div button.btngeneric.greenbg {
    background: rgba(0, 0, 0, 0) linear-gradient(#444, #777) repeat scroll 0 0; 
    border: 1px solid #d93;
    color: #fff;
  }
  
  #notification_contents div button.btngeneric.greenbg:hover {
    background: #d93 none;
  }

  /*----------footer-------------*/
  footer {
    padding: 5px 20px; 
    border-bottom: 1px solid #666666;
  }
  
  footer a.downloadapp {
    display: none;
  }
  
  footer .menufooter {
    float: initial;
  }
  
  footer .menufooter p {
    display: inline;
  }
  
  #main_container.container > div > div {
    border: initial !important;
  }
  
  /*-------------matches-view---------------*/
  a.newbutton {
    border-radius: 0;
    text-shadow: initial;
    box-shadow: initial;
    color: #fff;
    background-image: linear-gradient(#444, #777);
    font-size: 13px;
    line-height: 14px;
    padding: 8px 12px 2px;
    display: inline-block;
    height: 18px;
  }

  a.newbutton:hover {
    background: #d93 none;
    text-decoration: initial;
  }
  
    
  a#pause-button.newbutton.icon.pause {
    position: relative; 
    top: -30px;
  }
  
  a#live-button.newbutton.icon.refresh {
    position: relative; 
    top: -30px;
  }
  
  section#matches-arena #quarter_table td.infogame {
    text-align: right;
  }
  
  section#matches-arena .matches-arena-box {
    margin-bottom: 10px; 
    padding: 5px;
  }
  
  table.newtable td {
    padding: 5px 0;
  }
  
  section#matches-arena #quarter_table td {
    padding: 0;
  }
  
  section#matches-arena #quarter_table {
    width: 100%; 
    margin: 3px 0 0; 
    vertical-align: initial;
  }
  
  #matches-arena div.matches-arena-box table#quarter_table.newtable tbody tr td:not(.left) {
    width: 40px;
  }
  
  table#tableresults {
    width: 300px;
  }
  
  table#tableresults th {
    width: initial;
  }
  
  table#tableresults td {
    padding: 0px !important;
  }
  
  table#tableresults td.category {
    width: 40px; 
    text-align: center;
  }
  
  table#tableresults th {
    padding: 0px;
  }
  
  .tabber {
    margin-top: 0;
  }
  
  div#text {
    height: 234px !important;
  }
  
  #play-by-play-content.tabbertab > table {
    float: left !important;
    width: 50% !important;
    vertical-align: initial;
  }
  
  .tabs .selected a { 
    background: rgba(0, 0, 0, 0.7) linear-gradient(#444, #777) repeat scroll 0 0; 
    border-color: #d93;
    color: #fff;
  }
  
  .tabs a:hover {
    background: #d93 none;
    color: #000;
  }
  
  .tabs a {
    text-decoration: initial;
  }
  
  ul.tabs {
    border-bottom: 3px ridge #777;
  }
  
  #play-by-play-content.tabbertab div#text {
    float: left !important;
    width: 39.5% !important;
    position: absolute; 
    top: 8px;
    right: 0;
  }
  
  div#play-by-play-content.tabbertab > br, #matches-arena div.matches-arena-box > br, #text div br {
    display: none;
  }
  
  #live_2.mc-fs {
    position: relative;
  }

  #mc.PanelContainer.PanelContent {
    padding: 8px;
  }

  #matches-arena h2 {
    padding: 0;
  }
  /*----------players-view-----------*/
  .columnhalf {
    padding: 0 90px;
  }
  
  /*---------------forum----------------------*/
  div.dark.post_top span.post_links a:first-child {
    display: inline-block; 
    width: 12px; 
    height: 12px; 
  }
  
  div.dark.post_top span.post_links a:first-child:before {
    content: ""; 
    display: block;
    width: 0px; 
    height: 0px; 
    border-right: 8px solid transparent; 
    border-top: 8px solid #d93; 
    border-left: 8px solid #d93; 
    border-bottom: 8px solid #d93; 
    border-top-left-radius: 8px; 
    border-top-right-radius: 8px; 
    border-bottom-left-radius: 8px; 
    border-bottom-right-radius: 8px;
  }
  
  div.dark.post_top span.post_links a:first-child > img {
    display: none;
  }
  
  div .navigation_right {
    width: 50%;
  }
  div .post_top a {
    text-shadow: initial;
    color: #d93;
  }
  
  .mc-fs .navigation > div > a.linkButton {
    display: inline-block;
    height: 19px;
    line-height: 19px;
  }
  /*-----------tactic-view------------------*/
  .adminique-table tbody tr:hover td {
     background-color: #dedede;
  } 

  /*-----------analysis-view----------------*/
  .button.red {
    background-image: linear-gradient(#444, #777);
    color: #fff;
    text-shadow: initial;
    border: 1px ridge #adadad;
  }
  .gtable tbody tr:hover td { 
     background-color: #dedede;
  }

}
