css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nierle.com") {
  body {
    background-color: #222;
    color: #aaa;
  }
  
  BODY, P, BLOCKQUOTE, CENTER, FORM, OL, UL, LI, PRE {
    background-color: #222;
    color: #888;
  }
  
  TABLE, TH, TR, TD {
    color: #888;
  }
  
  
  #kategorien_sidemenu a:link, #kategorien_sidemenu a:active, #kategorien_sidemenu a:visited {
    background-color: #222;
    color: #ccc;
    border-bottom: 2px solid #999;
  }
  
  #kategorien_sidemenu a.kategorien_sidemenu_path_neighbour:link, #kategorien_sidemenu a.kategorien_sidemenu_path_neighbour:active, #kategorien_sidemenu a.kategorien_sidemenu_path_neighbour:visited, #kategorien_sidemenu a.kategorien_sidemenu_path_pre:link, #kategorien_sidemenu a.kategorien_sidemenu_path_pre:active, #kategorien_sidemenu a.kategorien_sidemenu_path_pre:visited {
    margin: 0px 14px 2px -8px;
  }
  
  #kategorien_sidemenu a.kategorien_sidemenu_path:hover, #kategorien_sidemenu a.kategorien_sidemenu_path_pre:hover, #kategorien_sidemenu a.kategorien_sidemenu_path:link, #kategorien_sidemenu a.kategorien_sidemenu_path:active, #kategorien_sidemenu a.kategorien_sidemenu_path:visited, #kategorien_sidemenu a.kategorien_sidemenu_path_neighbour:hover, #kategorien_sidemenu a.kategorien_sidemenu_path_neighbour:visited, #kategorien_sidemenu a.kategorien_sidemenu_subs:hover, #kategorien_sidemenu a.kategorien_sidemenu_subs:visited {
    background-color: #666;
    margin: 0px 14px 2px -8px;
  }
  
  #kategorien_menu {
    border: 1px solid #888;
    background-color: #444;
    box-shadow: 0px 0px 10px #444;
    width: 601px;
  }
  
  /*links*/
  #kategorien_menu a:link, #kategorien_menu a:active, #kategorien_menu a:visited {
    color: #999;
    background-color: #444;
  }
  
  #kategorien_menu a:hover {
    color: #ccc;
  }
  
  /*Buttons*/
  #kategorien_allebutton, #konto_banner, #konto_banner_menu_login_submit, input[type="button"], input[type="submit"], #article_show_cart_container #article_show_cart_wkbutton, #article_show_combi_container #article_show_combi_wkbutton {
    background-image: url(http://#);
    background-color: #666;
    border-radius: 6px;
    color: #ddd;
  }
  
  #konto_banner_menu {
    background-color: #666;
    border: 1px solid #888;
    box-shadow: 0px 0px 10px #444;
  }
  
  #konto_banner_menu a:link, #konto_banner_menu a:active, #konto_banner_menu a:visited {
    color: #aaa;
  }
  
  .kategorien_menu_teaser, .kategorien_menu_teaser_object, .kategorien_menu_teaser_objects, .kategorien_teaser_background , .kategorien_teaser_background img {
    border: 0px;
    width: 0px;
    height: 0px;
    z-index: -9999;
  }
  
  #kategorien_menu_right {
    background-color: #444;
    width: 300px;
  }
  
  #search_container #search_text, #search_mediacode, #search_manufacturer, #search_category, #search_sort, select {
    border: 1px solid #ccc;
    background-color: #999;
    color: #222;
    border-right: 0 none;
  }
  
    #search_container #search_title {
    border: 1px solid #ccc;
    background-color: #666;
    border-right: 0 none;
  }
  
  /*Arrows*/
  #search_container #search_button, #search_container #search_button.arrow {
    border: 1px solid #ccc;
    background-color: #666;
    border-left: 0 none;
  }
  
  #article_show_ratings_recommend_article_button.arrow, #article_show_ratings_recommend_shop_button.arrow, #search_container #search_button.arrow {
    filter: invert(1);
    border: 1px solid #222;
    border-left: none;
  }
  
  #article_show_ratings_recommend_article_button.arrow, #article_show_ratings_recommend_shop_button.arrow, #search_container #search_button.arrow, #article_show_combi_container tr td img {
    filter: invert(0.865);
    border: 0px solid #222;
  }
  
  #article_show_title_container, #article_show_cart_container, form input[type="text"] {
    background-color: #444;
    color: #999;
  }
  
#article_show_image_overlay {
	background-image: url(http://#);
  z-index: -10;
}
  
  #variante_liste .variante_auswahl {
    background-color: #444;
    color: #999;
    border-bottom: 1px solid #222;
  }
  
  #variante_liste, #variante_auswahl {
    border: 1px solid #222;
    border-left: 1px solid #222;
    border-right: 1px solid #222;
    border-radius: 5px;
    box-shadow: 0px 0px 10px #666;
  }
  
  #variante_liste .variante_auswahl:hover {
    background-color: #999;
    color: #444;
  }
  
  #footer a:link, #footer a:active, #footer a:visited {
    color: #ccc;
  }
  
  #footer {
    background-color: #222;
    color: #888;
  }
}
